    }

    /// @notice Change Token INFO `_tokenId`.
    /// @param _tokenId token that the information will be change
    /// @param newDetails New Information of the token that will be updated to
    /// @dev Changes the token details by onlyOwner
    function modifyTokenINFO(uint256 _tokenId, tokenDetails memory newDetails)
        public
        onlyDev
    {
        if (walletTokenID[_tokenId] == address(0)) {
            revert NotExists();
        }
        //   require(walletTokenID[_tokenId] != address(0));
        tokenDetails memory _currentDetails = tokenINFO[_tokenId];

        totalActivePoolSize =
            (totalActivePoolSize + newDetails.activePoolSize) -
            _currentDetails.activePoolSize;
        totalPendingPoolSize =
            (totalPendingPoolSize + newDetails.pendingPoolSize) -
            _currentDetails.pendingPoolSize;
        totalSalePoolSize =
            (totalSalePoolSize + newDetails.salePoolSize) -
            _currentDetails.salePoolSize;

        tokenINFO[_tokenId] = newDetails;
        _calculateTier(
            _tokenId,
            newDetails.activePoolSize + newDetails.pendingPoolSize
        );
    }

    /// @notice Fund the required amount of FUNDS to the contract which is snapshot is taken earlier
    /// @param snapShotId snapshot ID that has been executed earlier
    /// @dev Funds the contract for the people that earn Claim Rewards by the snapshot
    function snapshotFund(uint256 snapShotId) public onlyOwner {
        require(!snapshots[snapShotId].fundsProcessed);

        snapshotDetails memory _tempSnap = snapshots[snapShotId];
        uint256 taxFEE = _tempSnap.totalClaimTax + _tempSnap.totalCompoundTax;
        uint256 claimAmount = _tempSnap.totalCLAIMAmount;

        uint256 firstPull = (claimAmount) / 10**(18 - currencyDecimals);
        bool successOne = IERC20Extented(currencyContract).transferFrom(
            msg.sender,
            address(this),
            firstPull
        );

        uint256 secondPull = (taxFEE) / 10**(18 - currencyDecimals);
        bool successTwo = IERC20Extented(currencyContract).transferFrom(
            msg.sender,
            address(WarFIFeeAddress),
            secondPull
        );
        if (!successTwo || !successOne) {
            revert TransferProblem();
        }

        snapshots[snapShotId].fundsProcessed = true;

        //    emit snapshotFundedEvent(snapShotId, firstPull, secondPull);
    }

    /// @notice This is the snapshot function that will be executed by Contract Owner at the end of each cycle
    /// @param profitPercentage Contract Owner decides the trading profit in terms of percentage for the snapshot
    /// @dev Function calculates for each person how much Claim Rewards or compounds should be distrubuted at the end of each cycle based on users' mode
    function snapshot(
        uint256 profitPercentage,
        uint256 startIndex,
        uint256 endIndex
    ) public onlyOwner {
        uint256 totalCLAIMModeUser = 0;
        uint256 totalHALFUser = 0;
        uint256 totalCOMPOUNDUser = 0;

        uint256 totalCLAIMPools = 0;
        uint256 totalCOMPOUNDPools = 0;

        uint256 claimTax = 0;
        uint256 compoundTax = 0;

        if (startIndex < 1) {
            startIndex = 1;
        }

        if (startIndex > currentSupply) {
            revert();
        }
        if (endIndex > currentSupply) {
            endIndex = currentSupply;
        }
        for (uint256 i = startIndex; i <= endIndex; i++) {
            tokenDetails memory _tempDetails = tokenINFO[i];

            uint256 FeeAmount = (((_tempDetails.activePoolSize) *
                profitPercentage) / (BASIS_POINTS));
            FeeAmount = (FeeAmount * claimAndCompoundFee) / (BASIS_POINTS);
            uint256 _reward = ((_tempDetails.activePoolSize *
                profitPercentage) / (BASIS_POINTS));
            if (_tempDetails.mode == 0) {
                totalCLAIMModeUser += 1;

                if (FeeAmount > _reward) {
                    FeeAmount = _reward;
                }
                _reward -= FeeAmount;

                claimTax += FeeAmount;
                tokenINFO[i].claimableRewards += _reward;
                totalCLAIMPools += _reward;
            } else if (_tempDetails.mode == 1) {
                totalHALFUser += 1;

                if (FeeAmount > _reward) {
                    FeeAmount = _reward;
                }
                _reward -= FeeAmount;

                _reward = (_reward * 5000) / (BASIS_POINTS);

                tokenINFO[i].claimableRewards += (_reward);
                claimTax += (FeeAmount * 5000) / (BASIS_POINTS);
                compoundTax += (FeeAmount * 5000) / (BASIS_POINTS);

                totalCLAIMPools += _reward;

                if (
                    _tempDetails.activePoolSize +
                        _tempDetails.pendingPoolSize >=
                    maximumPoolSizePerNFT
                ) {
                    tokenINFO[i].claimableRewards += _reward;
                    totalCLAIMPools += _reward;
                } else {
                    if (
                        _tempDetails.activePoolSize +
                            _tempDetails.pendingPoolSize +
                            _reward >=
                        maximumPoolSizePerNFT
                    ) {
                        uint256 exceedAmount = maximumPoolSizePerNFT -
                            _tempDetails.activePoolSize -
                            _tempDetails.pendingPoolSize;

                        totalCOMPOUNDPools += (exceedAmount);
                        tokenINFO[i].activePoolSize += exceedAmount;
                        totalActivePoolSize += exceedAmount;
                        tokenINFO[i].claimableRewards += (_reward -
                            exceedAmount);
                        totalCLAIMPools += (_reward - exceedAmount);
                    } else {
                        tokenINFO[i].activePoolSize += _reward;
                        totalCOMPOUNDPools += _reward;
                        totalActivePoolSize += _reward;
                    }
                }
            } else if (_tempDetails.mode == 2) {
                totalCOMPOUNDUser += 1;

                if (FeeAmount > _reward) {
                    FeeAmount = _reward;
                }
                _reward -= FeeAmount;

                compoundTax += FeeAmount;

                if (
                    _tempDetails.activePoolSize +
                        _tempDetails.pendingPoolSize >=
                    maximumPoolSizePerNFT
                ) {
                    tokenINFO[i].claimableRewards += _reward;
                } else {
                    if (
                        _tempDetails.activePoolSize +
                            _tempDetails.pendingPoolSize +
                            _reward >=
                        maximumPoolSizePerNFT
                    ) {
                        uint256 exceedAmount = maximumPoolSizePerNFT -
                            _tempDetails.activePoolSize -
                            _tempDetails.pendingPoolSize;

                        totalCOMPOUNDPools += (exceedAmount);
                        tokenINFO[i].activePoolSize += exceedAmount;
                        totalActivePoolSize += exceedAmount;
                        tokenINFO[i].claimableRewards += (_reward -
                            exceedAmount);
                        totalCLAIMPools += (_reward - exceedAmount);
                    } else {
                        tokenINFO[i].activePoolSize += _reward;
                        totalCOMPOUNDPools += _reward;
                        totalActivePoolSize += _reward;
                    }
                }
            }

            if (tokenINFO[i].pendingPoolSize > 0) {
                totalPendingPoolSize -= tokenINFO[i].pendingPoolSize;
                tokenINFO[i].activePoolSize += tokenINFO[i].pendingPoolSize;
                totalActivePoolSize += tokenINFO[i].pendingPoolSize;
                tokenINFO[i].pendingPoolSize = 0;
            }

            _calculateTier(
                i,
                tokenINFO[i].activePoolSize + tokenINFO[i].pendingPoolSize
            );
        }

        snapshots[snapshotCounter] = snapshotDetails({
            profit: profitPercentage,
            snapshotTimestamp: block.timestamp,
            totalCLAIMUsers: totalCLAIMModeUser,
            totalHALFUsers: totalHALFUser,
            totalCOMPOUNDUsers: totalCOMPOUNDUser,
            totalCLAIMAmount: totalCLAIMPools,
            totalCOMPOUNDAmount: totalCOMPOUNDPools,
            totalClaimTax: claimTax,
            totalCompoundTax: compoundTax,
            SnapshotStartIndex: startIndex,
            SnapshotEndIndex: endIndex,
            fundsProcessed: false
        });

        //   emit snapshotEvent(snapshotCounter);
        snapshotCounter += 1;
    }

