    /// @notice This function used for Listing a Pool of an NFT by the NFT Holder
    /// @param _amount The amount of pool that will be list on marketplace
    /// @dev The amount need to be in 18 decimals always.
    function listPool(uint256 _amount) public whenNotPaused {
        _amount = calculateAmount(_amount);
        require(enableSale);

        require(_amount > 0);

        uint256 _numMinted = numMinted[msg.sender];
        if (_numMinted == 0) {
            revert NotExists();
        }

        uint256 _tokenIdOwnedByWallet = tokenIdOwnedByWallet[msg.sender];

        require(tokenINFO[_tokenIdOwnedByWallet].salePoolSize == 0);
        require(tokenINFO[_tokenIdOwnedByWallet].activePoolSize >= _amount);

        tokenINFO[_tokenIdOwnedByWallet].activePoolSize =
            tokenINFO[_tokenIdOwnedByWallet].activePoolSize -
            _amount;

        _calculateTier(
            _tokenIdOwnedByWallet,
            tokenINFO[_tokenIdOwnedByWallet].activePoolSize +
                tokenINFO[_tokenIdOwnedByWallet].pendingPoolSize
        );

        totalActivePoolSize -= _amount;
        totalSalePoolSize += _amount;
        tokenINFO[_tokenIdOwnedByWallet].salePoolSize += _amount;

        //   emit listEvent(_tokenIdOwnedByWallet, _amount);
    }

    ///@dev This function used for DeListing a Pool of an NFT by the NFT Holder
    function deListPool() public whenNotPaused {
        require(enableSale);
        uint256 _numMinted = numMinted[msg.sender];
        if (_numMinted == 0) {
            revert NotExists();
        }
        uint256 _tokenIdOwnedByWallet = tokenIdOwnedByWallet[msg.sender];

        require(tokenINFO[_tokenIdOwnedByWallet].salePoolSize > 0);

        tokenINFO[_tokenIdOwnedByWallet].activePoolSize += tokenINFO[
            _tokenIdOwnedByWallet
        ].salePoolSize;

        _calculateTier(
            _tokenIdOwnedByWallet,
            tokenINFO[_tokenIdOwnedByWallet].activePoolSize +
                tokenINFO[_tokenIdOwnedByWallet].pendingPoolSize
        );

        totalActivePoolSize += tokenINFO[_tokenIdOwnedByWallet].salePoolSize;
        totalSalePoolSize -= tokenINFO[_tokenIdOwnedByWallet].salePoolSize;

        emit delistEvent(
            _tokenIdOwnedByWallet,
            tokenINFO[_tokenIdOwnedByWallet].salePoolSize
        );

        tokenINFO[_tokenIdOwnedByWallet].salePoolSize = 0;
    }

    /// @notice This function used for Listing a Pool of an NFT by the NFT Holder
    /// @param _sourceTokenId The token ID of the source Pool
    /// @dev This function get called by the user that wants the buy the active pool
    function buyPool(uint256 _sourceTokenId) public whenNotPaused {
        require(enableSale);

        address _sourceUserAddress = walletTokenID[_sourceTokenId];
        require(_sourceUserAddress != address(0));
        require(tokenINFO[_sourceTokenId].salePoolSize > 0);

        uint256 _numMinted = numMinted[msg.sender];

        if (_numMinted > 0) {
            uint256 _tokenIdOwnedByWallet = tokenIdOwnedByWallet[msg.sender];

            uint256 buyerFeeAmount = ((tokenINFO[_sourceTokenId].salePoolSize) *
                buyerFee) / (BASIS_POINTS);

            uint256 sellerFeeAmount = ((
                tokenINFO[_sourceTokenId].salePoolSize
            ) * sellerFee) / (BASIS_POINTS);
            uint256 sellerTotalAmount = tokenINFO[_sourceTokenId].salePoolSize -
                sellerFeeAmount;

            bool successOne = IERC20Extented(currencyContract).transferFrom(
                msg.sender,
                _sourceUserAddress,
                sellerTotalAmount / 10**(18 - currencyDecimals)
            );
            if (!successOne) {
                revert TransferProblem();
            }

            bool successTwo = IERC20Extented(currencyContract).transferFrom(
                msg.sender,
                address(WarFIFeeAddress),
                (buyerFeeAmount + sellerFeeAmount) / 10**(18 - currencyDecimals)
            );
            if (!successTwo) {
                revert TransferProblem();
            }

            tokenINFO[_tokenIdOwnedByWallet].activePoolSize += tokenINFO[
                _sourceTokenId
            ].salePoolSize;
            _calculateTier(
                _tokenIdOwnedByWallet,
                tokenINFO[_tokenIdOwnedByWallet].activePoolSize +
                    tokenINFO[_tokenIdOwnedByWallet].pendingPoolSize
            );

            totalSalePoolSize -= tokenINFO[_sourceTokenId].salePoolSize;
            totalActivePoolSize += tokenINFO[_sourceTokenId].salePoolSize;
            tokenINFO[_sourceTokenId].salePoolSize = 0;
        } else {
            uint256 buyerFeeAmount = ((tokenINFO[_sourceTokenId].salePoolSize) *
                buyerFee) / (BASIS_POINTS);

            uint256 sellerFeeAmount = ((
                tokenINFO[_sourceTokenId].salePoolSize
            ) * sellerFee) / (BASIS_POINTS);
            uint256 sellerTotalAmount = tokenINFO[_sourceTokenId].salePoolSize -
                sellerFeeAmount;

            bool successOne = IERC20Extented(currencyContract).transferFrom(
                msg.sender,
                _sourceUserAddress,
                sellerTotalAmount / 10**(18 - currencyDecimals)
            );
            if (!successOne) {
                revert TransferProblem();
            }
            bool successTwo = IERC20Extented(currencyContract).transferFrom(
                msg.sender,
                address(WarFIFeeAddress),
                (buyerFeeAmount + sellerFeeAmount) / 10**(18 - currencyDecimals)
            );
            if (!successTwo) {
                revert TransferProblem();
            }
            safeMint(
                msg.sender,
                tokenINFO[_sourceTokenId].salePoolSize,
                true,
                false
            );
            totalSalePoolSize -= tokenINFO[_sourceTokenId].salePoolSize;
            tokenINFO[_sourceTokenId].salePoolSize = 0;
        }

        emit buyEvent(_sourceTokenId);
    }

