                amount
        );

        tokenINFO[tokenIdOwnedByWallet[msg.sender]].claimableRewards -= amount;
        //tokenINFO[tokenIdOwnedByWallet[msg.sender]].claimedRewards += amount;

        currencyContract.transfer(
            msg.sender,
            (amount / 10**(18 - currencyDecimals))
        );

        /*    emit claimRewardEvent(
