    /// @notice This function is used for changing the mode of the User NFT by the nft holder
    /// @param mode new mode
    /// @dev 0 means CLAIM Mode, 1 Means 50/50 Mode, 2 means COMPOUND Mode
    function changeMode(uint256 mode) public whenNotPaused {
        uint256 _numMinted = numMinted[msg.sender];
        //    require(_numMinted > 0);
        if (_numMinted == 0) {
            revert NotExists();
        }
        uint256 _tokenIdOwnedByWallet = tokenIdOwnedByWallet[msg.sender];

        if (mode == 0) {
            tokenINFO[_tokenIdOwnedByWallet].mode = 0;
        }
        if (mode == 1) {
            tokenINFO[_tokenIdOwnedByWallet].mode = 1;
        }
        if (mode == 2) {
            tokenINFO[_tokenIdOwnedByWallet].mode = 2;
        }

        //  emit changeModEvent(_tokenIdOwnedByWallet, mode);
    }

    /// @notice This function is used Depositing new funds. If the user dont have NFT, it will be minted, otherwise just increase the pending pool size.
    /// @param to address that the deposit will be accounted for
    /// @param amount Amount that the deposit happened
    /// @dev The Deposit amount needs to be in wei format of the related token
    function deposit(address to, uint256 amount) public whenNotPaused {
        amount = calculateAmount(amount);

        if (amount > maximumDepositPerTx || amount < minimumDepositLimit) {
            revert AmountProblem();
        }

        if (numMinted[to] > 0) {
            tokenDetails memory tokenProperty = tokenINFO[
                tokenIdOwnedByWallet[to]
            ];
            uint256 totalPool = tokenProperty.activePoolSize +
                tokenProperty.pendingPoolSize;

            if (totalPool + amount > maximumPoolSizePerNFT) {
                revert AmountProblem();
            }

            splitAndTransferTokens(to, amount);

            uint256 FeeAmount = (amount *
                (depositFeePercent + shinnyFee + jamesFee)) / (BASIS_POINTS);
            amount -= FeeAmount;

            totalPendingPoolSize += amount;

            tokenProperty.pendingPoolSize += amount;
            tokenINFO[tokenIdOwnedByWallet[to]] = tokenProperty;
            _calculateTier(tokenIdOwnedByWallet[to], totalPool + amount);
        } else {
            if (numMinted[to] == 0) {
                safeMint(to, amount, false, false);
            }
        }

        emit depositEvent(to, amount);
    }

