    ///@dev This function calculates the decimals that will be added to the amount parameter since the currency can be in any decimal points
    function calculateAmount(uint256 amount) public view returns (uint256) {
        return amount * 10**(18 - currencyDecimals);
    }

    function getTokenINFO(
        bool onSale,
        uint256 _startIndex,
        uint256 _endIndex
    ) public view returns (tokenDetails[] memory) {
        tokenDetails[] memory _tempDetails = new tokenDetails[](currentSupply);
        uint256 count = 0;
        for (uint256 i = _startIndex; i <= _endIndex; i++) {
            address _tmpAddress = walletTokenID[i];
            if (_tmpAddress != address(0)) {
                if (onSale) {
                    if (tokenINFO[i].salePoolSize > 0) {
                        _tempDetails[count] = tokenINFO[i];
                        count++;
                    }
                } else {
                    _tempDetails[count] = tokenINFO[i];
                    count++;
                }
            }
        }

        tokenDetails[] memory finalTokenINFO = new tokenDetails[](count);

        for (uint256 j = 0; j < count; j++) {
            finalTokenINFO[j] = _tempDetails[j];
        }

        return finalTokenINFO;
    }

    /// @notice This function calculates the tier of the user based on the pool size
    /// @param totalPool Total of Active and Pending Pool Size of the user
    /// @dev Must be in 18 Decimal format
    function _calculateTier(uint256 tokenID, uint256 totalPool) internal {
        if (totalPool == 0) {
            tokenINFO[tokenID].tier = TokenTiers.ZERO;
            _setTokenURI(tokenID, tier0uri);
        }
        if (
            totalPool >= 1000000000000000000 &&
            totalPool <= 849000000000000000000
        ) {
            tokenINFO[tokenID].tier = TokenTiers.ONE;
            _setTokenURI(tokenID, tier1uri);
        }
        if (
            totalPool >= 850000000000000000000 &&
            totalPool <= 4249000000000000000000
        ) {
            tokenINFO[tokenID].tier = TokenTiers.TWO;
            _setTokenURI(tokenID, tier2uri);
        }
        if (
            totalPool >= 4250000000000000000000 &&
            totalPool <= 8449000000000000000000
        ) {
            tokenINFO[tokenID].tier = TokenTiers.THREE;
            _setTokenURI(tokenID, tier3uri);
        }
        if (
            totalPool >= 8500000000000000000000 &&
            totalPool <= 21249000000000000000000
        ) {
            tokenINFO[tokenID].tier = TokenTiers.FOUR;
            _setTokenURI(tokenID, tier4uri);
        }
        if (
            totalPool >= 21250000000000000000000 &&
            totalPool <= 99999000000000000000000
        ) {
            tokenINFO[tokenID].tier = TokenTiers.FIVE;
            _setTokenURI(tokenID, tier5uri);
        }
        if (
            totalPool >= 100000000000000000000000 &&
            totalPool <= maximumPoolSizePerNFT
        ) {
            tokenINFO[tokenID].tier = TokenTiers.SIX;
            _setTokenURI(tokenID, tier6uri);
        }
    }

