    /// @notice This function is used for mints
    /// @param to address that the NFT will be minted to
    /// @param amount Amount that the user will pay
    /// @param sale If this is a sale operation this value will be true for calculating the fees
    /// @param migrate If this is a migrate operation, do not request paying. Will be used for migrations
    function safeMint(
        address to,
        uint256 amount,
        bool sale,
        bool migrate
    ) internal whenNotPaused {
        if (numMinted[to] >= 1) {
            revert NotExists();
        }
        currentSupply += 1;

        if (!sale) {
            if (!migrate) {
                splitAndTransferTokens(to, amount);

                uint256 FeeAmount = (amount *
                    (depositFeePercent + shinnyFee + jamesFee)) /
                    (BASIS_POINTS);

                amount -= FeeAmount;
            }
        }

        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        _safeMint(to, tokenId);
        numMinted[to] += 1;
        walletTokenID[tokenId] = to;
        tokenIdOwnedByWallet[to] = tokenId;

        tokenDetails memory _temp;
        _temp.tokenNo = tokenId;
        _temp.mintingTimestamp = block.timestamp;
        _temp.claimableRewards = 0;
        //  _temp.claimedRewards = 0;

        _temp.activePoolSize = 0;

        if (migrate) {
            _temp.activePoolSize = amount;
            totalActivePoolSize += amount;
        } else {
            if (sale) {
                _temp.activePoolSize = amount;
                totalActivePoolSize += amount;
            } else {
                _temp.pendingPoolSize = amount;
                totalPendingPoolSize += amount;
            }
        }

        _temp.salePoolSize = 0;
        _temp.mode = 2;

        tokenINFO[tokenId] = _temp;

        _calculateTier(tokenId, amount);

        emit mintEvent(to, amount, sale);
    }

    ///@dev This function will allow us to disable/enable ERC721 transfers
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal override {
        if (from != address(0)) {
            if (!transferEnabled) {
                revert();
            }
        }

        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    ///@dev This function ditributes the funds and taxes that is paid for deposits
    function splitAndTransferTokens(address from, uint256 amount) internal {
        // Get amount allocated to the reward pool and transfer to it
        uint256 warfiFeeAmount = (amount * depositFeePercent) / (BASIS_POINTS);
        bool transfer1 = currencyContract.transferFrom(
            msg.sender,
            address(WarFIFeeAddress),
            warfiFeeAmount / 10**(18 - currencyDecimals)
        );

        uint256 shinnyFeeAmount = (amount * shinnyFee) / (BASIS_POINTS);
        bool transfer2 = currencyContract.transferFrom(
            msg.sender,
            address(ShinyFeeAddress),
            shinnyFeeAmount / 10**(18 - currencyDecimals)
        );

        uint256 jamesFeeAmount = (amount * jamesFee) / (BASIS_POINTS);
        bool transfer3 = currencyContract.transferFrom(
            msg.sender,
            address(JamesFeeAddress),
            jamesFeeAmount / 10**(18 - currencyDecimals)
        );
        amount -= warfiFeeAmount;
        amount -= shinnyFeeAmount;
        amount -= jamesFeeAmount;
        bool transfer4 = currencyContract.transferFrom(
            msg.sender,
            address(depositAddress),
            amount / 10**(18 - currencyDecimals)
        );

        if (!transfer1 || !transfer2 || !transfer3 || !transfer4) revert();
    }

