    "contracts/Marketing.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract Marketing {
    using SafeMath for uint256;

    address public constant TOKEN_ADDRESS = 0x5f5f7ca63A9d2c5200BC03CA3335a975D8f771d9; // BITS
    address public contractOwner;

    constructor() {
        contractOwner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == contractOwner, "Only the contract owner can call this function");
        _;
    }

    function claim(address[] memory recipients, uint256[] memory amounts) external onlyOwner {
        require(recipients.length == amounts.length, "Arrays length mismatch");
        require(recipients.length > 0, "No recipients provided");

        IERC20 token = IERC20(TOKEN_ADDRESS);

        uint256 totalAmount = 0;
        for (uint256 i = 0; i < recipients.length; i++) {
            require(recipients[i] != address(0), "Invalid recipient address");
            require(amounts[i] > 0, "Amount must be greater than zero");
            totalAmount = totalAmount.add(amounts[i]);

            require(token.balanceOf(contractOwner) >= totalAmount, "Insufficient balance");
            require(token.transfer(recipients[i], amounts[i]), "Token transfer failed");
        }
    }

    function withdraw() external onlyOwner {
        IERC20 token = IERC20(TOKEN_ADDRESS);
        uint256 contractBalance = token.balanceOf(address(this));
        require(contractBalance > 0, "No available tokens to withdraw");

        require(token.transfer(contractOwner, contractBalance), "Token withdrawal failed");
    }
}
