      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @dev This contract is designed to work as fixed reward staking pool which reward could be claimed daily.
 * Has function to add reward to staking pool.
 * Has functions to stake token and withdraw token.
 * Has functions to get user unclaim reward amount.
 * Has functions to get user stake amount and claim amount.
 */
contract Staking is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    struct UserInfo {
        uint256 stakeAmount;
        uint256 claimedAmount;
    }

    address public upoTokenAddress;

    mapping(address => UserInfo) public userInfo;
    uint256 public startStakeTime;
    uint256 public endStakeTime;
    uint256 public duration;
    uint256 public userStakeLimit;
    uint256 public poolStakeLimit;
    uint256 public poolStakeTotal;
    uint256 public poolReward;
    uint256 public percentage;
    uint256 public distributedReward;

    event PoolRewardAdded(uint256 upoAmount);
    event PoolRewardReduced(uint256 upoAmount);
    event UpoStaked(address indexed userAddress, uint256 upoAmount);
    event WithdrawnUpo(address indexed userAddress, uint256 upoAmount);
    event RewardClaimed(address indexed userAddress, uint256 upoAmount);
    event StakingPaused();
    event StakingUnpaused();

    constructor(
        address _upoTokenAddress,
        uint256 _startStakeTime,
        uint256 _endStakeTime,
        uint256 _userStakeLimit,
        uint256 _poolStakeLimit,
        uint256 _percentage
    ) {
        upoTokenAddress = _upoTokenAddress;
        startStakeTime = _startStakeTime;
        endStakeTime = _endStakeTime;
        userStakeLimit = _userStakeLimit;
        poolStakeLimit = _poolStakeLimit;
        percentage = _percentage;

        uint256 startDay = (startStakeTime - (startStakeTime % 86400)) / 86400;
        uint256 endDay = (endStakeTime - (endStakeTime % 86400)) / 86400;
        uint256 _duration = endDay - startDay;
        duration = _duration;
    }

    /**
     * @dev Modifier to only allow the function to be executed before staking period started
     */
    modifier beforeStakeStarted() {
        require(
            block.timestamp < startStakeTime,
            "[Staking.beforeStakeStarted] Staking period started"
        );
        _;
    }

    /**
     * @dev Modifier to only allow the function to be executed after staking period started
     */
    modifier afterStakeStarted() {
        require(
            block.timestamp >= startStakeTime,
            "[Staking.afterStakeStarted] Staking period not yet started"
        );
        _;
    }

    /**
     * @dev Modifier to only allow the function to be executed after staking period end
     */
    modifier afterStakeEnded() {
        require(
            block.timestamp >= endStakeTime,
            "[Staking.afterStakeEnded] Staking period not yet ended"
        );
        _;
    }

    /**
     * @dev Revert receive and fallback functions.
     */
    receive() external payable {
        revert("[Staking] Revert receive function.");
    }

    fallback() external payable {
        revert("[Staking] Revert fallback function.");
    }

    /**
     * @dev Function for increase staking pool reward.
     * Owner can use to increase pool reward in contract.
     * @param _upoAmount - Amount of upo to be added to pool reward.
     */
    function addPoolReward(uint256 _upoAmount) external onlyOwner {
        IERC20 token = IERC20(upoTokenAddress);
        token.safeTransferFrom(msg.sender, address(this), _upoAmount);
        poolReward += _upoAmount;

        emit PoolRewardAdded(_upoAmount);
    }

    /**
     * @dev Function for withdraw exceed staking pool reward.
     * Owner can use to withdraw and decrease pool reward in contract.
     */
    function withdrawExceedPoolReward() external onlyOwner afterStakeStarted {
        uint256 actualRewardAmount = (poolStakeTotal * percentage * duration) /
            100 /
            365;
        require(
            poolReward > actualRewardAmount,
            "[Staking.withdrawExceedPoolReward] Pool reward should be more than actual reward amount"
        );
        uint256 exceedAmount = poolReward - actualRewardAmount;
        poolReward -= exceedAmount;
        safeUpoTransfer(msg.sender, exceedAmount);

        emit PoolRewardReduced(exceedAmount);
    }

    /**
     * @dev Function for stake token.
     * Use to increase user stake amount and pool stake total before staking period start.
     * @param _upoAmount - Amount of upo to be staked.
     */
    function stakeUpo(uint256 _upoAmount)
        external
        beforeStakeStarted
        whenNotPaused
        nonReentrant
    {
        require(
            _upoAmount + userInfo[msg.sender].stakeAmount <= userStakeLimit,
            "[Staking.stakeUpo] total stake amount should be less than user limit"
        );
        require(
            _upoAmount + poolStakeTotal <= poolStakeLimit,
            "[Staking.stakeUpo] total stake amount should be less than pool limit"
        );
        IERC20 token = IERC20(upoTokenAddress);
        token.safeTransferFrom(msg.sender, address(this), _upoAmount);
        userInfo[msg.sender].stakeAmount += _upoAmount;
        poolStakeTotal += _upoAmount;

        emit UpoStaked(msg.sender, _upoAmount);
    }

    /**
     * @dev Function for withdraw staked token.
     * Use to withdraw user all remaining staked token and delete user info after user claim all reward and staking period end.
     */
    function withdrawUpo() external afterStakeEnded nonReentrant {
        uint256 unclaimAmount = getUserUnclaimAmount(msg.sender);
        require(
            0 == unclaimAmount,
            "[Staking.withdrawUpo] unclaim amount should be zero before user can withdraw"
        );

        safeUpoTransfer(msg.sender, userInfo[msg.sender].stakeAmount);
        emit WithdrawnUpo(msg.sender, userInfo[msg.sender].stakeAmount);

        delete userInfo[msg.sender];
    }

    /**
     * @dev Function for claim staking reward.
     * Use to transfer reward to user wallet and increase user claim amount limit by user unclaim amount.
     * @param _upoAmount - Amount of upo to be claimed.
     */
    function claimReward(uint256 _upoAmount)
        external
        afterStakeStarted
        nonReentrant
        whenNotPaused
    {
        uint256 unclaimAmount = getUserUnclaimAmount(msg.sender);
        require(
            _upoAmount <= unclaimAmount,
            "[Staking.claimReward] claim amount should be less than user unclaim amount"
        );
        userInfo[msg.sender].claimedAmount += _upoAmount;
        distributedReward += _upoAmount;
        safeUpoTransfer(msg.sender, _upoAmount);

        emit RewardClaimed(msg.sender, _upoAmount);
    }

    /**
     * @dev Function for retrieve user stake amount.
     * Use to retrieve specify user stake amount.
     * @param _userAddress - user address to be retrieve.
     */
    function getUserStakeAmount(address _userAddress)
        external
        view
        returns (uint256)
    {
        return userInfo[_userAddress].stakeAmount;
    }

    /**
     * @dev Function for retrieve user claimed amount.
     * Use to retrieve specify user claimed amount.
     * @param _userAddress - user address to be retrieve.
     */
    function getUserClaimedAmount(address _userAddress)
        external
        view
        returns (uint256)
    {
        return userInfo[_userAddress].claimedAmount;
    }

    /**
     * @dev Function for retrieve user unclaim amount.
     * Use to retrieve specify user unclaim amount.
     * Calculated by claimedAmount - (poolReward * dayPassed * stakeAmount) / duration / poolStakeTotal.
     * @param _userAddress - user address to be retrieve.
     */
    function getUserUnclaimAmount(address _userAddress)
        public
        view
        returns (uint256)
    {
        UserInfo memory user = userInfo[_userAddress];
        uint256 currentDay = (block.timestamp - (block.timestamp % 86400)) /
            86400;
        uint256 startDay = (startStakeTime - (startStakeTime % 86400)) / 86400;
        uint256 dayPassed = (currentDay - startDay) > duration
            ? duration
            : (currentDay - startDay);

        uint256 totalRewardUntilToday = (user.stakeAmount *
            percentage *
            dayPassed) /
            100 /
            365;
        return totalRewardUntilToday - user.claimedAmount;
    }

    function safeUpoTransfer(address _userAddress, uint256 _upoAmount)
        internal
    {
        IERC20 token = IERC20(upoTokenAddress);

        uint256 upoBalance = token.balanceOf(address(this));
        if (_upoAmount >= upoBalance) {
            token.safeTransfer(_userAddress, upoBalance);
        } else {
            token.safeTransfer(_userAddress, _upoAmount);
        }
    }

    /**
     * @dev Function for pause staking.
     * Owner can use to pause staking.
     */
    function pause() external onlyOwner {
        _pause();
        emit StakingPaused();
    }

    /**
     * @dev Function for unpause staking.
     * Owner can use to unpause staking.
     */
    function unpause() external onlyOwner {
        _unpause();
        emit StakingUnpaused();
    }
}
