contract Staking is Ownable{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    
    event Withdraw(address addr, uint256 amount);
    event Stake(address addr, uint256 amount);
    // event WithdarwAll(address addr);
    
    struct staker{
        uint256 amount;
        uint256 reward;
        uint256 stakeTime;
        uint256 total_reward;
    }

    uint256 public total_stake;
    uint256 public total_reward;
    uint256 public resumeTime = block.timestamp;
    bool public isPaused = false;
    
    mapping (address => staker)  _stakers;
    
    IERC20 public CSPN;

    constructor(IERC20 _cspn){
        CSPN = _cspn;
    }
    
    function calculateStakeReward(address addr) internal view returns (uint256) {
        // First calculate how long the stake has been active
        // Use current seconds since epoch - the seconds since epoch the stake was made
        // The output will be duration in SECONDS ,
        // We will reward the user 0.1% per Hour So thats 0.1% per 3600 seconds
        // the alghoritm is  seconds = block.timestamp - stake seconds (block.timestap - _stake.since)
        // hours = Seconds / 3600 (seconds /3600) 3600 is an variable in Solidity names hours
        // we then multiply each token by the hours staked , then divide by the rewardPerDay rate
        uint256 rewardPerDay; //10%

        if (isPaused) {
            rewardPerDay = 0;
        } else {
            rewardPerDay = 1000;
            // need update decimals
            if (_stakers[addr].amount >= 1337 * 10 ** 18 && _stakers[addr].amount < 10000 * 10 ** 18) {
                rewardPerDay = 1337; //13.37%
            } else if (
                _stakers[addr].amount >= 10000 * 10 ** 18 && _stakers[addr].amount < 25000 * 10 ** 18
            ) {
                rewardPerDay = 1500; //15%
            } else if (
                _stakers[addr].amount >= 25000 * 10 ** 18 && _stakers[addr].amount < 50000 * 10 ** 18
            ) {
                rewardPerDay = 1650; //16.5%
            } else if (
                _stakers[addr].amount >= 50000 * 10 ** 18 && _stakers[addr].amount < 100000 * 10 ** 18
            ) {
                rewardPerDay = 1800; //18%
            } else if (_stakers[addr].amount >= 100000  * 10 ** 18) {
                rewardPerDay = 2000; //20%
            }
        }
        // 24 hours => 1 minutes for test
        if(resumeTime < _stakers[addr].stakeTime) {
            return (((block.timestamp - _stakers[addr].stakeTime) / 24 hours ) * _stakers[addr].amount) * rewardPerDay / 365 / 10000;
        } else {
            return (((block.timestamp - resumeTime) / 24 hours ) * _stakers[addr].amount) * rewardPerDay / 365 / 10000;
        }
    }


    function getStakeAmount(address addr) public view returns (uint256) {
        return _stakers[addr].amount;
    }

    function stake(uint256 amount) external {
        require(amount != 0 , "amount must not 0");

        CSPN.transferFrom(msg.sender, address(this), amount);
        
        _stakers[msg.sender].reward = _stakers[msg.sender].reward + calculateStakeReward(msg.sender);
        _stakers[msg.sender].amount = _stakers[msg.sender].amount + amount;
        _stakers[msg.sender].stakeTime = block.timestamp;

        total_stake = total_stake + amount;
        
        emit Stake(msg.sender, amount);
    }

    function withdraw(uint256 amount) external {
        require(amount != 0 , "amount must not 0");
        require(_stakers[msg.sender].amount - amount >= 0, "There is no amount to withdraw");
        
        CSPN.transfer(msg.sender, amount);
        
        _stakers[msg.sender].reward = _stakers[msg.sender].reward + calculateStakeReward(msg.sender);
        _stakers[msg.sender].amount = _stakers[msg.sender].amount - amount;
        _stakers[msg.sender].stakeTime = block.timestamp;

        total_stake = total_stake - amount;
        
        if(_stakers[msg.sender].amount == 0) {
            harvest(_stakers[msg.sender].reward);
        }
        emit Withdraw(msg.sender, amount);
    }

    function getReward(address addr) public view returns (uint256) {
        return _stakers[addr].reward + calculateStakeReward(addr);
    }

    function getTotalReward(address addr) public view returns (uint256) {
        return _stakers[addr].total_reward;
    }

    function harvest(uint256 amount) public {
        uint256 reward = getReward(msg.sender);
        require(reward >= amount, "can't harvest more than available");
        _stakers[msg.sender].reward = reward - amount;
        _stakers[msg.sender].total_reward = _stakers[msg.sender].total_reward + amount;
        _stakers[msg.sender].stakeTime = block.timestamp;
        total_reward = total_reward + amount;
        CSPN.transfer(msg.sender, amount);
    }

    function reinvest(uint256 amount) public {
        uint256 reward = getReward(msg.sender);
        require(reward >= amount, "can't reinvest more than available");
        _stakers[msg.sender].reward = reward - amount;
        _stakers[msg.sender].stakeTime = block.timestamp;
        _stakers[msg.sender].amount = _stakers[msg.sender].amount + amount;
        total_reward = total_reward + amount;
    }

    function setPaused(bool _paused) external onlyOwner {
        if(_paused == false) {
            resumeTime = block.timestamp;
        }
        isPaused = _paused;
    }

    function withdrawOwner(uint256 amount) external onlyOwner{
        require(amount < CSPN.balanceOf(address(this)), "There is no amount to withdraw");
        CSPN.transfer(_msgSender(), amount);
    }
}
