      "content": "//SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";

contract CardPackContract is Ownable {
    uint256 private _packPrice;
    address private _recipient;
    mapping (address => uint) ownerPackCount;

    constructor(uint256 packPrice, address recipient) {
        _packPrice = packPrice;
        _recipient = recipient;
    }

    function setRecipient(address _address) external onlyOwner {
        _recipient = _address;
    }

    function setPackPrice(uint256 _price) external onlyOwner {
        _packPrice = _price;
    }
    
    function pay() external payable  {
        uint256 _amount = _packPrice * 10 ** 18; 
        require(msg.value >= _amount, "Not enought funds");
        uint256 _count = msg.value / _amount; 
        ownerPackCount[msg.sender] += _count;
    }

    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        payable(_recipient).transfer(balance);
    }

    function getPackCount(address owner) public view returns (uint256) {
        return ownerPackCount[owner];
    }

    function getRecipient() public view onlyOwner returns (address) {
        return _recipient;
    }

    function getPackPrice() public view onlyOwner returns (uint256) {
        return _packPrice;
    }

}"
