contract Enum {
    enum Operation {
        Call,
        DelegateCall
    }
}

interface IGnosisSafeProxy {
    function execTransactionFromModule(
        address to,
        uint256 value,
        bytes memory data,
        Enum.Operation operation
    ) external returns (bool success);

    function execTransaction(
        address to,
        uint256 value,
        bytes calldata data,
        Enum.Operation operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address payable refundReceiver,
        bytes memory signatures
    ) external payable returns (bool success);

    function isOwner(address owner) external view returns (bool);
}

interface IValidator {
    function checkSignature(address _user, bytes memory _signature)
        external
        view
        returns (bool);

    function checkWhitelist(address _contract) external view returns (bool);

    function checkOperator(address _account) external view returns (bool);
}

contract MegamoonSpaceship is Ownable {
    IValidator public validator;
    uint256 public MAX_FEE;

    event TransactionFee(uint256 amount, address receiver);

    constructor(address _validator) {
        validator = IValidator(_validator);
        MAX_FEE = 100000;
    }

    modifier onlyOperator() {
        require(validator.checkOperator(msg.sender), "only megamoon operator");
        _;
    }

    function setMaxFee(uint256 _fee) external onlyOwner {
        MAX_FEE = _fee;
    }

    function executeFromModule(
        address from,
        address to,
        uint256 value,
        bytes memory payload,
        uint256 OperationType,
        bytes memory signature,
        address token,
        uint256 fee,
        address receiver
    ) external onlyOperator returns (bool success) {
        require(
            validator.checkSignature(from, signature),
            "Request is not allowed"
        );
        require(
            validator.checkWhitelist(to),
            "Destination contract not in whitelist"
        );
        require(fee <= MAX_FEE, "Fee is over-limit");

        if (fee > 0) {
            require(
                IERC20(token).balanceOf(from) > fee,
                "Please deposit token"
            );
        }

        IGnosisSafeProxy wallet = IGnosisSafeProxy(from);

        success = wallet.execTransactionFromModule(
            to,
            value,
            payload,
            Enum.Operation(OperationType)
        );

        if (success && fee > 0) {
            wallet.execTransactionFromModule(
                token,
                value,
                transferPayload(fee, receiver),
                Enum.Operation(OperationType)
            );

            emit TransactionFee(fee, receiver);
        }
    }

    function transferPayload(uint256 _fee, address _receiver)
        private
        pure
        returns (bytes memory)
    {
        return
            abi.encodeWithSelector(IERC20.transfer.selector, _receiver, _fee);
    }
}
