    "contracts/MysteryBox.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/utils/Counters.sol';
import '@openzeppelin/contracts/utils/Strings.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/access/AccessControlEnumerable.sol';

contract MysteryBox is ERC721, ReentrancyGuard, AccessControlEnumerable {
    // use Counter for auto incrementing tokenId
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;

    bytes32 public constant MINTER_ROLE = keccak256('MINTER_ROLE');

    mapping(string => uint256[]) public redeemInfo; // mapping orderId to tokenIds
    mapping(uint256 => address) public tokensRedeemed; // mapping redeemed tokenId to owner
    mapping(uint256 => uint256) public tokenLevels; // mapping tokenId to level
    mapping(uint256 => string) public tokenLevelURIs; // mapping level URI
    string public baseURI;

    event PhygitalRedeemed(
        address indexed wallet,
        uint256 indexed tokenId,
        string indexed orderId
    );

    event Mint(
        address indexed wallet,
        uint256 indexed tokenId,
        uint256 indexed level
    );

    event SetBaseURI(
        string indexed baseURI,
        address indexed admin
    );

    event SetLevelURIs(
        string indexed baseURI,
        uint256 indexed level,
        address indexed admin
    );

    constructor(string memory _name, string memory _symbol) ERC721(_name, _symbol) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
         _tokenIds.increment(); //tokenId starts from 1
    }

    // mint function, only minter can call
    function mint(
        address _account,
        uint256 _level
    ) public nonReentrant onlyRole(MINTER_ROLE) returns (uint256) {
        uint256 newItemId = _tokenIds.current();
        _mint(_account, newItemId);

        tokenLevels[newItemId] = _level;
        emit Mint(_account, newItemId, _level);

        _tokenIds.increment();
        return newItemId;
    }

    function redeemPhygital(
        uint256[] memory tokenIds,
        string memory orderId
    ) public {
        require(bytes(orderId).length > 0, 'Invalid orderId');
        require(tokenIds.length > 0, 'Invalid tokenIds');
        // check whether redeemed[orderId] is empty
        require(redeemInfo[orderId].length == 0, 'Duplicate orderId');

        // batch burn
        for (uint256 i = 0; i < tokenIds.length; i++) {
            _requireMinted(tokenIds[i]);
            require(ownerOf(tokenIds[i]) == msg.sender, 'Burner not owner');
            _burn(tokenIds[i]);
            tokensRedeemed[tokenIds[i]] = msg.sender;
            emit PhygitalRedeemed(msg.sender, tokenIds[i], orderId);
        }
        redeemInfo[orderId] = tokenIds;
    }

    //// EIP1363 payable token
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(AccessControlEnumerable, ERC721)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    // set level corresponding URI
    function setLevelURIs(uint256 levelId, string memory newUri)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {   
        tokenLevelURIs[levelId] = newUri;
        emit SetLevelURIs(newUri, levelId, msg.sender);
    }

    // set baseURI to reveal the mystery box
    function setBaseURI(string memory newUri)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {   
        baseURI = newUri;
        emit SetBaseURI(newUri, msg.sender);
    }

    // override tokenURI to implement the mystery box logic
    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        _requireMinted(tokenId);

        return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, Strings.toString(tokenId))) : tokenLevelURIs[tokenLevels[tokenId]];
    }

    function setMinterRole(address account)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        _setupRole(MINTER_ROLE, account);
    }

    function removeMinterRole(address account)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        _revokeRole(MINTER_ROLE, account);
    }
}
"
    },
    "contracts/MysteryBoxCampaignContract.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import '@openzeppelin/contracts/utils/cryptography/MerkleProof.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import './MysteryBox.sol';



contract MysteryBoxCampaignContract is ReentrancyGuard, Ownable {
    string public name;
    uint32 public campaignNum;
    uint8 public allowedRoundDifference;
    address public verifier;
    address public ethPriceOracle; // the base token of the Chain, e.g. ETH, BNB, MATIC, etc.
    MysteryBox token;

    struct CampaignTrack {
        uint32 campaignId;
        uint256[] prices; // base unit: depending on base Currency
        address baseCurrencyOracle;
        uint256[] maxTokens; // available tokens for each level, set 0 for [0] by default. [1] -> level 1; [2] -> level 2; ... This is because mapping in struct can not be emitted
        uint256 maxTokensPerUser; // 0 means unlimited
        IERC20Metadata[] paymentTokens;
        bytes32 merkleRoot;
        uint32 startTime;
        uint32 endTime;
    }

    mapping(uint256 => CampaignTrack) public campaigns;
    mapping(uint256 => mapping(address => uint256)) public userPurchases;
    mapping(uint256 => mapping(uint256 => uint256)) public tokensMinted;
    mapping(IERC20Metadata => address) public paymentTokenPriceFeeds;

    event UpdateMerkleRoot(uint32 indexed campaignId, bytes32 merkleRoot);
    event UpdatePaymentTokens(
        uint32 indexed campaignId,
        IERC20Metadata[] paymentTokens
    );

    event BuyToken(address indexed buyer, uint32 level, uint256 amount, uint32 campaignId);
    event BuyTokenWithFiat(string orderId, uint32 level, uint256 amount, uint32 campaignId);
    event AddTrack(uint32 indexed campaignId, CampaignTrack campaign);
    event CampaignStopped(uint32 indexed campaignId);
    event UpdatePaymentTokenOracleAddress(IERC20Metadata paymentToken, address oracleAddress);
    event UpdateHost(address verifier);


    constructor(string memory _name, MysteryBox _token, address _verifier, address _ethPriceOracle, uint8 _allowedTimeDifference) {
        name = _name;
        token = _token;
        verifier = _verifier;
        ethPriceOracle = _ethPriceOracle;
        allowedRoundDifference = _allowedTimeDifference;
    }

    // buy token with Base Token, e.g. ETH, BNB, MATIC, etc.
    function buyTokenWithEth( 
        uint32 _campaignId,
        uint256 _amount,
        uint32 _level,
        uint80 _ETHroundId,
        uint80 _BaseCurrencyRoundId,
        bytes32[] memory _proof
    ) public payable nonReentrant {
        require(_campaignId < campaignNum, 'Invalid campaign ID');
        CampaignTrack storage campaign = campaigns[_campaignId];

        require(_validateBuyToken(_campaignId, _amount, _level), 'invalid parameters');

        // ETH price feed
        uint256 itemPriceIn8Decimals = getOnChainPaymentTokenPrice(ethPriceOracle, _campaignId, _level, _ETHroundId, _BaseCurrencyRoundId);
        // price decimals: 8, eth decimals: 18
        uint256 itemPrice = itemPriceIn8Decimals * (10 ** 10);

        // no whitelist, everyone can mint
        if (campaign.merkleRoot != 0) {
            require(
                verifyEligible(msg.sender, campaign.merkleRoot, _proof),
                'You are not authorized to purchase a token'
            );
        }
        _mintTokenWithEth(itemPrice, _amount, _level, msg.sender);

        emit BuyToken(msg.sender, _level, _amount, campaign.campaignId);
    }

    // buy token with Fiat, return bought token ids 
    function buyTokenWithFiat(
        uint32 _campaignId, 
        uint256 _amount, 
        uint32 _level,
        string memory orderId

    ) public nonReentrant returns (uint256[] memory){
        require(_campaignId < campaignNum, 'Invalid campaign ID');

        require(_validateBuyToken(_campaignId, _amount, _level), 'invalid parameters');

        address sender = msg.sender;

        require(sender == verifier, "Invalid host address");

        // Batch mint, record the token ids and return
        uint256[] memory tokenIds = new uint256[](_amount);
        for (uint i = 0; i < _amount; i++) {
            tokenIds[i] = token.mint(sender, _level);
        }

        emit BuyTokenWithFiat(orderId, _level, _amount, _campaignId);

        return tokenIds;

    }

    // note: FE call oracle contract to get the price and let user approve the paymentToken
    function buyToken(
        uint32 _campaignId,
        IERC20Metadata _paymentToken,
        uint256 _amount,
        uint32 _level,
        bytes32[] memory _proof,
        uint80 _paymentTokenRoundId,
        uint80 _baseCurrencyRoundId
    ) public nonReentrant {
        require(_campaignId < campaignNum, 'Invalid campaign ID');
        CampaignTrack storage campaign = campaigns[_campaignId];

        require(_validateBuyToken(_campaignId, _amount, _level), 'invalid parameters');

        require(
            isSupportedPaymentToken(_campaignId, _paymentToken),
            'Payment token is not supported'
        );
        // payment token price feed
        uint256 itemPriceIn8Decimals = getOnChainPaymentTokenPrice(paymentTokenPriceFeeds[_paymentToken], _campaignId, _level, _paymentTokenRoundId, _baseCurrencyRoundId);

        uint256 payTokenDeciamls = _paymentToken.decimals();
        // price decimals: 8
        uint256 itemPrice = ( itemPriceIn8Decimals * (10 ** (payTokenDeciamls)) ) / (10 ** 8);

        // no whitelist, everyone can mint
        if (campaign.merkleRoot != 0) {
            require(
                verifyEligible(msg.sender, campaign.merkleRoot, _proof),
                'You are not authorized to purchase a token'
            );
        }
        _mintToken(itemPrice, _paymentToken, _amount, _level, msg.sender);

        emit BuyToken(msg.sender, _level, _amount, campaign.campaignId);
    }

    function addTrack(
        uint256[] memory prices,
        address baseCurrencyOracle,
        uint256[] memory maxTokens,
        uint256 maxTokensPerUser,
        IERC20Metadata[] memory paymentTokens,
        bytes32 merkleRoot,
        uint32 startTime,
        uint32 endTime
    ) public onlyOwner returns (uint32){        
        uint256 currentTimestamp = block.timestamp;
        require(
            startTime > currentTimestamp &&
                endTime > currentTimestamp &&
                endTime > startTime,
            'Invalid campaign schedule'
        );
        require(prices.length == maxTokens.length, 'Invalid input');

        require(baseCurrencyOracle != address(0), 'Invalid base currency oracle');

        bool hasNonZeroMaxToken = false;
        for (uint i = 1; i < maxTokens.length; i++) {
            if (maxTokens[i] > 0) {
                hasNonZeroMaxToken = true;
                break;
            }
        }
        require(hasNonZeroMaxToken, 'Zero max tokens for every level not allowed');

        CampaignTrack memory campaign = CampaignTrack(
            campaignNum,
            prices,
            baseCurrencyOracle,
            maxTokens,
            maxTokensPerUser,
            paymentTokens,
            merkleRoot,
            startTime,
            endTime
        );

        campaigns[campaignNum] = campaign;
        campaignNum += 1;
        emit AddTrack(campaignNum - 1, campaign);
        return campaignNum - 1;

    }

    // stop the campaign
    function stopCampaign(uint32 _campaignId) public onlyOwner {
        require(_campaignId < campaignNum, 'Invalid campaign ID');
        CampaignTrack storage campaign = campaigns[_campaignId];

        // Update the endTime to the current block timestamp
        campaign.endTime = uint32(block.timestamp);

        emit CampaignStopped(_campaignId);
    }

    // add or update payment token oracle address
    function updatePaymentTokenOracleAddress(
        IERC20Metadata _paymentToken,
        address _oracleAddress
    ) public onlyOwner {
        require(_oracleAddress != address(0), 'Invalid oracle address');
        paymentTokenPriceFeeds[_paymentToken] = _oracleAddress;

        emit UpdatePaymentTokenOracleAddress(_paymentToken, _oracleAddress);
    }

    function updatePaymentTokens(
        uint32 _campaignId,
        IERC20Metadata[] memory _paymentTokens
    ) public onlyOwner {
        require(_campaignId < campaignNum, 'Invalid campaign ID');

        CampaignTrack storage campaign = campaigns[_campaignId];
        campaign.paymentTokens = _paymentTokens;

        emit UpdatePaymentTokens(_campaignId, _paymentTokens);
    }

    function isSupportedPaymentToken(uint32 _campaignId, IERC20Metadata _paymentToken)
        public
        view
        returns (bool)
    {
        require(_campaignId < campaignNum, 'Invalid campaign ID');
        CampaignTrack memory campaign = campaigns[_campaignId];
        for (uint32 i = 0; i < campaign.paymentTokens.length; i++) {
            if (campaign.paymentTokens[i] == _paymentToken) {
                return true;
            }
        }

        return false;
    }

    function updateMerkleRoot(uint32 _campaignId, bytes32 _merkleRoot)
        public
        onlyOwner
    {
        require(_campaignId < campaignNum, 'Invalid campaign ID');
        CampaignTrack storage campaign = campaigns[_campaignId];
        campaign.merkleRoot = _merkleRoot;

        emit UpdateMerkleRoot(_campaignId, _merkleRoot);
    }

    // verify if the account is in the whitelist
    function verifyEligible(
        address _account,
        bytes32 _merkleRoot,
        bytes32[] memory _proof
    ) public pure returns (bool) {
        bytes32 leaf = keccak256(abi.encodePacked(_account));
        return MerkleProof.verify(_proof, _merkleRoot, leaf);
    }

    function _validateBuyToken(uint32 _campaignId, uint256 _amount, uint32 _level)
        private
        returns (bool)
    {
        require(_campaignId < campaignNum, 'Invalid campaign ID');
        CampaignTrack memory campaign = campaigns[_campaignId];

        uint256 currentTimestamp = block.timestamp;

        userPurchases[_campaignId][msg.sender] += _amount;
        tokensMinted[_campaignId][_level] += _amount;

        require(
            currentTimestamp > campaign.startTime &&
                currentTimestamp < campaign.endTime,
            'Campaign is not running'
        );

        // level starts from 1 and less than campaign.maxTokens.length
        require(
            _level > 0 && 
                _level < campaign.maxTokens.length, 
            'Invalid NFT level'
        );

        require(
            campaign.maxTokensPerUser == 0 ||
                msg.sender == verifier ||
                userPurchases[_campaignId][msg.sender] <= campaign.maxTokensPerUser,
            'Exceeding purchase limit'
        );



        require(
            tokensMinted[_campaignId][_level] <= campaign.maxTokens[_level], 
            'Maximum tokens minted'
        );

        return true;
    }

    function _mintTokenWithEth(
        uint256 _perEthPrice,
        uint256 _amount,
        uint32 _level,
        address _account
    ) private {
        require(
            msg.value >= _perEthPrice * _amount,
            'Insufficient payment'
        );
        // Batch mint
        for (uint i = 0; i < _amount; i++) {
            token.mint(_account, _level);
        }
    }

    function _mintToken(
        uint256 _perTokenPrice,
        IERC20Metadata _paymentToken,
        uint256 _amount,
        uint32 _level,
        address _account
    ) private {

        require(
            _paymentToken.transferFrom(
                msg.sender,
                address(this),
                _perTokenPrice * _amount
            ),
            'Transfer failed, check allowance or balance'
        );
        // Batch mint
        for (uint i = 0; i < _amount; i++) {
            token.mint(_account, _level);
        }
    }

    function getCampaignPrices(
        uint256 _campaignId
    )
        public view returns (uint256[] memory) {

        require(_campaignId < campaignNum, 'Invalid campaign ID');

        return campaigns[_campaignId].prices;
    }

    // get the Token price in base token, for frontend to call
    // input parameter: _aggregatorV3Path: the address of the price feed contract, campaignId: the campaign id, level: the level of the token
    function getLatestPaymentTokenPriceInBaseCurrency(
        address _aggregatorV3Path,
        uint32 _campaignId,
        uint32 _level
    ) public view returns (uint256, uint80, uint80) {
        require(_campaignId < campaignNum, 'Invalid campaign ID');
        CampaignTrack memory campaign = campaigns[_campaignId];
        address baseCurrencyOracle = campaign.baseCurrencyOracle;
        uint256 levelPrice = campaign.prices[_level]; // decimals: 8


        // get the price in USD of the payment token
        (uint80 paymentTokenRoundID, int256 paymentTokenPrice, , ,) = AggregatorV3Interface(
            _aggregatorV3Path).latestRoundData();

        // if the payment token is the base currency, return the price directly
        if (_aggregatorV3Path == baseCurrencyOracle) {
            return (levelPrice, paymentTokenRoundID, paymentTokenRoundID);
        }

        // get the price in USD of the base currency
        (uint80 baseCurrencyRoundID, int256 baseCurrencyPrice, , ,) = AggregatorV3Interface(
            baseCurrencyOracle).latestRoundData();

        // decimals: 8
        uint256 itemPriceInPaymentToken = (levelPrice * uint256(baseCurrencyPrice)) / uint256(paymentTokenPrice);

        return (itemPriceInPaymentToken, paymentTokenRoundID, baseCurrencyRoundID);
    }

    // get the onChian price of the payment token
    function getOnChainPaymentTokenPrice(
        address _aggregatorV3Path, // payment Token price feed address
        uint32 _campaignId,
        uint32 _level,
        uint80 _paymentTokenPriceRoundId, // payment token price round id
        uint80 _baseCurrencyPriceRoundId // base currency price round id
    ) public view returns (uint256) {
        CampaignTrack memory campaign = campaigns[_campaignId];
        address baseCurrencyOracle = campaign.baseCurrencyOracle;
        uint256 levelPrice = campaign.prices[_level]; // decimals: 8


        // allow the base currency price valid within specifc period from the latest timestamp
        (uint80 latestBaseCurrencyRoundId, , , ,) = AggregatorV3Interface(baseCurrencyOracle).latestRoundData();
        require(latestBaseCurrencyRoundId - _baseCurrencyPriceRoundId <= allowedRoundDifference, 'Invalid base currency price round');

        // allow the payment token price valid within specifc period from the latest timestamp
        (uint80 latestRoundId, , , ,) = AggregatorV3Interface(_aggregatorV3Path).latestRoundData();
        require(latestRoundId - _paymentTokenPriceRoundId <= allowedRoundDifference, 'Invalid payment token price round');

         // if the payment token is the base currency, return the price directly
        if (_aggregatorV3Path == baseCurrencyOracle) {
            return levelPrice;
        }

        // get the price in USD of the payment token
        (, int256 paymentTokenPrice, , ,) = AggregatorV3Interface(_aggregatorV3Path).getRoundData(_paymentTokenPriceRoundId);


        // get the price in USD of the base currency
        (, int256 baseCurrencyPrice, , ,) = AggregatorV3Interface(baseCurrencyOracle).getRoundData(_baseCurrencyPriceRoundId);

        // decimals: 8
        uint256 itemPriceInPaymentToken = (levelPrice * uint256(baseCurrencyPrice)) / uint256(paymentTokenPrice);

        return itemPriceInPaymentToken;
    }

    // getter functions of campaign track and tokenMinted
    function getCampaignTrack(uint32 _campaignId) public view returns (CampaignTrack memory) {
        require(_campaignId < campaignNum, 'Invalid campaign ID');
        return campaigns[_campaignId];
    }

    function getTokenMinted(uint32 _campaignId, uint32 _level) public view returns (uint256) {
        require(_campaignId < campaignNum, 'Invalid campaign ID');
        require(_level > 0 && _level < campaigns[_campaignId].maxTokens.length, 'Invalid level');
        return tokensMinted[_campaignId][_level];
    }

    function withdraw(IERC20Metadata _token) public onlyOwner {
        _token.transfer(msg.sender, _token.balanceOf(address(this)));
    }

    function withdrawEth() public onlyOwner {
        address payable to = payable(msg.sender);
        to.transfer(address(this).balance);
    }

    function setHost(address _verifier) public onlyOwner
    {
        require(_verifier != address(0), "Invalid verifier address");
        verifier = _verifier;

        emit UpdateHost(_verifier);
    }

    function removeHost() public onlyOwner
    {
        verifier = address(0);
    }
}
