pragma solidity ^0.6.12;

abstract contract TransactionThrottler is Ownable {
    uint256 private _tradingStart;
    uint256 private _restrictionEndTime;
    uint256 private _maxTransferAmount;
    mapping(address => bool) private _isWhitelisted;
    mapping(address => bool) private _isAllowed;
    mapping(address => uint256) private _previousTx;

    constructor() public {
        _tradingStart = block.timestamp + 1 days;
        _restrictionEndTime = _tradingStart + 10 minutes;
        _maxTransferAmount = 35000 * 10**18;
        _isAllowed[msg.sender] = true;
    }

    function setTradingStart(uint256 time) external onlyOwner {
        require(_tradingStart > block.timestamp, "Protection: To late");
        _tradingStart = time;
        _restrictionEndTime = time + 10 minutes;
    }

    function setMaxTransferAmount(uint256 amount) external onlyOwner {
        _maxTransferAmount = amount;
    }

    function setWhitelisted(address account) external onlyOwner {
        _isWhitelisted[account] = true;
    }

    function setAllowed(address account, bool value) external onlyOwner {
        _isAllowed[account] = value;
    }

    modifier transactionThrottler(
        address sender,
        address recipient,
        uint256 amount
    ) {
        if (_tradingStart > block.timestamp) {
            require(_isAllowed[sender] || _isAllowed[recipient], "Protection: Transfers disabled");
        } else if (_tradingStart <= block.timestamp && _restrictionEndTime > block.timestamp) {
            if (!_isAllowed[sender] || !_isAllowed[recipient]) {
                require(amount <= _maxTransferAmount, "Protection: Limit exceeded");
            }

            if (!_isWhitelisted[sender] && !_isWhitelisted[recipient]) {
                require(
                    _previousTx[sender] + 60 <= block.timestamp && _previousTx[recipient] + 60 <= block.timestamp,
                    "Protection: 1 tx/min allowed"
                );
                _previousTx[sender] = block.timestamp;
                _previousTx[recipient] = block.timestamp;
            } else if (!_isWhitelisted[recipient]) {
                require(_previousTx[recipient] + 60 <= block.timestamp, "Protection: 1 tx/min allowed");
                _previousTx[recipient] = block.timestamp;
            } else if (!_isWhitelisted[sender]) {
                require(_previousTx[sender] + 60 <= block.timestamp, "Protection: 1 tx/min allowed");
                _previousTx[sender] = block.timestamp;
            }
        }
        _;
    }
}

