// File DuelEvent.sol

pragma solidity ^0.6.12;

contract DuelEvent is Ownable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many tokens the user has provided.
        uint256 rewardDebt; // Reward debt.
        uint256 pendingReward; // Pending rewards.
        bool claimed;
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 inputToken; // Address of token contract for input
        uint256 supply; // Token supply for this pool
        uint256 allocPoint; // How many allocation points assigned to this pool. Tokens to distribute per second.
        uint256 lastRewardTime; // Last time number that tokens distribution occurs.
        uint256 accTokenPerShare; // Accumulated tokens per share, times 1e12. See below.
        uint256 totalRewards; // Total rewards for the pool
    }

    // Basis point base to calculate fees
    uint256 public constant FEE_BASE = 10000;

    // 10% claming fee (only from the reward), can be adjusted by the owner (fee 1000 = 10%)
    uint256 public claimingFee = 1000;

    // 5% early withdrawal fee, can be adjusted by the owner (fee 500 = 5%)
    // disabled if winner pool has been selected
    uint256 public withdrawalFee = 500;

    // Address where all fees goes, can be adjusted by the owner
    address public feeRecipient;

    // Reward token instance
    IERC20 public immutable rewardToken;
    DuelToken public immutable mintableRewardToken;

    // Reward token per second, can be adjusted by the owner
    uint256 public tokenPerSecond = 4e15;

    // The time number when rewards starts
    uint256 public startTime;

    // The time number until deposits are possible
    uint256 public depositEndTime;

    // The time number when rewards ends
    uint256 public endTime;

    // For reward token
    bool public mintable;

    // Event Result status enum
    enum EventResultStatus {
        Active,
        Win,
        Draw,
        Cancel
    }

    // Event result struct
    struct EventResult {
        uint256 winnerPid;
        EventResultStatus status;
    }

    // Event result to check the status of the event
    EventResult public eventResult;

    // Pools array
    PoolInfo[] public poolInfo;

    // Users mapping, poolId => userAddress => UserInfo
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;

    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event Claim(address indexed user, uint256 indexed pid, uint256 amount);
    event Exit(address indexed user, uint256 indexed pid);
    event LeftoverClaim(address indexed user, uint256 amount);

    constructor(
        IERC20 _rewardToken,
        DuelToken _mintableRewardToken,
        address _feeAddress,
        uint256 _startTime,
        uint256 _endTime,
        uint256 _depositEndTime,
        uint256 _tokenPerSecond,
        IERC20 _inputToken,
        uint256[] memory _allocationPoints,
        bool _mintable
    ) public {
        rewardToken = _rewardToken;
        mintableRewardToken = _mintableRewardToken;
        feeRecipient = _feeAddress;
        startTime = _startTime;
        depositEndTime = _depositEndTime;
        endTime = _endTime;
        mintable = _mintable;
        if(_tokenPerSecond>0) tokenPerSecond = _tokenPerSecond;

        for (uint256 i = 0; i < _allocationPoints.length; i++) {
            addPool(_inputToken, _allocationPoints[i]);
        }

        eventResult.status = EventResultStatus.Active;
    }

    /**
     * @dev Updates reward variables for all pools. Be careful of gas spending!
     */
    function massUpdatePools() public {
        for (uint256 pid = 0; pid < poolInfo.length; pid++) {
            updatePool(pid);
        }
    }

    /**
     * @dev Updates reward vairables for the pool.
     */
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];

        if (block.timestamp <= pool.lastRewardTime || pool.lastRewardTime >= endTime) {
            return;
        }

        uint256 inputSupply = pool.supply;

        if (inputSupply <= 0) {
            pool.lastRewardTime = block.timestamp;
            return;
        }

        uint256 toTime = block.timestamp > endTime ? endTime : block.timestamp;

        uint256 multiplier = getMultiplier(pool.lastRewardTime, toTime);
        uint256 reward = multiplier.mul(tokenPerSecond).mul(pool.allocPoint).div(totalAllocPoint);

        pool.accTokenPerShare = pool.accTokenPerShare.add(reward.mul(1e12).div(inputSupply));
        pool.lastRewardTime = toTime;
        pool.totalRewards = pool.totalRewards.add(reward);

        if(mintable)
        {
            mintableRewardToken.mint(address(this), reward);
        }
    }

    /**
     * @dev Deposit tokens to the pool for reward token allocation.
     */
    function deposit(uint256 _pid, uint256 _amount) external nonReentrant {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(block.timestamp < depositEndTime, "DuelEvent: Deposit deadline");
        require(_amount > 0, "DuelEvent: Zero amount");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        updatePool(_pid);

        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accTokenPerShare).div(1e12).sub(user.rewardDebt);
            user.pendingReward = user.pendingReward.add(pending);
        }

        user.amount = user.amount.add(_amount);
        user.rewardDebt = user.amount.mul(pool.accTokenPerShare).div(1e12);

        pool.supply = pool.supply.add(_amount);

        pool.inputToken.safeTransferFrom(address(msg.sender), address(this), _amount);

        emit Deposit(msg.sender, _pid, _amount);
    }

    /**
     * @dev Withdraw Input tokens from the pool.
     */
    function withdraw(uint256 _pid, uint256 _amount) external nonReentrant {
        require(_amount > 0, "DuelEvent: Zero amount");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        require(user.amount >= _amount, "DuelEvent: Withdrawal amount exceed balance");

        updatePool(_pid);

        uint256 pending = user.amount.mul(pool.accTokenPerShare).div(1e12).sub(user.rewardDebt);
        user.pendingReward = user.pendingReward.add(pending);

        uint256 fee = getWithdrawalFee(_pid);

        if(block.timestamp > endTime || eventResult.status != EventResultStatus.Active) {
            fee = 0;
        }

        uint256 amount = applyFee(fee, _amount);
        uint256 feeAmount = calculateFee(fee, _amount);

        user.amount = user.amount.sub(_amount);
        user.rewardDebt = user.amount.mul(pool.accTokenPerShare).div(1e12);

        pool.supply = pool.supply.sub(_amount);

        pool.inputToken.safeTransfer(address(msg.sender), amount);
        if (feeAmount > 0) {
            pool.inputToken.safeTransfer(address(feeRecipient), feeAmount);
        }

        emit Withdraw(msg.sender, _pid, amount);
    }

    /**
     * @dev Withdraw without caring about rewards. EMERGENCY ONLY.
     */
    function emergencyWithdraw(uint256 _pid) external {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        pool.supply = pool.supply.sub(user.amount);

        uint256 amount = applyFee(withdrawalFee, user.amount);
        uint256 feeAmount = calculateFee(withdrawalFee, user.amount);

        user.amount = 0;
        user.rewardDebt = 0;
        user.pendingReward = 0;

        pool.inputToken.safeTransfer(address(msg.sender), amount);
        pool.inputToken.safeTransfer(address(feeRecipient), feeAmount);

        emit EmergencyWithdraw(msg.sender, _pid, amount);
    }

    /**
     * @dev Exit pool and claim rewards
     */
    function exit(uint256 _pid) external nonReentrant {
        require(eventResult.status != EventResultStatus.Active, "DuelEvent: Event has not been concluded");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        require(!user.claimed, "DuelEvent: Claimed already");

        updatePool(_pid);

        uint256 userReward = pendingRewards(_pid, msg.sender);

        if (user.amount > 0) {

            uint256 amount = user.amount;

            pool.supply = pool.supply.sub(user.amount);
            user.amount = 0;

            pool.inputToken.safeTransfer(address(msg.sender), amount);

            //if (feeAmount > 0) {
            //    pool.inputToken.safeTransfer(address(feeRecipient), feeAmount);
            //}

            emit Withdraw(msg.sender, _pid, user.amount);
        }

        user.claimed = true;

        if (userReward > 0) {
            uint256 reward = applyFee(claimingFee,userReward);
            uint256 feeAmount = calculateFee(claimingFee,userReward);

            //pool.inputToken.safeTransfer(address(msg.sender), reward);
            //pool.inputToken.safeTransfer(address(feeRecipient), feeAmount);

            safeTokenTransfer(msg.sender, reward);
            safeTokenTransfer(address(feeRecipient), feeAmount);

            emit Claim(msg.sender, _pid, userReward);
        }

        emit Exit(msg.sender, _pid);
    }

    /**
     * @dev Returns reward multiplier over the given _from to _to time.
     */
    function getMultiplier(uint256 _from, uint256 _to) public pure returns (uint256) {
        return _to.sub(_from);
    }

    /**
     * @dev Returns pending rewards for user.
     */
    function pendingRewards(uint256 _pid, address _user) public view returns (uint256) {
        PoolInfo memory pool = poolInfo[_pid];
        UserInfo memory user = userInfo[_pid][_user];

        if (block.timestamp < startTime || user.claimed) {
            return 0;
        }

        uint256 accTokenPerShare = pool.accTokenPerShare;
        uint256 inputSupply = pool.supply;
        uint256 userReward = user.pendingReward;

        if (block.timestamp > pool.lastRewardTime && inputSupply != 0) {
            uint256 toTime = block.timestamp > endTime ? endTime : block.timestamp;
            uint256 multiplier = getMultiplier(pool.lastRewardTime, toTime);
            uint256 reward = multiplier.mul(tokenPerSecond).mul(pool.allocPoint).div(totalAllocPoint);
            accTokenPerShare = accTokenPerShare.add(reward.mul(1e12).div(inputSupply));
        }

        userReward = userReward.add(user.amount.mul(accTokenPerShare).div(1e12).sub(user.rewardDebt));

        if (eventResult.status == EventResultStatus.Win && eventResult.winnerPid == _pid && pool.totalRewards>0) {
            uint256 userShare = userReward.mul(1e12).div(pool.totalRewards);
            for (uint256 pid = 0; pid < poolInfo.length; pid++) {
                if (pid != eventResult.winnerPid) {
                    PoolInfo memory _pool = poolInfo[pid];
                    userReward = userReward.add(userShare.mul(_pool.totalRewards).div(1e12));
                }
            }
        } else if (
            (eventResult.status == EventResultStatus.Win && eventResult.winnerPid != _pid) ||
            eventResult.status == EventResultStatus.Cancel
        ) {
            userReward = 0;
        }

        return userReward;
    }

    /**
     * @dev Returns the current timestamp.
     */
    function getTimestamp() external view returns (uint256) {
        return block.timestamp;
    }

    /**
     * @dev Returns the poolInfo length.
     */
    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function getEventStatus() external view returns (uint256) {
        if(eventResult.status == EventResultStatus.Active) {
            return 1;
        } else if(eventResult.status == EventResultStatus.Win) {
            return 2;
        } else if(eventResult.status == EventResultStatus.Draw) {
            return 3;
        } else if(eventResult.status == EventResultStatus.Cancel) {
            return 4;
        } else {
            return 0;
        }
    }

    function getEventWinnerPID() external view returns (uint256) {
        return eventResult.winnerPid;
    }

    /**
     * @dev Returns the poolInfo supply.
     */
    function poolSupply(uint256 _pid) external view returns (uint256) {
        return poolInfo[_pid].supply;
    }

    /**
     * @dev Returns the user deposit.
     */
    function userDeposit(uint256 _pid, address _user) external view returns (uint256) {
        return userInfo[_pid][_user].amount;
    }

    /**
     * @dev Returns the userInfo claimed.
     */
    function isClaimed(uint256 _pid, address _user) external view returns (bool) {
        return userInfo[_pid][_user].claimed;
    }

    /**
     * @dev Returns base reward per second.
     */
    function getBaseRewardPerSecond() external view returns (uint256) {
        return tokenPerSecond;
    }

    /**
     * @dev Returns the pool specific reward per time.
     */
    function getPoolRewardPerSecond(uint256 _pid) external view returns (uint256) {
        return tokenPerSecond.mul(poolInfo[_pid].allocPoint).div(totalAllocPoint);
    }

    /**
     * @dev Sets event status to EventResultStatus.Win and selects winner pool. Only owner.
     */
    function setWin(uint256 _pid) public onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(block.timestamp >= depositEndTime, "DuelEvent: Deposit deadline not reached");
        massUpdatePools();
        eventResult.status = EventResultStatus.Win;
        eventResult.winnerPid = _pid;
    }

    /**
     * @dev Sets event status to EventResultStatus.Draw. Only owner.
     */
    function setDraw() public onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(block.timestamp >= depositEndTime, "DuelEvent: Deposit deadline not reached");
        massUpdatePools();
        eventResult.status = EventResultStatus.Draw;
    }

    /**
     * @dev Sets event status to EventResultStatus.Cancel. Only owner.
     */
    function setCancel() public onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(block.timestamp >= depositEndTime, "DuelEvent: Deposit deadline not reached");
        massUpdatePools();
        eventResult.status = EventResultStatus.Cancel;
    }

    /**
     * @dev Updates pool allocation points, only owner.
     */
    function setPool(
        uint256 _pid,
        uint256 _alloc,
        bool _withUpdate
    ) public onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_alloc);
        poolInfo[_pid].allocPoint = _alloc;
    }

    /**
     * @dev Sends leftover tokens to the fee recipient, only owner.
     */
    function claimLeftovers() external onlyOwner {
        require(eventResult.status != EventResultStatus.Active, "DuelEvent: Event has not been concluded");
        require(poolInfo[0].inputToken.balanceOf(address(this)) == 0, "DuelEvent: Not all users has claimed");

        uint256 balance = rewardToken.balanceOf(address(this));
        if(mintable)
        {
            balance = mintableRewardToken.balanceOf(address(this));
        }
        require(balance > 0, "DuelEvent: Zero balance");

        safeTokenTransfer(feeRecipient, balance);
        emit LeftoverClaim(feeRecipient, balance);
    }

    /**
     * @dev Sends tokens to the fee recipient, only owner.
     */
    function claimTokensEmergency() external onlyOwner {

        uint256 balance = rewardToken.balanceOf(address(this));
        if(mintable)
        {
            balance = mintableRewardToken.balanceOf(address(this));
        }

        require(balance > 0, "DuelEvent: Zero balance");

        safeTokenTransfer(msg.sender, balance);
    }

    /**
     * @dev Updates claming fee, max is 10%, only owner.
     */
    function setClaimingFee(uint256 _fee) external onlyOwner {
        require(_fee <= 2000, "DuelEvent: Max claming fee");
        claimingFee = _fee;
    }

    /**
     * @dev Updates withdrawal fee, max is 10%, only owner.
     */
    function setWithdrawalFee(uint256 _fee) external onlyOwner {
        require(_fee <= 1000, "DuelEvent: Max withdrawal fee");
        withdrawalFee = _fee;
    }

    /**
     * @dev Updates fee recipient, only owner.
     */
    function setFeeRecipient(address _feeRecipient) external onlyOwner {
        require(_feeRecipient != address(0), "DuelEvent: Zero fee recipient");
        feeRecipient = _feeRecipient;
    }

    /**
     * @dev Updates reward per time, only owner.
     */
    function setTokenPerSecond(uint256 _amount) external onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(_amount <= 30 * 1e18, "DuelEvent: Max per second");
        require(_amount >= 1 * 1e13, "DuelEvent: Min per second");
        tokenPerSecond = _amount;
    }

    /**
     * @dev Updates start time, only owner.
     */
    function setStartTime(uint256 _time) external onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(startTime > block.timestamp, "DuelEvent: Event has been started");
        require(_time < endTime, "DuelEvent: Start time should be less then end time");

        startTime = _time;

        for (uint256 pid = 0; pid < poolInfo.length; pid++) {
            poolInfo[pid].lastRewardTime = startTime;
        }
    }

    /**
     * @dev Updates deposit end time, only owner.
     */
    function setDepositEndTime(uint256 _time) external onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(_time > startTime && _time < endTime, "DuelEvent: Wrong deposit end time");

        depositEndTime = _time;
    }

    /**
     * @dev Updates end time, only owner.
     */
    function setEndTime(uint256 _time) external onlyOwner {
        require(eventResult.status == EventResultStatus.Active, "DuelEvent: Event has been concluded");
        require(endTime > block.timestamp, "DuelEvent: Event has been finished");
        require(_time > startTime, "DuelEvent: End time should be greater then start time");

        endTime = _time;
    }

    /**
     * @dev Adds a new pool, internal.
     */
    function addPool(IERC20 _input, uint256 _alloc) internal {
        totalAllocPoint = totalAllocPoint.add(_alloc);
        poolInfo.push(
            PoolInfo({
                inputToken: _input,
                supply: 0,
                allocPoint: _alloc,
                lastRewardTime: block.timestamp > startTime ? block.timestamp : startTime,
                accTokenPerShare: 0,
                totalRewards: 0
            })
        );
    }

    /**
     * @dev Safe token transfer function, just in case if rounding error
     * causes pool to not have enough token balance.
     */
    function safeTokenTransfer(address _to, uint256 _amount) internal {
        uint256 balance = rewardToken.balanceOf(address(this));
        if(mintable)
        {
            balance = mintableRewardToken.balanceOf(address(this));
        }
        uint256 amount = _amount > balance ? balance : _amount;

        if(mintable)
        {
            require(mintableRewardToken.transfer(_to, amount), "DuelEvent: safeTokenTransfer error");
        }
        else
        {
            require(rewardToken.transfer(_to, amount), "DuelEvent: safeTokenTransfer error");
        }

    }

    /**
     * @dev it calculates (1 - fee) * amount
     * Applies the fee by subtracting fees from the amount and returns
     * the amount after deducting the fee.
     */
    function applyFee(uint256 _feeInBips, uint256 _amount) internal pure returns (uint256) {
        return _amount.mul(FEE_BASE.sub(_feeInBips)).div(FEE_BASE);
    }

    /**
     * @dev it calculates fee * amount
     * Calculates the fee amount.
     */
    function calculateFee(uint256 _feeInBips, uint256 _amount) internal pure returns (uint256) {
        return _amount.mul(_feeInBips).div(FEE_BASE);
    }

    /**
     * @dev Get withdrawal fee in basis points for the user of the given pool.
     */
    function getWithdrawalFee(uint256 _pid) internal view returns (uint256) {
        if (eventResult.status != EventResultStatus.Active) {
            if (
                (eventResult.status == EventResultStatus.Win && _pid == eventResult.winnerPid) ||
                eventResult.status == EventResultStatus.Draw
            ) {
                return claimingFee;
            } else {
                return 0;
            }
        } else {
            return withdrawalFee;
        }
    }
}
