import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@chainlink/contracts/src/v0.8/AutomationCompatible.sol";

interface IAddressRouter {
    function getAddress() external view returns (address);
}
struct UserCheckpoint {
    uint roundId;
    uint claimIndex;
    uint totalStaked;
    bool hasStakes;
}
struct Checkpoint {
    uint roundId;
    uint totalStaked;
    uint totalRewards;
    uint startTime;
    uint unlockTime;
}
struct AccountData {
    uint currentStakes;
    uint claimableAmount;
    uint lastClaimRoundId;
    uint stakeRoundId;
    uint currentRoundId;
    bool claimedAllRewards;
}
struct PendingStakes {
    uint stakes;
    uint rewards;
}
struct LeftOver {
    uint rewards;
    bool exist;
}
uint constant CLAIM_BULK_AMOUNT = 360;

contract BrldEarn is AutomationCompatibleInterface, Ownable {
    event Stake(
        address indexed account,
        uint roundId,
        uint stakeRoundId,
        uint amount
    );
    event Unstake(address indexed account, uint roundId, uint amount);
    event ClaimRewards(
        address indexed account,
        uint roundId,
        uint rewardAmount
    );
    event StartRound(
        uint indexed roundId,
        uint totalStaked,
        uint totalRewards,
        uint startTime,
        uint endTime
    );
    event ReceivedCommissions(
        address indexed from,
        uint indexed roundId,
        uint amount
    );
    PendingStakes public pendingStakes;
    Checkpoint[] public globalCheckpoints;
    mapping(address => UserCheckpoint) userCheckpoints;
    LeftOver public leftOvers;
    uint public ROUND_INTERVAL = 86400;
    uint public BRINGOLD_FEE = 4 ether;
    IAddressRouter public bringoldWalletAddressRouter;
    IERC20 BRIN;
    IERC20 BRLD;
    using Counters for Counters.Counter;
    Counters.Counter private roundId;

    constructor(
        address _BRIN,
        address _BRLD,
        address _bringoldWalletAddressRouter
    ) {
        BRIN = IERC20(_BRIN);
        BRLD = IERC20(_BRLD);
        bringoldWalletAddressRouter = IAddressRouter(
            _bringoldWalletAddressRouter
        );
        globalCheckpoints.push(
            Checkpoint({
                roundId: roundId.current(),
                totalStaked: 0,
                totalRewards: 0,
                startTime: 0,
                unlockTime: 0
            })
        );
        roundId.increment();
        globalCheckpoints.push(
            Checkpoint({
                roundId: roundId.current(),
                totalStaked: 0,
                totalRewards: 0,
                startTime: block.timestamp,
                unlockTime: 1693569600
            })
        );
    }

    function setBringoldFee(uint fee) external onlyOwner {
        require(fee > 0 && fee < 50 ether, "invalid fee amount");
        BRINGOLD_FEE = fee;
    }

    function setRoundInterval(uint interval) external onlyOwner {
        require(interval > 0, "invalid interval");
        ROUND_INTERVAL = interval;
    }

    function _stake(uint amount) internal {
        UserCheckpoint memory userLastCheckpoint;
        uint lastStakes;
        if (userCheckpoints[msg.sender].roundId != 0) {
            userLastCheckpoint = userCheckpoints[msg.sender];
            require(_getClaimableAmount() == 0, "must claim rewards");
            lastStakes = userLastCheckpoint.totalStaked;
        }

        BRLD.transferFrom(msg.sender, address(this), amount);
        BRIN.transferFrom(msg.sender, address(this), BRINGOLD_FEE);
        BRIN.transfer(
            bringoldWalletAddressRouter.getAddress(),
            (BRINGOLD_FEE * 90) / 100
        );
        pendingStakes.stakes += amount;
        pendingStakes.rewards += (BRINGOLD_FEE * 10) / 100;

        userCheckpoints[msg.sender] = UserCheckpoint(
            roundId.current() + 1,
            roundId.current(),
            lastStakes + amount,
            true
        );
        emit Stake(
            msg.sender,
            roundId.current(),
            roundId.current() + 1,
            amount
        );
    }

    /**
        @dev Stakes amount of brings
        @param amount Amount to be staked
    */
    function stake(uint amount) external {
        require(amount > 0, "Amount must be gt zero");

        _stake(amount);
    }

    function _unStake() internal {
        BRLD.transfer(msg.sender, userCheckpoints[msg.sender].totalStaked);
    }

    /**
        @dev Unstakes and claims rewards
    */
    function unStake() external {
        UserCheckpoint storage userLastCheckpoint = userCheckpoints[msg.sender];
        require(
            userLastCheckpoint.hasStakes == true,
            "You have zero staked tokens"
        );
        require(_getClaimableAmount() == 0, "must claim rewards");
        BRIN.transferFrom(msg.sender, address(this), BRINGOLD_FEE);
        BRIN.transfer(
            bringoldWalletAddressRouter.getAddress(),
            (BRINGOLD_FEE * 90) / 100
        );
        pendingStakes.rewards += (BRINGOLD_FEE) / 10;
        _unStake();

        globalCheckpoints[roundId.current()].totalStaked -= userLastCheckpoint
            .totalStaked;
        userLastCheckpoint.totalStaked = 0;
        userLastCheckpoint.hasStakes = false;
        emit Unstake(
            msg.sender,
            roundId.current(),
            userLastCheckpoint.totalStaked
        );
    }

    function _claimRewards() internal {
        uint rewards = 0;
        UserCheckpoint storage userLastCheckpoint = userCheckpoints[msg.sender];

        require(
            roundId.current() > userLastCheckpoint.claimIndex,
            "cannot claim"
        );

        uint startIndex = userLastCheckpoint.claimIndex + 1;

        uint endIndex;
        if (roundId.current() < CLAIM_BULK_AMOUNT + startIndex) {
            endIndex = roundId.current();
        } else {
            endIndex = startIndex + CLAIM_BULK_AMOUNT;
        }
        for (uint i = startIndex; i < endIndex; i++) {
            Checkpoint storage globalCheckpoint = globalCheckpoints[i];

            uint userStakeAtCheckpoint = userLastCheckpoint.totalStaked;
            uint totalStakeAtCheckpoint = globalCheckpoint.totalStaked;
            uint totalRewardsAtCheckpoint = globalCheckpoint.totalRewards;
            if (totalStakeAtCheckpoint > 0) {
                rewards +=
                    (userStakeAtCheckpoint * totalRewardsAtCheckpoint) /
                    totalStakeAtCheckpoint;
            }
        }

        userLastCheckpoint.claimIndex = endIndex - 1;
        BRIN.transferFrom(msg.sender, address(this), BRINGOLD_FEE);
        BRIN.transfer(
            bringoldWalletAddressRouter.getAddress(),
            (BRINGOLD_FEE * 90) / 100
        );
        pendingStakes.rewards += (BRINGOLD_FEE) / 10;
        BRIN.transfer(msg.sender, rewards);

        emit ClaimRewards(msg.sender, roundId.current(), rewards);
    }

    /**
        @dev claims available rewards
    */
    function claimRewards() external {
        _claimRewards();
    }

    function _startNextRound() internal {
        Checkpoint memory currentCheckpoint = globalCheckpoints[
            roundId.current()
        ];
        require(
            currentCheckpoint.unlockTime <= block.timestamp,
            "cannot start round"
        );

        roundId.increment();
        globalCheckpoints.push(
            Checkpoint(
                roundId.current(),
                currentCheckpoint.totalStaked + pendingStakes.stakes,
                pendingStakes.rewards,
                block.timestamp,
                block.timestamp + ROUND_INTERVAL
            )
        );
        Checkpoint memory currentGlobalCheckpoint = globalCheckpoints[
            roundId.current()
        ];
        if (currentGlobalCheckpoint.totalStaked != 0 && leftOvers.exist) {
            globalCheckpoints[roundId.current()].totalRewards += leftOvers
                .rewards;
            leftOvers.exist = false;
            leftOvers.rewards = 0;
        }
        pendingStakes.stakes = 0;
        pendingStakes.rewards = 0;
        emit StartRound(
            roundId.current(),
            currentGlobalCheckpoint.totalStaked,
            currentGlobalCheckpoint.totalRewards,
            block.timestamp,
            block.timestamp + ROUND_INTERVAL
        );
    }

    function startNextRound() external {
        _startNextRound();
    }

    function receiveTokens(uint amount) external {
        BRIN.transferFrom(msg.sender, address(this), amount);
        if (globalCheckpoints[roundId.current()].totalStaked != 0) {
            globalCheckpoints[roundId.current()].totalRewards += amount;
        } else {
            leftOvers = LeftOver(leftOvers.rewards + amount, true);
        }
        emit ReceivedCommissions(msg.sender, roundId.current(), amount);
    }

    function _getClaimableAmount() internal view returns (uint) {
        uint rewards = 0;
        UserCheckpoint memory userLastCheckpoint = userCheckpoints[msg.sender];
        uint startIndex = userLastCheckpoint.claimIndex + 1;
        for (uint i = startIndex; i < roundId.current(); i++) {
            Checkpoint memory globalCheckpoint = globalCheckpoints[i];

            uint userStakeAtCheckpoint = userLastCheckpoint.totalStaked;
            uint totalStakeAtCheckpoint = globalCheckpoint.totalStaked;
            uint totalRewardsAtCheckpoint = globalCheckpoint.totalRewards;
            if (totalStakeAtCheckpoint > 0) {
                rewards +=
                    (userStakeAtCheckpoint * totalRewardsAtCheckpoint) /
                    totalStakeAtCheckpoint;
            }
        }
        if (rewards != 0) {
            return rewards;
        }
        return 0;
    }

    function getClaimableAmount() external view returns (uint) {
        return _getClaimableAmount();
    }

    function getCurrentRound() external view returns (Checkpoint memory) {
        return globalCheckpoints[roundId.current()];
    }

    function getLastRound() external view returns (Checkpoint memory) {
        return globalCheckpoints[roundId.current() - 1];
    }

    function getCurrentStakeOfAccount() external view returns (uint) {
        return userCheckpoints[msg.sender].totalStaked;
    }

    /**
        @dev Returns current stakes and amount that can be claimed
    */
    function getAccountData() external view returns (AccountData memory) {
        if (userCheckpoints[msg.sender].claimIndex != 0) {
            return
                AccountData(
                    userCheckpoints[msg.sender].totalStaked,
                    _getClaimableAmount(),
                    userCheckpoints[msg.sender].claimIndex,
                    userCheckpoints[msg.sender].roundId,
                    roundId.current(),
                    _getClaimableAmount() == 0
                );
        }
        return AccountData(0, 0, 0, 0, 0, false);
    }

    /**
        @dev Returns total amount of commissions in last 24 hours to be distributed between brings holders
    */
    function getTotalCommissionOfLastRound() external view returns (uint) {
        return globalCheckpoints[roundId.current() - 1].totalRewards;
    }

    //Chainlink Keeper
    function checkUpkeep(
        bytes calldata
    ) external view override returns (bool upkeepNeeded, bytes memory) {
        upkeepNeeded =
            globalCheckpoints[roundId.current()].unlockTime <= block.timestamp;
    }

    function performUpkeep(bytes calldata) external override {
        _startNextRound();
    }
}
"
    }
  }
