    "contracts/BlastEquipmentNFT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@prb/math/contracts/PRBMathUD60x18.sol";
import "./interfaces/IBlastEquipmentNFT.sol";
import { Errors } from "./libraries/Errors.sol";

/// @title Blast Equipment NFT
/// @dev BlastNFT ERC721 token
contract BlastEquipmentNFT is
    ERC721,
    IBlastEquipmentNFT,
    ERC721URIStorage,
    ERC721Burnable,
    Pausable,
    AccessControl
{
    using Counters for Counters.Counter;
    using SafeERC20 for IERC20;
    using PRBMathUD60x18 for uint256;

    /// @dev Variable Attributes
    /// @notice These attributes would be nice to have on-chain because they affect the value of NFT and they are persistent when NFT changes hands.
    struct VariableAttributes {
        uint256 level;
        uint256 durabilityRestored;
        uint256 durability;
        uint256 lastRepairTime;
        uint256 repairCount;
        uint256 replicationCount;
    }

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant GAME_ROLE = keccak256("GAME_ROLE");
    bytes32 public constant REVEAL_ROLE = keccak256("REVEAL_ROLE");
    bytes32 public constant REPLICATOR_ROLE = keccak256("REPLICATOR_ROLE");

    Counters.Counter public _tokenIdCounter;
    uint256 public durabilityPointTimer = 1 weeks;
    mapping(uint256 => bytes32) public hashValue;
    mapping(uint256 => VariableAttributes) public attributes;
    mapping(uint256 => StaticAttributes) public staticAttributes;
    mapping(uint256 => uint256) private revealed;
    mapping(uint256 => string) private realTokenURI;
    string private previewURI;
    string private baseURI;

    modifier hasGameRole() {
        require(
            hasRole(GAME_ROLE, _msgSender()) ||
            hasRole(DEFAULT_ADMIN_ROLE, _msgSender()) ||
            hasRole(REPLICATOR_ROLE, _msgSender()),
            Errors.MISSING_GAME_ROLE
        );
        _;
    }

    /// @dev Grants `DEFAULT_ADMIN_ROLE`, `MINTER_ROLE` and `PAUSER_ROLE` to the
    /// @param name Name of the contract
    /// @param symbol Symbol of the contract
    constructor(string memory name, string memory symbol, string memory _previewURI, string memory _baseURI) ERC721(name, symbol) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(MINTER_ROLE, _msgSender());
        _setupRole(GAME_ROLE, _msgSender());
        _setupRole(REVEAL_ROLE, _msgSender());
        _setupRole(REPLICATOR_ROLE, _msgSender());

        previewURI = _previewURI;
        baseURI = _baseURI;
    }

    /// @notice Creates a new token for `to`. Its token ID will be automatically
    /// @dev The caller must have the `MINTER_ROLE`.
    function safeMint(
        address _to,
        bytes32[] calldata _hash,
        string[] calldata _realUri,
        StaticAttributes[] calldata _staticAttributes
    ) external onlyRole(MINTER_ROLE) {
        require(_to != address(0), Errors.NO_ZERO_ADDRESS);
        require(_hash.length == _realUri.length, Errors.INVALID_PARAM);

        for (uint256 i = 0; i < _hash.length; i = i + 1) {
            _safeMint(_to, _hash[i], _realUri[i], _staticAttributes[i]);
        }
    }

    function safeMintReplicator(
        address _to,
        bytes32 _hash,
        string calldata _realUri,
        StaticAttributes memory _staticAttributes
    ) external override onlyRole(REPLICATOR_ROLE) returns (uint256) {
        require(_to != address(0), Errors.NO_ZERO_ADDRESS);

        return _safeMint(_to, _hash, _realUri, _staticAttributes);
    }

    function _safeMint(
        address _to,
        bytes32 _hash,
        string memory _realUri,
        StaticAttributes memory _staticAttributes
    ) internal returns (uint256) {
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        hashValue[tokenId] = _hash;
        realTokenURI[tokenId] = _realUri;
        attributes[tokenId] = VariableAttributes({
            level: 1,
            durabilityRestored: 0,
            durability: 0,
            lastRepairTime: block.timestamp,
            repairCount: 0,
            replicationCount: 0
        });
        staticAttributes[tokenId] = _staticAttributes;

        _mint(_to, tokenId);

        emit AttributeUpdated(tokenId, 1, 0, 0, block.timestamp, 0, 0);

        return tokenId;
    }

    function revealRealTokenURI(uint256 _tokenId)
        external
        override
        onlyRole(REVEAL_ROLE)
    {
        revealed[_tokenId] = 1;
        VariableAttributes storage _variableAttribute = attributes[_tokenId];
        _variableAttribute.lastRepairTime = block.timestamp;

        emit PermanentURI(realTokenURI[_tokenId], _tokenId);
    }

    function setRealTokenURI(uint256 _tokenId, string calldata _realUri)
        external
        override
        onlyRole(REVEAL_ROLE)
    {
        realTokenURI[_tokenId] = _realUri;
        emit PermanentURI(_realUri, _tokenId);
    }

    function setPreviewTokenURI(string calldata _previewURI)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        previewURI = _previewURI;
    }

    function setLevel(uint256 _tokenId, uint256 _newLevel)
        external
        override
        hasGameRole
    {
        StaticAttributes memory _staticAttribute = staticAttributes[_tokenId];
        require(_staticAttribute.maxLevel >= _newLevel, Errors.MAX_LEVEL_REACHED);

        VariableAttributes storage _attribute = attributes[_tokenId];
        _attribute.level = _newLevel;
        uint256 _durabilityPoint = getDurabilityPoints(_attribute, _tokenId);
        emit AttributeUpdated(
            _tokenId,
            _newLevel,
            _attribute.durabilityRestored,
            _durabilityPoint,
            _attribute.lastRepairTime,
            _attribute.repairCount,
            _attribute.replicationCount
        );
    }

    function setRepairCount(uint256 _tokenId, uint256 _newRepairCount)
        external
        override
        hasGameRole
    {
        VariableAttributes storage _attribute = attributes[_tokenId];

        _attribute.repairCount = _newRepairCount;
        _attribute.durabilityRestored += getDurabilityPoints(_attribute, _tokenId);
        _attribute.lastRepairTime = block.timestamp;

        uint256 _durabilityPoint = getDurabilityPoints(_attribute, _tokenId);
        emit AttributeUpdated(
            _tokenId,
            _attribute.level,
            _attribute.durabilityRestored,
            _durabilityPoint,
            _attribute.lastRepairTime,
            _newRepairCount,
            _attribute.replicationCount
        );
    }

    function setReplicationCount(uint256 _tokenId, uint256 _newReplicationCount)
        external
        override
        hasGameRole
    {
        StaticAttributes memory _staticAttribute = staticAttributes[_tokenId];
        require(_staticAttribute.maxReplication >= _newReplicationCount, Errors.MAX_REPLICATION_COUNT_REACHED);

        VariableAttributes storage _attribute = attributes[_tokenId];
        _attribute.replicationCount = _newReplicationCount;
        uint256 _durabilityPoint = getDurabilityPoints(_attribute, _tokenId);
        emit AttributeUpdated(
            _tokenId,
            _attribute.level,
            _attribute.durabilityRestored,
            _durabilityPoint,
            _attribute.lastRepairTime,
            _attribute.repairCount,
            _newReplicationCount
        );
    }

    function scrap(uint256 _tokenId) external override hasGameRole {
        _burn(_tokenId);
        delete attributes[_tokenId];
        delete staticAttributes[_tokenId];
        delete hashValue[_tokenId];
        delete realTokenURI[_tokenId];
    }

    function getAttributes(uint256 _tokenId)
        external
        view
        override
        returns (
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        VariableAttributes memory _attribute = attributes[_tokenId];
        uint256 _durabilityPoint = getDurabilityPoints(_attribute, _tokenId);
        return (
            _attribute.level,
            _attribute.durabilityRestored,
            _durabilityPoint,
            _attribute.lastRepairTime,
            _attribute.repairCount,
            _attribute.replicationCount
        );
    }

    function getStaticAttributes(uint256 _tokenId)
        external
        view
        override
        returns (
            uint8,
            uint8,
            uint8,
            uint8,
            uint8,
            uint8
        )
    {
        StaticAttributes memory _attribute = staticAttributes[_tokenId];
        return (
            _attribute.maxLevel,
            _attribute.maxDurability,
            _attribute.maxReplication,
            _attribute.adjective,
            _attribute.rarity,
            _attribute.grade
        );
    }

    function getDurabilityPoints(VariableAttributes memory _attribute, uint256 _tokenId) internal view returns (uint256) {
        StaticAttributes memory _staticAttribute = staticAttributes[_tokenId];
        uint256 _durabilityPoint = (block.timestamp - _attribute.lastRepairTime) / durabilityPointTimer;
        return (_durabilityPoint >= _staticAttribute.maxDurability ? _staticAttribute.maxDurability : _durabilityPoint);
    }

    function setStaticAttributes(uint256 _tokenId, StaticAttributes calldata _staticAttributes) external onlyRole(DEFAULT_ADMIN_ROLE) {
        staticAttributes[_tokenId] = _staticAttributes;
    }

    /// @notice Set the DurabilityPoint Timer
    /// @dev The caller must have the `DEFAULT_ADMIN_ROLE`.
    function setDurabilityPointTimer(uint256 _newTimer) public onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_newTimer > 0, Errors.INVALID_PARAM);
        durabilityPointTimer = _newTimer;
    }

    /// @notice Set BaseURI for token metadata URI
    /// @dev The caller must have the `DEFAULT_ADMIN_ROLE`.
    function setBaseURI(string memory _baseURI) public onlyRole(DEFAULT_ADMIN_ROLE) {
        baseURI = _baseURI;
    }

    // @notice Pauses/Unpauses the contract
    // @dev While paused, actions are not allowed
    // @param stop whether to pause or unpause the contract.
    function pause(bool stop) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (stop) {
            _pause();
        } else {
            _unpause();
        }
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override whenNotPaused {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    /// @notice Unpauses all token transfers.
    /// @dev The caller must be the Owner (or have approval) of the Token.
    /// @param tokenId Token ID.
    function _burn(uint256 tokenId)
        internal
        override(ERC721, ERC721URIStorage)
    {
        require(_isApprovedOrOwner(_msgSender(), tokenId), Errors.NOT_OWNER);
        super._burn(tokenId);
    }

    /// @notice Returns the TokenURI.
    /// @param tokenId Token ID.
    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        _requireMinted(tokenId);

        if (revealed[tokenId] == 0) {
            return previewURI;
        }
        return string(abi.encodePacked(baseURI, realTokenURI[tokenId]));
    }

    /// @dev See {IERC165-supportsInterface}.
    /// @param interfaceId Interface ID.
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(AccessControl, ERC721, IERC165)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
"
    },
    "contracts/interfaces/IBlastEquipmentNFT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

struct StaticAttributes {
    uint8 maxLevel;
    uint8 maxDurability;
    uint8 maxReplication;
    uint8 adjective;
    uint8 rarity;
    uint8 grade;
}

/**
 * @dev required interface of an Equipment NFT.
 */
interface IBlastEquipmentNFT is IERC721 {
    /// @notice Event Attribute Updated
    event AttributeUpdated(
        uint256 tokenId,
        uint256 level,
        uint256 durabilityRestored,
        uint256 durability,
        uint256 lastRepairTime,
        uint256 repairCount,
        uint256 replicationCount
    );

    /// @notice Event Revealed TokenURI
    event PermanentURI(string _value, uint256 indexed _id);

    function safeMintReplicator(address _to, bytes32 _hash, string calldata _realUri, StaticAttributes calldata _staticAttribute) external returns (uint);

    function revealRealTokenURI(uint _tokenId) external;

    function setRealTokenURI(uint _tokenId, string calldata _realUri) external;

    function setLevel(uint256 _tokenId, uint256 _newLevel) external;

    function setRepairCount(uint256 _tokenId, uint256 _newRepairCount) external;

    function setReplicationCount(uint256 _tokenId, uint256 _newReplicationCount)
        external;

    function scrap(uint256 _tokenId) external;

    function getAttributes(uint256 _tokenId) external view returns (uint256, uint256, uint256, uint256, uint256, uint256);

    function getStaticAttributes(uint256 _tokenId) external view returns (uint8, uint8, uint8, uint8, uint8, uint8);
}
"
    },
    "contracts/libraries/Errors.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

/**
 * @title Errors library
 * @author JensonCollins
 * @notice Defines the error messages emitted by the different contracts of the Aave protocol
 * @dev Error messages prefix glossary:
 *  - VL = ValidationLogic
 *  - MATH = Math libraries
 *  - CT = Common errors between tokens (AToken, VariableDebtToken and StableDebtToken)
 *  - AT = AToken
 *  - SDT = StableDebtToken
 *  - VDT = VariableDebtToken
 *  - LP = LendingPool
 *  - LPAPR = LendingPoolAddressesProviderRegistry
 *  - LPC = LendingPoolConfiguration
 *  - RL = ReserveLogic
 *  - LPCM = LendingPoolCollateralManager
 *  - P = Pausable
 */

library Errors {
    //common errors
    string public constant NO_ZERO_ADDRESS = "1"; // Address cannot be zero
    string public constant NO_ZERO_VALUE = "2"; // Value cannot be zero
    string public constant NOT_OWNER = "3"; // Caller is not owner
    string public constant NOT_EXIST_TOKEN_ID = "4"; // Token ID does not exist
    string public constant INVALID_PARAM = "5"; // Invalid parameter
    string public constant INVALID_AMOUNT = "6"; // In case msg.value amount is different as expected
    string public constant FAILED_TO_SEND_ETHER_TREASURY = "7"; // Failed to send ether to treasury
    string public constant FAILED_TO_SEND_ETHER_COMPANY = "8"; // Failed to send ether to company
    string public constant USING_MATIC_NOW = "9"; // Using MATIC now
    string public constant NOT_USING_MATIC_NOW = "10"; // Not using MATIC now

    // AccessControl
    string public constant MISSING_GAME_ROLE = "11"; // Caller does not have the GAME_ROLE role

    // BlastEquipmentNFT contract
    string public constant MAX_LEVEL_REACHED = "12"; // Max level reached
    string public constant MAX_REPLICATION_COUNT_REACHED = "24"; // Max replication counter reached

    // Marektplace contract
    string public constant TOKEN_NOT_WHITELISTED = "13"; // Caller is not whitelisted
    string public constant LISTING_IS_NOT_ACTIVED = "14"; // Listing is not actived
    string public constant FAILED_TO_SEND_ETHER_USER = "15"; // Failed to send ether to user

    // MarketplaceLootbox contract
    string public constant MAX_LIMIT_REACHED = "16"; // Reached max limit
    string public constant INVALID_MERKLE_PROOF = "17"; // Invalid merkle proof

    // Lootbox contract
    string public constant NOT_AVAILABLE_TO_OPEN = "18"; // Lootbox is not available to open

    // CS contract
    string public constant CS_EXPIRED_DEADLINE = "19"; // Invalid signature
    string public constant CS_INVALID_SIGNATURE = "20"; // Invalid signature

    // Replicator contract
    string public constant NOT_READY_REPLICATE = "21"; // Not ready to replicate
    string public constant NOT_READY_MORPH = "22"; // Not ready to morph
    string public constant INVALID_HEX_CHARACTER = "23"; // Not ready to morph
}
"
    }
