pragma solidity ^0.8.0;




interface ISettlement {
    function settlement(
        uint256 _random,
        address _player,
        uint256 _count,
        uint256 _requestId,
        uint64 _mainType,
        uint64 _subType
    ) external;
}

interface IGame {
    function startGame(address player, uint256 dollar, uint256 tokenId, bytes memory gameArgs)
        external
        payable
        returns (uint256);
}

contract Lottery is VRFConsumerBaseV2, Roles, IGame {
    event SetDrawArgs(uint256 price, uint64 min, uint64 max);
    event SetGasPrice(uint256[] prices);

    address public platform;
    uint64 public mainType;
    uint64 public subType;

    uint256 public ten = 0.1 ether;
    uint256 public fifty = 0.2 ether;
    uint256 public hundred = 0.3 ether;
    uint256 public aboveHundred = 0.5 ether;

    uint256 public singlePrice = 2000000; // 2 dollars
    uint64 public maxCount = 100;
    uint64 public minCount = 1;

    struct VRFRequest {
        address player;
        uint64 count;
    }

    ISettlement public gamePool;

    mapping(uint256 => VRFRequest) public vrfReq;

    // VRF
    // uint64 private subscriptionId = 539;
    // bytes32 private keyHash = 0xcc294a196eeeb44da2888d17c0625cc88d70d9760a69d58d853ba6581a9ab0cd;
    // address private coordinator = 0xAE975071Be8F8eE67addBC1A82488F1C24858067;
    VRFCoordinatorV2Interface COORDINATOR;
    uint64 public subscriptionId = 4062;
    uint32 public callbackGasLimit = 2000000;
    uint16 public requestConfirmations = 3;
    uint32 public numWords = 1;
    bytes32 public keyHash = 0x4b09e658ed251bcafeebbc69400383d49f344ace09b9576fe248bb02c003fe9f;
    // address public coordinator = 0x7a1BaC17Ccc5b313516C5E16fb24f7659aA5ebed;

    event Withdraw(address indexed recipient, uint256 amount, uint256 timestamp);

    modifier onlyPlatform() {
        require(msg.sender == platform, "Not granted");
        _;
    }

    constructor(address _platform, address _gamePool, address _coordinator, uint64 _mainType, uint64 _subType)
        VRFConsumerBaseV2(_coordinator)
        Roles(_msgSender())
    {
        COORDINATOR = VRFCoordinatorV2Interface(_coordinator); // VRF
        // coordinator = _coordinator;
        gamePool = ISettlement(_gamePool);
        mainType = _mainType;
        subType = _subType;
        platform = _platform;
    }

    function setChainLinkArgs(
        uint64 _subscriptionId,
        uint32 _callbackGasLimit,
        uint16 _requestConfirmations,
        uint32 _numWords,
        bytes32 _keyHash
    ) external onlyOwner {
        subscriptionId = _subscriptionId;
        callbackGasLimit = _callbackGasLimit;
        requestConfirmations = _requestConfirmations;
        numWords = _numWords;
        keyHash = _keyHash;
    }

    function setDrawArgs(uint256 price, uint64 min, uint64 max) external onlyOwner {
        require(price > 0 && min > 0 && max > min, "Args error");
        singlePrice = price;
        minCount = min;
        maxCount = max;
        emit SetDrawArgs(price, min, max);
    }

    function setGasFee(uint256[] memory prices) external onlyOwner {
        require(prices.length == 4, "Length error");
        ten = prices[0];
        fifty = prices[1];
        hundred = prices[2];
        aboveHundred = prices[3];
        emit SetGasPrice(prices);
    }

    function withdrawGasFee(address recipient, uint256 amount) external onlyOwner {
        require(recipient != address(0), "Zero address");
        sendValue(recipient, amount);
        emit Withdraw(recipient, amount, block.timestamp);
    }

    function sendValue(address recipient, uint256 amount) internal {
        address payable payableRecipient = payable(recipient);

        require(address(this).balance >= amount, "Insufficient balance");

        (bool success,) = payableRecipient.call{value: amount}("");
        require(success, "Unable to send value");
    }

    function startGame(address player, uint256 dollar, uint256 tokenId, bytes memory gameArgs)
        external
        payable
        onlyPlatform
        returns (uint256)
    {
        tokenId;
        gameArgs; // to avoid warnings of compiler
        require(dollar % singlePrice == 0, "Not an integer multiple");
        uint64 count = uint64(dollar / singlePrice);
        require(count <= maxCount && count >= minCount, "Over count");
        uint256 gasFee;
        if (count <= 10) {
            gasFee = ten;
        } else if (count <= 50) {
            gasFee = fifty;
        } else if (count <= 100) {
            gasFee = hundred;
        } else {
            gasFee = aboveHundred;
        }
        uint256 left = msg.value - gasFee; // !!! if msg.value < gasFee, revert!!!
        if (left > 0) {
            sendValue(player, left);
        }

        return requestRandomWords(player, count);
    }

    function requestRandomWords(address player, uint64 gameCount) private returns (uint256) {
        uint256 requestId =
            COORDINATOR.requestRandomWords(keyHash, subscriptionId, requestConfirmations, callbackGasLimit, numWords);

        vrfReq[requestId] = VRFRequest({player: player, count: gameCount});

        return requestId;
    }

    function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal override {
        VRFRequest memory request = vrfReq[requestId];
        require(request.player != address(0), "request not found");

        // uint256 random = randomWords[0];
        // uint256 hash = uint256(blockhash(block.number - 1));
        // TODO check again !!!
        // uint256 random = uint256(keccak256(abi.encodePacked(randomWords[0])));
        address player = request.player;
        uint256 gameCount = request.count;
        gamePool.settlement(randomWords[0], player, gameCount, requestId, mainType, subType);
        delete vrfReq[requestId];
    }
}
