    "contracts/utils/PromoStorage.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.6;

import "contracts/utils/cryptography/SignatureCheckerUpgradeable.sol";
import "contracts/utils/cryptography/draft-EIP712Upgradeable.sol";
import "contracts/utils/Access.sol";

library PromoLib {
    bytes32 internal constant PROMO_VERIFIER_ROLE = keccak256("PROMO_VERIFIER_ROLE");
    bytes32 internal constant PROMO_PRICE_SETTING_KEY = keccak256("PROMO_PRICE_SETTING_KEY");
    bytes32 internal constant PROMO_USER_NONCE_MAPPING = keccak256("PROMO_USER_NONCE_MAPPING");

    function promoPrice() internal view returns(uint256) {
        return SettingsStorage.getUint256Setting(PROMO_PRICE_SETTING_KEY);
    }

    struct UserNonce {
        mapping (
            address /*user*/ => mapping (address /*verifier*/ => mapping(uint256 /*nonce*/ => bool /*used*/))
        ) userVerifierNonceIsUsed;
    }

    function promoUserVerifierNonceIsUsed() internal view returns(
        mapping (address => mapping (address => mapping(uint256 => bool))) storage
    ) {
        UserNonce storage userNonce;
        bytes32 slot = PROMO_USER_NONCE_MAPPING;
        assembly {
            userNonce.slot := slot
        }
        return userNonce.userVerifierNonceIsUsed;
    }

    function getPromoUserVerifierNonceIsUsed(
        address user,
        address verifier,
        uint256 nonce
    ) internal view returns(bool) {
        return promoUserVerifierNonceIsUsed()[user][verifier][nonce];
    }

    function usePromoUserVerifierNonce(
        address user,
        address verifier,
        uint256 nonce
    ) internal {
        require(!promoUserVerifierNonceIsUsed()[user][verifier][nonce], "NONCE_ALREADY_USED");
        promoUserVerifierNonceIsUsed()[user][verifier][nonce] = true;
    }
}

abstract contract PromoStorage is EIP712Upgradeable, Access {
    using SignatureCheckerUpgradeable for address;

    event PromoPricePaid(
        address indexed minter,
        address indexed verifier,
        uint256 indexed tokenId,
        uint256 promoPrice,
        uint256 nonce
    );

    function mintWithPromoPriceDigest(
        address minter,
        uint256 promoPrice,
        uint256 nonce,
        uint256 deadline
    ) public view returns(bytes32 digest) {
        digest = _hashTypedDataV4(keccak256(abi.encode(
            keccak256("mintWithPromoPrice(address minter,uint256 promoPrice,uint256 nonce,uint256 deadline)"),
            minter,
            promoPrice,
            nonce,
            deadline
        )));
    }

    function _checkMintWithPromoPriceSignature(
        address minter,
        uint256 promoPrice,
        uint256 nonce,
        uint256 deadline,
        address signer,
        bytes memory signature
    ) internal {
        require(block.timestamp < deadline, "EXPIRED");
        require(promoPrice == PromoLib.promoPrice(), "INVALID_PROMO_PRICE");
        bytes32 digest = mintWithPromoPriceDigest({
            minter: minter,
            promoPrice: promoPrice,
            nonce: nonce,
            deadline: deadline
        });
        PromoLib.usePromoUserVerifierNonce(minter, signer, nonce);
        require(signer != address(0), "SIGNER_IS_ZERO_ADDRESS");
        require(signer.isValidSignatureNow({hash: digest, signature: signature}), "INVALID_SIGNATURE");
        require(hasRole(PromoLib.PROMO_VERIFIER_ROLE, signer), "SIGNER_HAS_NO_VERIFIER_ROLE");
    }
}"
