    "contracts/utils/Access.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.6;

import "contracts/utils/Strings.sol";
import "contracts/utils/structs/EnumerableSet.sol";
import "contracts/access/OwnableUpgradeable.sol";
import "contracts/utils/AccessControlEnumerableUpgradeable.sol";
import "contracts/utils/structs/EnumerableSetUpgradeable.sol";

interface IAccessStorage {
    function getUint256Setting(bytes32) external view returns (uint256);
}

interface ICheckConfiguratorAllowedSelector {
    function checkConfiguratorAllowedSelector(bytes32 selector) external view returns(bool);
}

library SettingsStorage {
    using Strings for uint256;

    event MinUint256SettingChanged(address contractAddress, bytes32 indexed setting, uint256 value);
    event MaxUint256SettingChanged(address contractAddress, bytes32 indexed setting, uint256 value);
    event Uint256SettingChanged(address contractAddress, bytes32 indexed setting, uint256 value);
    event AddressSettingChanged(address contractAddress, bytes32 indexed setting, address value);

    struct Layout {
        mapping(bytes32 => uint256) uint256Settings;
        mapping(bytes32 => address) addressSettings;
        mapping(bytes32 => uint256) uint256SettingsMin;
        mapping(bytes32 => uint256) uint256SettingsMax;
    }

    bytes32 internal constant STORAGE_SLOT =
        keccak256('storage.SettingsStorage');

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }

    function setUint256SettingMinMax(bytes32 setting, uint256 min, uint256 max) external {
        Layout storage l = layout();
        require(setting != bytes32(0), "setting is zero");
        l.uint256SettingsMin[setting] = min;
        l.uint256SettingsMax[setting] = max;
        emit MinUint256SettingChanged(address(this), setting, min);
        emit MaxUint256SettingChanged(address(this), setting, max);
    }

    function setUint256SettingMin(bytes32 setting, uint256 value) public {
        Layout storage l = layout();
        require(setting != bytes32(0), "setting is zero");
        l.uint256SettingsMin[setting] = value;
        emit MinUint256SettingChanged(address(this), setting, value);
    }

    function setUint256SettingMax(bytes32 setting, uint256 value) public {
        Layout storage l = layout();
        require(setting != bytes32(0), "setting is zero");
        l.uint256SettingsMax[setting] = value;
        emit MaxUint256SettingChanged(address(this), setting, value);
    }

    function strAndBytes32(string memory str, bytes32 b) public pure returns (string memory) {
        return string(abi.encodePacked(str, uint256(b).toHexString(32)));
    }

    function setUint256Setting(bytes32 setting, uint256 value) external {
        Layout storage l = layout();
        require(setting != bytes32(0), "setting is zero");

        uint256 min = l.uint256SettingsMin[setting];
        uint256 max = l.uint256SettingsMax[setting];
        require(value >= min, strAndBytes32("value < min: ", setting));
        if (max != 0) {
            require(value <= max, strAndBytes32("value > max: ", setting));
        }

        l.uint256Settings[setting] = value;
        emit Uint256SettingChanged(address(this), setting, value);
    }

    function getUint256Setting(bytes32 setting) internal view returns (uint256) {
        Layout storage l = layout();
        return l.uint256Settings[setting];
    }

    function setAddressSetting(bytes32 setting, address value) external {
        Layout storage l = layout();
        l.addressSettings[setting] = value;
        emit AddressSettingChanged(address(this), setting, value);
    }

    function getAddressSetting(bytes32 setting) internal view returns (address) {
        Layout storage l = layout();
        return l.addressSettings[setting];
    }
}

/// @title AccessUtils
/// @notice Utility functions
contract AccessUtils {
    function getUint256SettingAccessIdentifier(bytes32 setting) public pure returns (bytes32) {
        bytes32 accessIdentifier = keccak256(abi.encodePacked(
            bytes4(keccak256(bytes("setUint256Setting(bytes32,uint256)"))),
            setting
        ));
        return accessIdentifier;
    }
}

/// @title useful access features of combination of Roles model and Ownable
abstract contract Access is ICheckConfiguratorAllowedSelector, OwnableUpgradeable, AccessControlEnumerableUpgradeable {
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;
    using EnumerableSet for EnumerableSet.Bytes32Set;

    bytes32 constant internal CONFIGURATOR_ROLE = keccak256("CONFIGURATOR_ROLE");
    bytes32 constant internal CONFIGURATOR_ALLOWED_SELECTORS_SLOT = keccak256("Access.CONFIGURATOR_ALLOWED_SELECTORS.slot");

    function __Access_init(address ownerValue) internal initializer {
        __Ownable_init_unchained();
        __Access_init_unchained(ownerValue);
    }

    function __Access_init_unchained(address ownerValue) internal {
        transferOwnership(ownerValue);
        _setupRole(CONFIGURATOR_ROLE, address(this));  // grant itself role
    }

    event CallSetUint256Setting(address caller, address contractAddress, bytes32 setting, uint256 value);

    function setUint256Setting(bytes32 setting, uint256 value) external {
        emit CallSetUint256Setting(msg.sender, address(this), setting, value);
        bytes32 accessIdentifier = keccak256(abi.encodePacked(
            bytes4(keccak256(bytes("setUint256Setting(bytes32,uint256)"))),
            setting
        ));
        _checkOwnerOrConfiguratorAllowedSelector(accessIdentifier);
        _setUint256Setting(setting, value);
    }

    function _setUint256Setting(bytes32 setting, uint256 value) internal {
        SettingsStorage.setUint256Setting(setting, value);
    }

    function setUint256SettingMinMax(bytes32 setting, uint256 min, uint256 max) external onlyOwner {
        SettingsStorage.setUint256SettingMinMax(setting, min, max);
    }

    function _setUint256SettingMinMax(bytes32 setting, uint256 min, uint256 max) internal {
        SettingsStorage.setUint256SettingMinMax(setting, min, max);
    }

    function _setUint256SettingMin(bytes32 setting, uint256 min) internal {
        SettingsStorage.setUint256SettingMin(setting, min);
    }

    function _setUint256SettingMax(bytes32 setting, uint256 max) internal {
        SettingsStorage.setUint256SettingMax(setting, max);
    }

    // note: exposes all storage variables to read
    function getUint256Setting(bytes32 setting) public view returns (uint256) {
        return SettingsStorage.getUint256Setting(setting);
    }

    function setAddressSetting(bytes32 setting, address value) external {
        bytes32 accessIdentifier = keccak256(abi.encodePacked(bytes4(keccak256(bytes("setAddressSetting(bytes32,address)"))), setting));
        _checkOwnerOrConfiguratorAllowedSelector(accessIdentifier);
        _setAddressSetting(setting, value);
    }

    function _setAddressSetting(bytes32 setting, address value) internal {
        SettingsStorage.setAddressSetting(setting, value);
    }

    function getAddressSetting(bytes32 setting) public view returns (address) {
        return SettingsStorage.getAddressSetting(setting);
    }

    function _configuratorAllowedSelectorsSet() private pure returns (EnumerableSet.Bytes32Set storage set) {
        bytes32 slot = CONFIGURATOR_ALLOWED_SELECTORS_SLOT;
        assembly {
            set.slot := slot
        }
    }

    event ConfiguratorAllowedSelectorSet(bytes32 selector, bool value);

    function checkConfiguratorAllowedSelector(bytes32 selector) external override view returns(bool) {
        // note: we store bytes32
        //   for selector we store bytes32(bytes4(selector))
        return _configuratorAllowedSelectorsSet().contains(selector);
    }

    function _enableConfiguratorSelector(bytes32 _id) internal {
        _configuratorAllowedSelectorsSet().add(_id);
        emit ConfiguratorAllowedSelectorSet(_id, true);
    }

    function _enableConfiguratorUint256Setting(bytes32 settingKey) internal {
        bytes32 accessIdentifier = keccak256(abi.encodePacked(
            bytes4(keccak256(bytes("setUint256Setting(bytes32,uint256)"))),
            settingKey
        ));
        _enableConfiguratorSelector(accessIdentifier);
    }

    function setConfiguratorAllowedSelector(bytes32 selector, bool value) external onlyOwner {
        if (value) {
            _configuratorAllowedSelectorsSet().add(selector);
            emit ConfiguratorAllowedSelectorSet(selector, true);
        } else {
            _configuratorAllowedSelectorsSet().remove(selector);
            emit ConfiguratorAllowedSelectorSet(selector, false);
        }
    }

    function getConfiguratorAllowedSelectorAt(uint256 index) public view returns (bytes32) {
        return _configuratorAllowedSelectorsSet().at(index);
    }

    /// @notice Grant role to account
    /// @param role role
    /// @param account account
    function grantRole(bytes32 role, address account) public virtual override onlyOwner {
        _grantRole(role, account);
        _roleMembers[role].add(account);
    }

    /// @notice Revokes role from account
    /// @param role role
    /// @param account account
    function revokeRole(bytes32 role, address account) public virtual override onlyOwner {
        _revokeRole(role, account);
        _roleMembers[role].remove(account);
    }

    function _checkOwnerOrConfiguratorAllowedSelector(bytes32 selector) internal view {
        if (msg.sender != owner()) {
            require(hasRole(CONFIGURATOR_ROLE, msg.sender), "not the owner or configurator");
            require(_configuratorAllowedSelectorsSet().contains(selector), "disallowed selector");
        }
    }

    modifier onlyOwnerOrConfigurator(bytes4 selector) {
        // this is a trick to save contract size because modifier is copied in place
        _checkOwnerOrConfiguratorAllowedSelector(bytes32(selector));
        _;
    }
}
"
    },
    "contracts/utils/Strings.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

/**
 * @dev String operations.
 */
library Strings {
    bytes16 private constant _HEX_SYMBOLS = "0123456789abcdef";

    /**
     * @dev Converts a `uint256` to its ASCII `string` decimal representation.
     */
    function toString(uint256 value) internal pure returns (string memory) {
        // Inspired by OraclizeAPI's implementation - MIT licence
        // https://github.com/oraclize/ethereum-api/blob/b42146b063c7d6ee1358846c198246239e9360e8/oraclizeAPI_0.4.25.sol

        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }

    /**
     * @dev Converts a `uint256` to its ASCII `string` hexadecimal representation.
     */
    function toHexString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return "0x00";
        }
        uint256 temp = value;
        uint256 length = 0;
        while (temp != 0) {
            length++;
            temp >>= 8;
        }
        return toHexString(value, length);
    }

    /**
     * @dev Converts a `uint256` to its ASCII `string` hexadecimal representation with fixed length.
     */
    function toHexString(uint256 value, uint256 length) internal pure returns (string memory) {
        bytes memory buffer = new bytes(2 * length + 2);
        buffer[0] = "0";
        buffer[1] = "x";
        for (uint256 i = 2 * length + 1; i > 1; --i) {
            buffer[i] = _HEX_SYMBOLS[value & 0xf];
            value >>= 4;
        }
        require(value == 0, "Strings: hex length insufficient");
        return string(buffer);
    }
}
"
