/**
  @title Creating PixelBattle_ERC1155
  @dev Contract for creating nfts using ERC1155. Unlike ERC20, ERC1155 lacks a decimals field,
       since each token is distinct and cannot be partitioned
*/
contract PixelBattle_ERC1155 is ERC1155URIStorage, Ownable, Pausable, ERC1155Burnable {

    // init
    string public name = "PixelBattle by Pixelplay";
    string public symbol = "PixelMecha";

    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;


    using Strings for uint256;
    string private _baseURI = "";
    mapping(uint256 => string) private _tokenURIs;
    mapping(uint256 => uint256) private _totalSupply;
    mapping(uint256 => mapping(address => uint256)) private _balances;

    uint public _r1 = 1;
    uint public _r2 = 2;
    uint public _r3 = 3;
    uint public _r4 = 4;
    uint public _r5 = 5;
    uint public _r6 = 6;

    MintRoundManager _round1Manager = new MintRoundManager();
    MintRoundManager _round2Manager = new MintRoundManager();
    MintRoundManager _round3Manager = new MintRoundManager();
    MintRoundManager _round4Manager = new MintRoundManager();
    MintRoundManager _round5Manager = new MintRoundManager();
    MintRoundManager _round6Manager = new MintRoundManager();

    function getRndMngr(uint round)
    public
    view
    returns (MintRoundManager){
        MintRoundManager _rm;
        if (round == _r1) {
            _rm = _round1Manager;
        } else if (round == _r2) {
            _rm = _round2Manager;
        } else if (round == _r3) {
            _rm = _round3Manager;
        } else if (round == _r4) {
            _rm = _round4Manager;
        } else if (round == _r5) {
            _rm = _round5Manager;
        } else if (round == _r6) {
            _rm = _round6Manager;
        }
        return _rm;
    }

    constructor() ERC1155(
    //        "ipfs://QmQZD1NtvKj2aqo6zCUN6ko3wXALGqy8NV4vhvooNpSSdB/{id}.json"
    //        "https://ipfs.io/ipfs/QmQNc9TRBLrntBDmK6a5CR9eVgjH37xDsAwV7nfXFg1APC/{id}.json"
    //        "https://turquoise-bitter-scorpion-130.mypinata.cloud/ipfs/QmQNc9TRBLrntBDmK6a5CR9eVgjH37xDsAwV7nfXFg1APC/{id}.json"
    //        "https://turquoise-bitter-scorpion-130.mypinata.cloud/ipfs/QmQZD1NtvKj2aqo6zCUN6ko3wXALGqy8NV4vhvooNpSSdB/{id}.json"
        "https://api.nftvisualization.io/api/v1/hyper-play/contract/0x0d278F3209628298271Cc50FE53556381698d0F3/token/{id}/metadata"
    ) {
    }

    // ======================== MINT by payable ========================
    function mint(uint round, uint256[] memory ids)
    public
    payable
    {
        basicCheck(ids);
        MintRoundManager _roundManager = getRndMngr(round);
        uint qty = ids.length;
        bool isWLMode = _roundManager.isWLMode();
        bool isPackMode = _roundManager.isPackMode();
        uint[] memory metaData = _roundManager.getMetaData();
        uint maxMintAmountPerWallet = metaData[0];
        uint maxMintAmountPerTx = metaData[1];
        uint256 mintPrice = metaData[2];
        uint amountPack = metaData[4];
        uint totalSupply = metaData[5];
        uint256 mintIndexForSale = _roundManager.getMintedAmountInfo();
        uint startBlockNumber = _roundManager.getMintingStartBlock();
        uint endBlockNumber = _roundManager.getMintingEndBlock();

        if (isPackMode) {
            require(qty == amountPack, "Mint Quantity per tx exceed (1)");
        } else {
            require(qty <= maxMintAmountPerTx, "Mint Quantity per tx exceed (2)");
        }

        require(mintIndexForSale + qty <= totalSupply, "Total mint Quantity exceed");
        require(getCurrentBlock() >= startBlockNumber && getCurrentBlock() < endBlockNumber, "Start time has not reached yet");
        require(msg.value >= SafeMath.mul(mintPrice, qty), "Not enough Matic");

        if (isWLMode) {
            require(qty <= _roundManager.getAllowList(msg.sender), "Mint Quantity exceed");
            require(_roundManager.hasAddress(msg.sender), "Not White List or invalid Quantity");
        }
        if (isPackMode) {
            mintPack(msg.sender, ids);

            _roundManager.minted(amountPack);
            if (isWLMode) {
                _roundManager.decreaseAmount(msg.sender, amountPack);
            }
        }  else {
            mintPack(msg.sender, ids);

            _roundManager.minted(qty);
            if (isWLMode) {
                _roundManager.decreaseAmount(msg.sender, qty);
            }
        }
    }

    // ======================== MINT/BURN by Owner ========================
    function mintPack(address recepient, uint256[] memory ids)
    private
    returns (uint256[] memory)
    {
        basicCheck(ids);

        uint256 amount = 1;
        uint256[] memory amounts = new uint256[](ids.length);

        for (uint256 i = 0; i < ids.length; i++) {

            uint256 _itemId = ids[i];
            amounts[i] = 1;

            _tokenURIs[_itemId] = uri(_itemId);
            _totalSupply[_itemId] = amount;
        }
        _mintBatch(recepient, ids, amounts, "");
        return ids;
    }

    function mintFixed(address recepient, uint tokenId)
    public
    onlyOwner
    returns (uint256)
    {
        string memory tokenUri = uri(tokenId);
        _totalSupply[tokenId] = 1;
        _mint(recepient, tokenId, 1, "");
        return tokenId;
    }

    function mintBatch(address to,
        uint256[] memory ids,
        uint256[] memory amounts)
    public
    onlyOwner
    {
        for (uint256 i = 0; i < ids.length; i++) {
            uint256 _itemId = ids[i];
            _totalSupply[_itemId] = 1;
            _tokenURIs[_itemId] = uri(_itemId);
        }
        return _mintBatch(to, ids, amounts, "");
    }

    // ======================== BURN ========================
    function burn(address from, uint256 tokenId)
    public
    onlyOwner
    {
        _burn(from, tokenId, 1);
    }

    function burnBatch(address from, uint256[] memory ids)
    public
    onlyOwner
    {
        uint256[] memory amounts = new uint256[](ids.length);

        for (uint256 i = 0; i < ids.length; i++) {
            amounts[i] = 1;
        }
        _burnBatch(from, ids, amounts);
    }

    // ======================== STATUS ========================
    function currentBlockTimeStamp() public view returns (uint) {
        return block.timestamp;
    }

    function getCurrentBlock() public view returns (uint) {
        return block.number;
    }

    // msg.data (bytes): complete calldata
    function getMsgData() public onlyOwner view returns (bytes4) {
        return msg.sig;
    }
    // msg.sig (bytes4): first four bytes of the calldata (i.e. function identifier)
    function getMsgSig() public onlyOwner view returns (bytes4) {
        return msg.sig;
    }
    // msg.gas (uint): remaining gas - deprecated in version 0.4.21 and to be replaced by gasleft()
    function getGasLeft() public onlyOwner view returns (uint) {
