      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract SogaHeroesSales is ReentrancyGuard, Ownable, ERC1155Holder {
  using Counters for Counters.Counter;
  Counters.Counter private _itemIds;

  AggregatorV3Interface internal priceFeed;
  IERC20 public ERC20Token;
  IERC20 public SogaToken;
  address internal nftContract;
  address internal addressExceptionToBuy;
  bool internal maticAllowed;
  bool internal usdcAllowed;
  bool internal sogaAllowed;
  uint internal minMaticR1;
  uint internal minSogaR1;
  uint internal maxPurchR1;
  uint internal minMaticR2;
  uint internal minSogaR2;
  uint internal maxPurchR2;
  uint internal minMaticR3;
  uint internal minSogaR3;
  uint internal maxPurchR3;
  
  constructor(address nftContractAddress, address erc20ContractAddress, address oracleAddress) {
      nftContract = nftContractAddress;
      ERC20Token = IERC20(erc20ContractAddress);
      priceFeed = AggregatorV3Interface(oracleAddress);
      maticAllowed = true;
      usdcAllowed = true;
      sogaAllowed = false;
      minMaticR1 = 5;
      minSogaR1 = 10;
      maxPurchR1 = 1000;
      minMaticR2 = 12;
      minSogaR2 = 25;
      maxPurchR2 = 1000;
      minMaticR3 = 22;
      minSogaR3 = 45;
      maxPurchR3 = 1000;
  }

  struct HeroeItem {
	  uint256 nftId;
      uint8 rarity;
	  address payable seller;
	  address payable owner;
	  uint256 price;
      bool sold;	
  }

  mapping(uint256 => HeroeItem) private idToHeroeItem;

  event HeroeItemCreated (
    uint256 indexed nftId,
    uint8 rarity,
    address seller,
    address owner,
    uint256 price,
    bool sold
  );

  function getMaticLatestPrice() public view returns (uint256) {
   (
        uint80 roundID, 
        int price,
        uint startedAt,
        uint timeStamp,
        uint80 answeredInRound
    ) = priceFeed.latestRoundData();
    return uint(price) * 10**10;
  } 

  function setAddressException(address addr) public onlyOwner {
    addressExceptionToBuy = addr;
  }

  function setCoins(address erc20ContractAddress, address sogaContractAddress) public onlyOwner {
    ERC20Token = IERC20(erc20ContractAddress);
    SogaToken = IERC20(sogaContractAddress);
  }

  function setPrice(uint itemId, uint256 newprice) public onlyOwner {
      idToHeroeItem[itemId].price = newprice;
  }

  function setMaticAllowed(bool allowed) public onlyOwner {
    maticAllowed = allowed;
  }

  function setUsdcAllowed(bool allowed) public onlyOwner {
    usdcAllowed = allowed;
  }

  function setSogaAllowed(bool allowed) public onlyOwner {
    sogaAllowed = allowed;
  }

  function setMinPriceMatic(uint[] memory minsMatic) public onlyOwner {
    require(minsMatic.length == 3, "Array length is wrong");
    minMaticR1 = minsMatic[0];
    minMaticR2 = minsMatic[1];
    minMaticR3 = minsMatic[2];
  }

  function setMinPriceSoga(uint[] memory minsSoga) public onlyOwner {
    require(minsSoga.length == 3, "Array length is wrong");
    minSogaR1 = minsSoga[0];
    minSogaR2 = minsSoga[1];
    minSogaR3 = minsSoga[2];
  }

  function setMaxPurchases(uint[] memory maxPurchases) public onlyOwner {
    require(maxPurchases.length == 3, "Array length is wrong");
    maxPurchR1 = maxPurchases[0];
    maxPurchR2 = maxPurchases[1];
    maxPurchR3 = maxPurchases[2];
  }

  /* Upload VPL NFT for sale */
  function createMarketItem(
    uint256 tokenId,
    uint8 rarity,
    uint256 price
  ) public payable nonReentrant {
    require(price > 0, "Price must be at least 1 wei");

    _itemIds.increment();

    idToHeroeItem[_itemIds.current()] = HeroeItem(
      tokenId,
      rarity,
      payable(msg.sender),
      payable(address(0)),
      price,
      false
    );
    
    IERC1155(nftContract).safeTransferFrom(msg.sender, address(this), tokenId, 1, "");
   
    emit HeroeItemCreated(
      tokenId,
      rarity,
      msg.sender,
      address(0),
      price,
      false
    );
  }

  function createMarketItemBatch(uint256 [] memory tokenIds, uint8 [] memory rarities, uint256 [] memory prices) public {
    require(tokenIds.length == rarities.length && tokenIds.length == prices.length, "Mismatch lengths");
    for (uint256 i = 0; i < tokenIds.length; i++) {
      createMarketItem(tokenIds[i], rarities[i], prices[i]);
    }
  }

  /* Make sale */
  /* 0: usdc, 1: matic, 2: soga */
  function createMarketSale(uint256 itemId, uint8 matic) public payable nonReentrant {
    uint256 price = idToHeroeItem[itemId].price;
    uint256 tokenId = idToHeroeItem[itemId].nftId;
    uint8 rarity = idToHeroeItem[itemId].rarity;

    uint minMatic;
    uint minSoga;
    uint maxPurchase;
    if (rarity == 1) {
        minMatic = minMaticR1;
        minSoga = minSogaR1;
        maxPurchase = maxPurchR1;
    } else if (rarity == 2) {
        minMatic = minMaticR2;
        minSoga = minSogaR2;
        maxPurchase = maxPurchR2;
    } else {
        minMatic = minMaticR3;
        minSoga = minSogaR3;
        maxPurchase = maxPurchR3;
    }

    require(buyedByRarity(msg.sender, rarity) < maxPurchase, "You have already buyed the maximum Heroes with this rarity");
    if (matic == 1) { //MATIC
        require(maticAllowed, "MATIC is not allowed at this time");
        require(msg.value > (minMatic * 10 ** 18), "Please submit the asking price in order to complete the purchase with MATIC");
        idToHeroeItem[itemId].seller.transfer(msg.value);
    } else if (matic == 0) { //USDC
        require(usdcAllowed, "Generic ERC20 like USDC is not allowed at this time");
        require(msg.value == price, "Please submit the asking price in order to complete the purchase with USDC");
        ERC20Token.transferFrom(msg.sender, idToHeroeItem[itemId].seller, msg.value);
    } else { //SOGA
        require(sogaAllowed, "SOGA is not allowed at this time");
        //require(msg.value >= price/2, "Please submit the asking price in order to complete the purchase with SOGA");
        require(msg.value == (minSoga * 10 ** 18), "Please submit the asking price in order to complete the purchase with SOGA");
        SogaToken.transferFrom(msg.sender, idToHeroeItem[itemId].seller, msg.value);
    }
    IERC1155(nftContract).safeTransferFrom(address(this), msg.sender, tokenId, 1, "");
    idToHeroeItem[itemId].owner = payable(msg.sender);
    idToHeroeItem[itemId].sold = true;
  }

  /* Returns all market items */
  function fetchMarketItems() public view returns (HeroeItem[] memory) {
    uint itemCount = _itemIds.current();
    HeroeItem[] memory items = new HeroeItem[](itemCount);
    for (uint i = 0; i < itemCount; i++) {
        HeroeItem storage currentItem = idToHeroeItem[i+1];
        items[i] = currentItem;
    }
    return items;
  }

  /* Return a market item by id */
  function getMarketItem(uint256 itemId) public view returns (HeroeItem memory) {
    HeroeItem storage currentItem = idToHeroeItem[itemId];
    return currentItem;
  }

  function buyedByRarity(address userAddress, uint8 rarity) public view returns (uint) {
    uint itemCount = _itemIds.current();
    uint countByRarity = 0;
    for (uint i = 0; i < itemCount; i++) {
      HeroeItem storage currentItem = idToHeroeItem[i+1];
      if (addressExceptionToBuy != userAddress && currentItem.owner == userAddress && currentItem.rarity == rarity) {
        countByRarity++;
      }
    }
    return countByRarity;
  }
}"
