// File contracts/QrolliPostNFT.sol

pragma solidity ^0.8.0;

contract QrolliPostNFT is ERC1155SupplyUpgradeable, OwnableUpgradeable {
    using SafeERC20Upgradeable for IERC20Upgradeable;

    event MintedTo(address account, uint256 tokenId, uint256 amount);

    string public name;
    string public symbol;

    address public moderator;

    struct Post {
        string id;
        string uri;
        uint256 price;
        uint256 supply;
        uint256 royalty;
        uint256 ownerId;
    }

    struct PostStatus {
        uint256 likes;
        uint256 reports;
        mapping(uint256 => uint256) likedIds;
        mapping(uint256 => uint256) reportedIds;
    }

    mapping(string => Post) public posts;
    mapping(string => PostStatus) public postsStatus;
    mapping(string => uint256) public tokenIdByPostId;
    mapping(uint256 => string) public postIdByTokenId;
    mapping(uint256 => string) public uris;

    mapping(uint256 => uint256) public likesByProfiles;
    mapping(uint256 => uint256) public reportsByProfiles;
    mapping(uint256 => uint256) public collectsByProfiles;

    mapping(uint256 => uint256) public likesOfProfiles;
    mapping(uint256 => uint256) public reportsOfProfiles;
    mapping(uint256 => uint256) public collectsOfProfiles;

    mapping(string => mapping(uint256 => address)) public collectorsByPostId;

    address public qrToken;
    IQrolliID public qrolliId;

    uint256 public devPercent = 10;
    uint256 private _nextTokenId = 0;

    address public tipperHub;
    address public tippingToken;

    address public rewardHub;

    modifier onlyGov() {
        require(msg.sender == owner() || msg.sender == moderator, "1");
        _;
    }

    modifier onlyGovOrSelf() {
        require(
            msg.sender == owner() ||
                msg.sender == moderator ||
                msg.sender == address(this),
            "1"
        );
        _;
    }

    modifier onlyProfiles() {
        require(qrolliId.balanceOf(msg.sender) > 0, "2");
        _;
    }

    function initialize(
        string memory name_,
        string memory symbol_,
        address qr_,
        address qrolliId_,
        address moderator_
    ) public initializer {
        __ERC1155_init("");
        __Ownable_init();

        name = name_;
        symbol = symbol_;
        qrToken = qr_;
        qrolliId = IQrolliID(qrolliId_);
        moderator = moderator_;
    }

    function setName(string memory name_) external onlyGov {
        name = name_;
    }

    function setSymbol(string memory symbol_) external onlyGov {
        symbol = symbol_;
    }

    function setAddresses(
        address qr_,
        address qrolliId_,
        address rewardHub_
    ) external onlyGov {
        qrToken = qr_;
        qrolliId = IQrolliID(qrolliId_);
        rewardHub = rewardHub_;
    }

    function setDevPercentage(uint256 _percent) external onlyGov {
        devPercent = _percent;
    }

    function setTipperHub(
        address tipper_,
        address tippingToken_
    ) external onlyGov {
        tipperHub = tipper_;
        tippingToken = tippingToken_;
    }

    function withdraw(address[] calldata tokens) external onlyGov {
        for (uint256 i; i < tokens.length; i += 1) {
            IERC20Upgradeable token = IERC20Upgradeable(tokens[i]);
            if (tokens[i] == address(0)) {
                // solhint-disable-next-line avoid-low-level-calls
                (bool success, ) = msg.sender.call{
                    value: address(this).balance
                }("");
                require(success, "3");
            } else if (token.balanceOf(address(this)) > 0) {
                token.safeTransfer(msg.sender, token.balanceOf(address(this)));
            }
        }
    }

    function setModerator(address moderator_) external onlyOwner {
        moderator = moderator_;
    }

    /**
     * @dev calculates the next token ID based on value of _nextTokenId
     * @return uint256 for the next token ID
     */
    function getNextTokenId() public view returns (uint256) {
        return _nextTokenId;
    }

    /**
     * @dev increments the value of _nextTokenId
     */
    function _incrementTokenId() private {
        _nextTokenId++;
    }

    function verifySignature(
        bytes32 hashedMessage,
        bytes calldata signature
    ) private view returns (bool) {
        address _recovered = ECDSA.recover(hashedMessage, signature);
        return _recovered == moderator;
    }

    function addPost(
        string calldata id,
        string calldata _uri,
        uint256 price,
        uint256 limit,
        uint256 royalty,
        uint256 ownerId
    ) private {
        uint256 tokenId = getNextTokenId();
        Post storage _post = posts[id];
        _post.id = id;
        _post.uri = _uri;
        _post.price = price;
        _post.supply = limit;
        _post.royalty = royalty;
        _post.ownerId = ownerId;

        tokenIdByPostId[id] = tokenId + 1;
        postIdByTokenId[tokenId] = id;
        uris[tokenId] = _uri;
        _incrementTokenId();
        IRewardHub(rewardHub).increaseScore(0, ownerId);
    }

    function collect(
        string calldata id,
        string calldata _uri,
        uint256 price,
        uint256 limit,
        uint256 royalty,
        uint256 ownerId,
        uint256 deadline,
        bytes calldata signature
    ) external onlyProfiles {
        uint256 tokenId = tokenIdByPostId[id];

        if (tokenId == 0) {
            bytes32 hashed = ECDSA.toEthSignedMessageHash(
                abi.encodePacked(
                    id,
                    _uri,
                    price,
                    limit,
                    royalty,
                    ownerId,
                    deadline
                )
            );
            require(verifySignature(hashed, signature), "signature");
            require(block.timestamp < deadline, "!expired");
            require(royalty <= 100, "!royalty");
            addPost(id, _uri, price, limit, royalty, ownerId);
        }

        tokenId = tokenIdByPostId[id] - 1;

        Post memory _post = posts[id];

        if (_post.price > 0) {
            IERC20Upgradeable(qrToken).safeTransferFrom(
                msg.sender,
                address(this),
                _post.price
            );
            IERC20Upgradeable(qrToken).transfer(
                qrolliId.ownerOf(_post.ownerId),
                (_post.price * (100 - devPercent)) / 100
            );
        }

        require(balanceOf(msg.sender, tokenId) == 0, "collected");
        require(
            _post.supply == 0 || totalSupply(tokenId) < _post.supply,
            "!limit"
        );

        collectorsByPostId[id][totalSupply(tokenId)] = msg.sender;

        _mint(msg.sender, tokenId, 1, "");

        uint256 profileId = qrolliId.tokenOfOwnerByIndex(msg.sender, 0);

        collectsByProfiles[profileId] = collectsByProfiles[profileId] + 1;
        collectsOfProfiles[_post.ownerId] =
            collectsOfProfiles[_post.ownerId] +
            1;

        IRewardHub(rewardHub).increaseScore(1, _post.ownerId);
        emit MintedTo(msg.sender, tokenId, 1);
    }

    function getLike(
        string calldata id,
        address account
    ) public view returns (bool) {
        uint256 profileId = qrolliId.tokenOfOwnerByIndex(account, 0);

        for (uint256 i = 0; i < postsStatus[id].likes; i++) {
            if (profileId == postsStatus[id].likedIds[i]) {
                return true;
            }
        }
        return false;
    }

    function likeByRelayer(
        address _sender,
        string calldata id
    ) public onlyGovOrSelf {
        PostStatus storage _postStatus = postsStatus[id];
        uint256 profileId = qrolliId.tokenOfOwnerByIndex(_sender, 0);

        require(getLike(id, _sender) == false, "1");
        _postStatus.likedIds[_postStatus.likes] = profileId;
        _postStatus.likes = _postStatus.likes + 1;

        if (tokenIdByPostId[id] > 0) {
            Post memory _post = posts[id];
            likesOfProfiles[_post.ownerId] = likesOfProfiles[_post.ownerId] + 1;
        }

        likesByProfiles[profileId] = likesByProfiles[profileId] + 1;
    }

    function like(string calldata id) external onlyProfiles {
        likeByRelayer(msg.sender, id);
    }

    function dislikeByRelayer(
        address _sender,
        string calldata id
    ) public onlyGovOrSelf {
        PostStatus storage _postStatus = postsStatus[id];
        uint256 profileId = qrolliId.tokenOfOwnerByIndex(_sender, 0);

        require(getLike(id, _sender), "1");
        for (uint256 i = 0; i < _postStatus.likes; i++) {
            if (profileId == _postStatus.likedIds[i]) {
                _postStatus.likedIds[i] = _postStatus.likedIds[
                    _postStatus.likes - 1
                ];
                _postStatus.likes = _postStatus.likes - 1;
                break;
            }
        }
        if (tokenIdByPostId[id] > 0) {
            Post memory _post = posts[id];
            likesOfProfiles[_post.ownerId] = likesOfProfiles[_post.ownerId] - 1;
        }
        likesByProfiles[profileId] = likesByProfiles[profileId] - 1;
    }

    function dislike(string calldata id) external onlyProfiles {
        dislikeByRelayer(msg.sender, id);
    }

    function getReport(
        string calldata id,
        address account
    ) public view returns (bool) {
        uint256 profileId = qrolliId.tokenOfOwnerByIndex(account, 0);
        for (uint256 i = 0; i < postsStatus[id].reports; i++) {
            if (profileId == postsStatus[id].reportedIds[i]) {
                return true;
            }
        }
        return false;
    }

    function reportByRelayer(
        address _sender,
        string calldata id
    ) public onlyGovOrSelf {
        PostStatus storage _postStatus = postsStatus[id];
        uint256 profileId = qrolliId.tokenOfOwnerByIndex(_sender, 0);
        require(getReport(id, _sender) == false, "1");
        _postStatus.reportedIds[_postStatus.reports] = profileId;
        _postStatus.reports = _postStatus.reports + 1;

        if (tokenIdByPostId[id] > 0) {
            Post memory _post = posts[id];
            reportsOfProfiles[_post.ownerId] =
                reportsOfProfiles[_post.ownerId] +
                1;
        }
        reportsByProfiles[profileId] = reportsByProfiles[profileId] + 1;
    }

    function report(string calldata id) external onlyProfiles {
        reportByRelayer(msg.sender, id);
    }

    function getPostById(
        uint256 _tokenId
    )
        public
        view
        returns (
            string memory,
            string memory,
            uint256,
            uint256,
            uint256,
            address,
            uint256,
            uint256
        )
    {
        string memory postId = postIdByTokenId[_tokenId];
        Post memory _post = posts[postId];
        address postOwner = qrolliId.ownerOf(_post.ownerId);
        return (
            _post.id,
            _post.uri,
            _post.price,
            _post.supply,
            _post.royalty,
            postOwner,
            postsStatus[postId].likes,
            postsStatus[postId].reports
        );
    }

    function getPostLikes(
        string calldata _postId
    ) public view returns (address[] memory) {
        address[] memory _likers = new address[](postsStatus[_postId].likes);
        for (uint256 i = 0; i < postsStatus[_postId].likes; i++) {
            _likers[i] = qrolliId.ownerOf(postsStatus[_postId].likedIds[i]);
        }
        return _likers;
    }

    function getPostReporters(
        string calldata _postId
    ) public view returns (address[] memory) {
        address[] memory _reporters = new address[](
            postsStatus[_postId].reports
        );
        for (uint256 i = 0; i < postsStatus[_postId].reports; i++) {
            _reporters[i] = qrolliId.ownerOf(
                postsStatus[_postId].reportedIds[i]
            );
        }
        return _reporters;
    }

    function getLikesByProfile(address _profile) public view returns (uint256) {
        uint256 profileId = qrolliId.tokenOfOwnerByIndex(_profile, 0);
        return likesByProfiles[profileId];
    }

    function getReportsByProfile(
        address _profile
    ) public view returns (uint256) {
        uint256 profileId = qrolliId.tokenOfOwnerByIndex(_profile, 0);
        return reportsByProfiles[profileId];
    }

    function getCollectorsByPostId(
        string calldata _post
    ) public view returns (address[] memory) {
        uint256 tokenId = tokenIdByPostId[_post] - 1;
        uint256 supply = totalSupply(tokenId);
        address[] memory _collectors = new address[](supply);
        for (uint256 i = 0; i < supply; i++) {
            _collectors[i] = collectorsByPostId[_post][i];
        }
        return _collectors;
    }

    function tip(
        string calldata id,
        uint256 _value
    ) external payable onlyProfiles {
        address postOwner = qrolliId.ownerOf(posts[id].ownerId);
        uint256 profileId = qrolliId.tokenOfOwnerByIndex(msg.sender, 0);
        if (tippingToken == address(0)) {
            (bool success, ) = postOwner.call{value: _value}("");
        } else {
            IERC20Upgradeable(tippingToken).transferFrom(
                msg.sender,
                postOwner,
                _value
            );
        }
        IQrolliTipperHub(tipperHub).addTipHistory(id, profileId, _value);
        IRewardHub(rewardHub).increaseScore(2, profileId);
    }

    function setURI(uint256 id, string memory uri_) external onlyGov {
        uris[id] = uri_;
    }

    function uri(uint256 id) public view override returns (string memory) {
        return uris[id];
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

        if (from != address(0) && to != address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                string memory postId = postIdByTokenId[ids[i]];
                uint256 supply = totalSupply(ids[i]);
                uint256 k = 0;

                for (uint256 j = 0; j < supply; j++) {
                    if (collectorsByPostId[postId][j] == from) {
                        collectorsByPostId[postId][j] = to;
                        k++;
                    }
                    if (k == amounts[i]) {
                        break;
                    }
                }
            }
        }
    }

    uint256[48] private __gap;
}
