      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract iStripperCredits is Ownable{
    using SafeERC20 for IERC20; // Use SafeERC20 library for safe transfer of ERC20 tokens

    // Define a struct to represent a pack of credits
    struct CreditsPack{
        uint256 packId;
        uint256 usdtprice;
    }

    uint256 public currentDolzPrice;
    bytes32 private jobId;
    uint256 private fee;
    address public DOLZ;
    address public wallet;

    // Define a mapping to store the credit packs
    mapping(uint256 => CreditsPack) public creditPacks;
    // Define an array to store the IDs of all the credit packs
    uint256[] public packsList;

    // Define events to emit when a pack is created, edited, deleted or bought
    event PackCreated(uint256 poId, uint256 usdtPrice, uint256 timestamp, address caller); 
    event PackEdited(uint256 poId, uint256 newPrice, uint256 timestamp, address caller); 
    event PackDeleted(uint256 poId, uint256 timestamp, address caller);
    event PackBought(uint256 poId, uint256 usdtPrice, uint256 dolzAmount, uint256 dolzPrice, uint256 timestamp, address caller, string sessionId);

    constructor(address dolzAddress, address _wallet) {
        setDolzToken(dolzAddress);
        setWallet(_wallet);
    }

    function buyPack(uint256 poId, string calldata sessionId) external {
        // Check if the credit pack exists
        require(creditPacks[poId].usdtprice != 0, "iStripperCredits: This pack does not exists");
        CreditsPack memory pack = creditPacks[poId];
        uint256 packPrice = pack.usdtprice;
        uint256 dolzAmountToTransfer = packPrice * 1e18 / currentDolzPrice;

        // Check if the buyer has sufficient balance of DOLZ tokens
        require(IERC20(DOLZ).balanceOf(msg.sender) >= dolzAmountToTransfer * 1e18, "iStripperCredits: Insufficient balance");
        // Transfer the required amount of DOLZ tokens to the wallet address
        IERC20(DOLZ).safeTransferFrom(msg.sender, wallet, dolzAmountToTransfer * 1e18);
        emit PackBought(poId, packPrice, dolzAmountToTransfer, currentDolzPrice, block.timestamp, msg.sender, sessionId);
    }

    function setDolzPrice(uint256 _currentPrice) external onlyOwner{
        currentDolzPrice = _currentPrice;
    }

    function setDolzToken(address _dolzAddress) public onlyOwner{
        DOLZ = _dolzAddress;
    }

    function setWallet(address _wallet) public onlyOwner{
        wallet = _wallet;
    }

    function editPackPrice(uint256 poId, uint256 newPrice) public onlyOwner{
        require(creditPacks[poId].usdtprice != 0, "iStripperCredits: This pack does not exists");
        require(newPrice != 0, "iStripperCredits: New price cannot be null");
        CreditsPack memory pack = creditPacks[poId];
        pack.usdtprice = newPrice;
        creditPacks[poId] = pack;
        emit PackEdited(poId, newPrice, block.timestamp, msg.sender);
    }

    function createPack(uint256 poId, uint256 _usdtprice) external onlyOwner{
        require(creditPacks[poId].usdtprice == 0, "iStripperCredits: This pack already exists !");
        CreditsPack memory newPack = CreditsPack({packId: poId, usdtprice: _usdtprice});
        creditPacks[poId] = newPack;
        packsList.push(poId);
        emit PackCreated(poId, _usdtprice, block.timestamp, msg.sender);
    }

    function deletePack(uint256 poId) external onlyOwner{
        require(creditPacks[poId].usdtprice != 0, "iStripperCredits: This pack does not exists");
        delete creditPacks[poId];
        delete packsList[poId];
        emit PackDeleted(poId, block.timestamp, msg.sender);
    }

    function getPacksList() public view returns(uint256[] memory){
        return packsList;
    }

    function getPackPrice(uint256 poId) public view returns(uint256){
        return creditPacks[poId].usdtprice;
    }

    function getPack(uint256 poId) public view returns(CreditsPack memory){
        return creditPacks[poId];
    }

    // Emergency withdraw tokens
    function withdrawTokens(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).safeTransfer(owner(), _amount);
    }
}"
    },
