pragma solidity ^0.8.0;

interface IZenithFactory {
  function deployPool ( address _stakedToken, address _rewardToken, uint256 _rewardPerBlock, uint256 _startBlock, uint256 _bonusEndBlock ) external;
  function getStakeAddressByIndex ( uint256 index ) external view returns ( address );
  function getStakeAddressByPair ( address staked, address rewarded ) external view returns ( address[] memory );
  function getStakeAddressByRangeIndex ( uint256 fromIndex, uint256 toIndex ) external view returns ( address[] memory );
  function getTotalStake (  ) external view returns ( uint256 );
  function isManager ( address user ) external view returns ( bool );
  function owner (  ) external view returns ( address );
  function penaltyConfig (  ) external view returns ( uint256 minBlock, uint256 penaltyArg1, uint256 penaltyArg2 );
  function renounceOwnership (  ) external;
  function setManager ( address target, bool status ) external;
  function transferOwnership ( address newOwner ) external;
}
