    "contracts/baoMember.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "./baoBase.sol";

contract baoMember is ERC1155Pausable, Ownable, baoBase {
    AggregatorV3Interface internal _priceFeedAddress;
    uint256 public constant BASE_VOTE_POWER = 1;
    uint256 public constant BLOCK_PER_DAY = 40000;

    MINT_STATUS public _mint_status;
    uint256 public _max_supply = 10000;
    uint256 public _current_to_mint = 1;
    uint256 public _remaining_to_mint = 0;

    uint256 public _base_allow_to_mint = 5;
    uint256 public _vote_power_multiplier = 1;

    uint256 public _member_count;
    mapping(address => Member) public _member_info;
    mapping(uint256 => address) public _staked_tokens;
    mapping(string => address) public _nick_names_check;
    mapping(address => string) public _nick_names;
    uint256 public _staked_count;
    address[] public _holders;

    //Mint Price
    uint256 public _mint_price_usd = 20;

    //tether contract Address
    IERC20 public immutable _tetherContract;

    //errors
    error baoMember__supplyEnded();
    error baoMember__supplyLimit();
    error baoMember__mintingIsPaused();
    error baoMember__justAlloListPermitted();
    error baoMember__maxMintReached();
    error baoMember__invalidStakeRequest();
    error baoMember__invalidUnstakeRequest();
    //

    //events
    event baoEvent__Stake(address indexed member, uint256 indexed tokenid);
    event baoEvent__Unstake(address indexed member, uint256 indexed tokenid);
    event baoEvent__Mint(address indexed member, uint256 indexed tokenid);
    event baoEvent__ChangeMintPrice(address indexed executer, uint256 oldprice, uint256 newprice);
    event baoEvent__ChangeMaxSupply(address indexed executer, uint256 oldsupply, uint256 newsupply);

    //Modifiers
    modifier mint_allowance() {
        Member memory member = _member_info[msg.sender];
        if (member.allow_to_mint_number <= 0) {
            revert baoMember__maxMintReached();
        }
        _;
    }
    modifier add_user_info() {
        if (_member_info[msg.sender].memberAddress == address(0)) {
            _member_info[msg.sender].memberAddress = msg.sender;
            _member_info[msg.sender].allow_to_mint_number = _base_allow_to_mint;
            _member_count += 1;
        }
        _;
    }

    modifier supply_check() {
        if (_mint_status == MINT_STATUS.PAUSE) {
            revert baoMember__mintingIsPaused();
        } else if (_mint_status == MINT_STATUS.ALLOW_LIST) {
            if (!_member_info[msg.sender].is_allow_list) {
                revert baoMember__justAlloListPermitted();
            }
        }

        if (_remaining_to_mint <= 0) {
            revert baoMember__supplyLimit();
        }

        if (_max_supply - _current_to_mint <= 0) {
            revert baoMember__supplyEnded();
        }
        _;
    }

    ////
    constructor(
        string memory baseURI,
        address tetherAddress,
        address priceFeedAddress
    ) ERC1155(baseURI) {
        _mint_status = MINT_STATUS.PAUSE;
        _tetherContract = IERC20(tetherAddress);
        _priceFeedAddress = AggregatorV3Interface(priceFeedAddress);
    }

    function SetNickName(string memory nickname) public {
        require(_nick_names_check[nickname] == address(0), "this nickname has already taken!");
        _nick_names_check[nickname] = msg.sender;
        _nick_names[msg.sender] = nickname;
    }

    function Stake(uint256 tokenid) public {
        if (
            (_staked_tokens[tokenid] != address(0) ||
                balanceOf(msg.sender, tokenid) == 0 ||
                _member_info[msg.sender].is_stacked == true)
        ) {
            revert baoMember__invalidStakeRequest();
        }

        _member_info[msg.sender].is_stacked = true;
        _member_info[msg.sender].stake_block_number = block.number;
        _member_info[msg.sender].staked_tokenid = tokenid;
        _staked_tokens[tokenid] = msg.sender;
        _staked_count += 1;
        emit baoEvent__Stake(msg.sender, tokenid);
    }

    function Unstake(uint256 tokenid) public {
        if (
            (_staked_tokens[tokenid] == address(0) ||
                balanceOf(msg.sender, tokenid) != 1 ||
                _member_info[msg.sender].is_stacked == false ||
                _staked_tokens[tokenid] != msg.sender)
        ) {
            revert baoMember__invalidUnstakeRequest();
        }
        _member_info[msg.sender].is_stacked = false;
        _member_info[msg.sender].stake_block_number = 0;
        _member_info[msg.sender].staked_tokenid = 0;
        _staked_tokens[tokenid] = address(0);
        _staked_count -= 1;
        emit baoEvent__Unstake(msg.sender, tokenid);
    }

    function GetVotePower(address memberAddress) public view returns (uint256) {
        Member memory member = _member_info[memberAddress];
        if (member.memberAddress == address(0)) {
            return 0;
        } else {
            if (member.is_stacked) {
                return ((((block.number - member.stake_block_number) / BLOCK_PER_DAY) *
                    _vote_power_multiplier) + BASE_VOTE_POWER);
            } else {
                return (BASE_VOTE_POWER);
            }
        }
    }

    function GetMintFee() public view returns (uint256) {
        if (_member_info[msg.sender].free_mint == true) {
            return 0;
        } else {
            (, int256 price, , , ) = _priceFeedAddress.latestRoundData();
            uint256 adjustedPrice = uint256(price) * 10 ** 10; // 18 decimals

            return (_mint_price_usd * 10 ** 18) / adjustedPrice;
        }
    }

    function MintByMatic() public payable add_user_info supply_check mint_allowance {
        require(msg.value >= (GetMintFee() * 10 ** 18), "Not enough funds to mint NFT");
        mint(msg.sender);
    }

    function MintByTether() public add_user_info supply_check mint_allowance {
        if (GetMintFee() != 0) {
            _tetherContract.transferFrom(msg.sender, address(this), _mint_price_usd * (10 ** 6));
        }
        mint(msg.sender);
    }

    function mint(address to) private {
        Member memory member = _member_info[to];
        _mint(to, _current_to_mint, 1, "");
        emit baoEvent__Mint(to, _current_to_mint);
        _holders.push(to);
        _current_to_mint += 1;
        member.current_nft_count += 1;
        member.allow_to_mint_number -= 1;
        member.free_mint = false;
        _remaining_to_mint -= 1;

        if (member.mintMultiplier > 0) {
            while (member.mintMultiplier - 1 > 0) {
                _mint(to, _current_to_mint, 1, "");
                emit baoEvent__Mint(to, _current_to_mint);
                _current_to_mint += 1;
                member.mintMultiplier -= 1;
                member.current_nft_count += 1;
                member.allow_to_mint_number -= 1;
                _remaining_to_mint -= 1;
            }
        }

        member.mintMultiplier = 0;
        _member_info[to] = member;
    }

    ////protocol functions

    function change_mint_price(uint256 newprice) public onlyOwner {
        emit baoEvent__ChangeMintPrice(msg.sender, _mint_price_usd, newprice);
        _mint_price_usd = newprice;
    }

    function change_max_supply(uint256 newsupply) public onlyOwner {
        emit baoEvent__ChangeMaxSupply(msg.sender, _max_supply, newsupply);
        _max_supply = newsupply;
    }

    //Community Admin Management
    function get_locked_value() public view returns (uint256, uint256) {
        return (address(this).balance, _tetherContract.balanceOf(address(this)));
    }

    function widthdrawal(address to, uint256 amount, uint8 wtype) public onlyOwner {
        if (wtype == 0) {
            require(address(this).balance > 0, "Not any Matics to transfer");
            if (amount == 0) {
                payable(to).transfer(address(this).balance);
            } else {
                payable(to).transfer(amount);
            }
        } else {
            require(_tetherContract.balanceOf(address(this)) > 0, "Not any Tethers to transfer");
            if (amount == 0) {
                _tetherContract.transfer(to, _tetherContract.balanceOf(address(this)));
            } else {
                _tetherContract.transfer(to, amount);
            }
        }
    }

    function allow_to_mint(uint256 num) public onlyOwner {
        require(
            _current_to_mint + num <= _max_supply && _remaining_to_mint == 0,
            "max supply exceeds"
        );
        _remaining_to_mint = num;
    }

    function change_mint_state(MINT_STATUS newStatus) public onlyOwner {
        _mint_status = newStatus;
    }

    function get_member_info(address memberAddress) public view returns (Member memory) {
        return _member_info[memberAddress];
    }

    function change_uri(string memory newUri) public onlyOwner {
        _setURI(newUri);
    }

    function get_holders() public view returns (address[] memory) {
        return (_holders);
    }

    function get_stakers() public view returns (address[] memory, uint256[] memory) {
        address[] memory addresses = new address[](_staked_count);
        uint256[] memory stakedBlocks = new uint256[](_staked_count);
        uint256 index = 0;

        for (uint256 i = 1; i < _current_to_mint; i++) {
            address holderAddress = _staked_tokens[i];
            if (_member_info[holderAddress].is_stacked) {
                addresses[index] = holderAddress;
                stakedBlocks[index] = _member_info[holderAddress].stake_block_number;
                index++;
            }
        }
        return (addresses, stakedBlocks);
    }

    function get_tokens_by_address(address holder) public view returns (uint256[] memory) {
        uint256[] memory tokenIds = new uint256[](_member_info[holder].current_nft_count);
        address[] memory holders = _holders;
        uint256 index = 0;
        for (uint256 i = 0; i < holders.length; i++) {
            if (holders[i] == holder) {
                tokenIds[index] = (i + 1);
                index++;
            }
        }
        return (tokenIds);
    }

    function allow_list_(address[] memory addresses) public onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            if (_member_info[addresses[i]].memberAddress == address(0)) {
                Member memory newMember = Member(
                    addresses[i],
                    0,
                    0,
                    0,
                    0,
                    _base_allow_to_mint,
                    false,
                    false,
                    false
                );
                _member_info[addresses[i]] = newMember;
                _member_count += 1;
            }
            mint(addresses[i]);
        }
    }

    function allow_list(
        address[] memory addresses,
        uint256 mintmultiplier,
        uint256 mintcount,
        bool freemint,
        bool allowlist
    ) public onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            if (_member_info[addresses[i]].memberAddress == address(0)) {
                Member memory newMember = Member(
                    addresses[i],
                    mintmultiplier,
                    0,
                    0,
                    0,
                    mintcount,
                    allowlist,
                    false,
                    freemint
                );
                _member_info[addresses[i]] = newMember;
                _member_count += 1;
            } else {
                _member_info[addresses[i]].mintMultiplier = mintmultiplier;
                _member_info[addresses[i]].allow_to_mint_number = mintcount;
                _member_info[addresses[i]].is_allow_list = allowlist;
                _member_info[addresses[i]].free_mint = freemint;
            }
        }
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public override {
        require(
            from == _msgSender() || isApprovedForAll(from, _msgSender()),
            "ERC1155: caller is not owner nor approved"
        );
        require(_staked_tokens[id] == address(0), "this token is staked");
        _safeTransferFrom(from, to, id, amount, data);
        _holders[id - 1] = to;
        _member_info[from].current_nft_count -= 1;
        _member_info[to].current_nft_count += 1;
    }

    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public override {
        require(
            from == _msgSender() || isApprovedForAll(from, _msgSender()),
            "ERC1155: transfer caller is not owner nor approved"
        );
        require(false, "Batch transfer is not allowed");
        _safeBatchTransferFrom(from, to, ids, amounts, data);
    }
