// File: contracts/TokenBank.sol


pragma solidity ^0.8.20;






contract TokenBank is ERC20, AccessControl, Accounting {
    using SafeMath for uint256;

    bytes32 public constant WHITE_ROLE = keccak256("WHITE_ROLE");

    //Accounting private _accounting = new Accounting(tx.origin, totalSupply());

    constructor() ERC20("TokenBank", "TNK") {
     _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
     _grantRole(WHITE_ROLE, msg.sender);
     _mint(msg.sender, 10000000 * 10 ** decimals());
    }

    function addToWL(address m_account) public onlyRole(WHITE_ROLE) {
        _addToWL(m_account);
    }

    function removeFromWL(address m_account) public onlyRole(WHITE_ROLE) {
        _removeFromWL(m_account);
    }

    function getWL() public view returns (address[] memory) {
        return _getWL();
    }


function _beforeTokenTransfer(address from, address to, uint256 amount) internal view 
    {
    if(_checkInWL(to)) 
        {
        if(!super.checkAccount(from, balanceOf(from).div(100000).mul(250), amount)) revert("Wrong amount (more than 0.25% per 24h)");
        }
    }

    function _afterTokenTransfer(address from, address to, uint256 amount)
        internal
    {
        super.updateAccount(from, to, amount, balanceOf(from));
    }
}
