    "contracts/Summon.sol": {
      "content": "// Copyright (c) 2021-2022 MCH Co., Ltd.
pragma solidity ^0.8.4;

import "./IERC20Burnable.sol";
import "./ISoul.sol";
import "./Pausable.sol";

contract Summon is Pausable {
    IERC20Burnable public mchc;
    IERC20Burnable public rays;
    ISoul public soul;
    address public validator;
    address public mchcTransferTo;
    address public raysTransferTo;

    uint256 public requiredRaysAmount;
    uint256 public requiredMCHCAmount;

    event Summoned(uint256 tokenId, address owner);
    event ParametersSet(uint256 requiredRaysAmount, uint256 requiredMCHCAmount);
    event AddressesSet(address mchc, address rays, address soul, address validator, address mchcTransferTo, address raysTransferTo);

    function summon(uint256 tokenId, uint32 _chainId, uint8 v, bytes32 r, bytes32 s) external whenNotPaused {
        require(ecrecover(toEthSignedMessageHash(prepareMessage(tokenId, _chainId)), v, r, s) == validator, "Summon: invalid summon signature");

        if (requiredMCHCAmount > 0) {
            mchc.transferFrom(msg.sender, mchcTransferTo, requiredMCHCAmount);
        }
        rays.transferFrom(msg.sender, raysTransferTo, requiredRaysAmount);

        soul.mint(msg.sender, tokenId);

        emit Summoned(tokenId, msg.sender);
    }

    function prepareMessage(uint256 tokenId, uint32 _chainId) internal pure returns (bytes32) {
        return keccak256(abi.encode(tokenId, _chainId));
    }

    function toEthSignedMessageHash(bytes32 hash) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hash));
    }

    function setAddresses(address _mchc, address _rays, address _soul, address _validator, address _mchcTransferTo, address _raysTransferTo) external onlyOwner {
        mchc = IERC20Burnable(_mchc);
        rays = IERC20Burnable(_rays);
        soul = ISoul(_soul);
        validator = _validator;
        mchcTransferTo = _mchcTransferTo;
        raysTransferTo = _raysTransferTo;

        emit AddressesSet(_mchc, _rays, _soul, _validator, _mchcTransferTo, _raysTransferTo);
    }

    function setParameters(uint256 _requiredRaysAmount, uint256 _requiredMCHCAmount) external onlyOwner {
        requiredRaysAmount = _requiredRaysAmount;
        requiredMCHCAmount = _requiredMCHCAmount;

        emit ParametersSet(requiredRaysAmount, requiredMCHCAmount);
    }
}
"
