// File contracts/catchup/TowerCatchUp.sol

pragma solidity ^0.8.0;



interface IToken {
    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool);

    function transfer(address to, uint256 amount) external returns (bool);
}

contract TowerCatchUp is Pausable, Ownable {
    using ECDSA for bytes32;

    event CatchupDone(string _playerId, address indexed _msgSender, uint256 _catchupDays, uint256 _amount);
    event VerifierChanged(address _owner, address indexed _oldSigner, address indexed _newSigner);
    event TokenWithdrawn(address _receiver, uint256 _amount);

    address public verifier;
    IToken public tokenAddress;

    // total number of days done catchup for
    uint256 public totalCatchupCheckins = 0;

    struct CheckInEntry {
        string _playerId;
        uint256 _catchupDays;
        uint256 _amount;
        uint256 _timestamp;
    }

    mapping(address => uint256) public userNonce;

    // mapping for itemId and the number of such items purchased
    mapping(address => CheckInEntry[]) public catchupEntries;
    mapping(address => uint256) public catchupCounter;

    constructor(address _tokenAddress, address _verifier) {
        tokenAddress = IToken(_tokenAddress);
        verifier = _verifier;
    }

    function setVerifier(address _newVerifier) external onlyOwner {
        emit VerifierChanged(msg.sender, verifier, _newVerifier);
        verifier = _newVerifier;
    }

    function verifySignature(
        string memory _playerId,
        address _msgSender,
        uint256 _catchupDays,
        uint256 _amount,
        bytes memory _signature
    ) internal returns (bool) {
        uint256 nonce = userNonce[_msgSender];

        bytes32 hash = keccak256(abi.encodePacked(_playerId, _msgSender, _catchupDays, _amount, nonce));
        require(hash.toEthSignedMessageHash().recover(_signature) == verifier, "TOWERCatchup: invalid signature");
        userNonce[_msgSender]++;
        return true;
    }

    function checkInCatchup(
        string memory _playerId,
        uint256 _catchupDays,
        uint256 _amount,
        bytes memory _signature
    ) external {
        address msgSender = msg.sender;

        require(bytes(_playerId).length > 0, "TOWERCatchup: invalid id");
        require(_catchupDays > 0, "TOWERCatchup: invalid days");
        require(_amount > 0, "TOWERCatchup: invalid amount");

        require(verifySignature(_playerId, msgSender, _catchupDays, _amount, _signature));

        CheckInEntry[] storage checkInEntries = catchupEntries[msgSender];

        CheckInEntry memory checkInEntry = CheckInEntry(_playerId, _catchupDays, _amount, block.timestamp);
        catchupCounter[msgSender] += _catchupDays;
        checkInEntries.push(checkInEntry);
        totalCatchupCheckins += _catchupDays;

        IToken(tokenAddress).transferFrom(msgSender, address(this), _amount);
        emit CatchupDone(_playerId, msgSender, _catchupDays, _amount);
    }

    function withdrawToken(address _receiver, uint256 _amount) external onlyOwner {
        IToken(tokenAddress).transfer(_receiver, _amount);
        emit TokenWithdrawn(_receiver, _amount);
    }

    function pause() external onlyOwner whenNotPaused {
        _pause();
    }

    function unpause() external onlyOwner whenPaused {
        _unpause();
    }
}
