import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "base64-sol/base64.sol";
import "./MintSplitter.sol";

contract BannersSVGNFTContract is ERC721Enumerable, MintSplitter, Ownable {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;
    Counters.Counter private _bannersCount;

    // max mint of 25_000
    uint256 public constant limit = 25_000;
    // max 5 per transaction
    uint256 public constant maxPerTx = 5;
    // max 20 per wallet
    uint256 public constant maxPerWallet = 20;
    // max for admin
    uint256 public constant maxForAdmin = 1000;

    uint256[5] public prices = [
        0.015 ether,
        0.030 ether,
        0.045 ether,
        0.060 ether,
        0.075 ether
    ];

    uint private count = 0;

    // [ 0: number, 1: el1, 2: el2, 3: el3, 4: style, 5: colour, 6: hem, 7: f1, 8: f2, 9: s1, 10: s2, 11: s3, 12: s4 ]
    mapping(uint256 => string[13]) private banners;
    mapping(address => bool) public minted;

    event BannerMinted(
        uint256 indexed id,
        address indexed owner,
        uint256 timestamp
    );

    IERC20 public wETH;

    bool public adminMinted;
    mapping(address => uint256) private userMints;

    constructor(
        address _wETHAddress
    ) ERC721("Banners (for Adventurers)", "BNNRS") {
        wETH = IERC20(_wETHAddress);
        adminMinted = false;
    }

    function contractURI() public view returns (string memory) {
        return
            "https://bannersnft.mypinata.cloud/ipfs/QmU4AjpDbKCCSftoa7RwcvcN5VnNhFW3f6ftKoP6eQP89H";
    }

    function adminSetup(uint256 amtToMint, address to) public onlyOwner {
        require(amtToMint <= 100, "You can only mint 100 at a time");
        require(!adminMinted, "Admin has already minted");
        mintItem(amtToMint, to);
        count = count + amtToMint;
        if(count >= maxForAdmin) {
            adminMinted = true;
        }        
    }

    function adminSetup(address to) public onlyOwner {
        require(!adminMinted, "Admin has already minted");
        mintItem(100, to);
        count = count + 100;
        if(count >= maxForAdmin) {
            adminMinted = true;
        }        
    }

    /// @dev public mint function
    function mint(uint256 quantityToMint) public {
        requestMint(quantityToMint);
    }

    /// @dev mint for airdropping n nft tokens to the old holders
    function mintDrop(uint256 quantityToMint, address[] memory to) public onlyOwner {
        for(uint index = 0; index < to.length; index++) {
            mintItem(quantityToMint, to[index]);
        }   
    }

    /// @dev mint function
    function requestMint(uint256 amountToMint) internal {
        uint256 currentId = _tokenIds.current();
        require(
            currentId <= limit,
            "Done Minting Ser, check secondary markets."
        );
        require(currentId + amountToMint <= limit, "Not enough left to mint");
        require(
            0 < amountToMint && amountToMint <= maxPerTx,
            "You can only mint 5 at a time."
        );
        require(
            amountToMint <= (5 - userMints[msg.sender]),
            "You have minted your max per transaction"
        );
        require(
            amountToMint + userMints[msg.sender] <= maxPerWallet,
            "You cant mint that many this time, you will exceed the wallet max."
        );

        uint256 price = prices[amountToMint - 1];

        require(
            wETH.balanceOf(msg.sender) >= price,
            "Not enough ETH for mint."
        );
        require(
            wETH.allowance(msg.sender, address(this)) >= price,
            "Not enough ETH allowance to mint."
        );

        userMints[msg.sender] = userMints[msg.sender] + amountToMint;

        mintItem(amountToMint);

        _receiveAndSplit(wETH, price, msg.sender);
    }

    /// @dev internal mint function
    function mintItem(uint256 quantityToMint, address to) internal {
        for (uint256 i = 0; i < quantityToMint; i++) {
            _tokenIds.increment();

            uint256 id = _tokenIds.current();
            _safeMint(to, id);

            emit BannerMinted(id, to, block.timestamp);
        }
    }


    /// @dev internal mint function
    function mintItem(uint256 quantityToMint) internal {
        for (uint256 i = 0; i < quantityToMint; i++) {
            _tokenIds.increment();

            uint256 id = _tokenIds.current();
            _safeMint(msg.sender, id);

            emit BannerMinted(id, msg.sender, block.timestamp);
        }
    }

    function getIDstring(uint256 id) public view returns (string memory) {
        require(_exists(id), "not exist");

        return banners[id][0];
    }

    function getElements(uint256 id)
        public
        view
        returns (string[3] memory elements)
    {
        require(_exists(id), "not exist");

        elements[0] = banners[id][1];
        elements[1] = banners[id][2];
        elements[2] = banners[id][3];
    }

    function getColour(uint256 id) public view returns (string memory) {
        require(_exists(id), "not exist");

        return banners[id][5];
    }

    function getHem(uint256 id) public view returns (string memory) {
        require(_exists(id), "not exist");

        return banners[id][6];
    }

    function getStyle(uint256 id) public view returns (string memory) {
        require(_exists(id), "not exist");

        return banners[id][4];
    }

    /// @dev creates image based on id
    // todo: update the corner color to white FFFFFF
    function composeImage(uint256 id) private view returns (string memory) {
        string memory output = string(
            abi.encodePacked(
                '<svg width="720" height="720" viewBox="0 0 720 720" fill="none" xmlns="http://www.w3.org/2000/svg"><style type="text/css">.st1{fill:#FFFFFF;}.st2{fill:#FFFFFF;}.st3{font-family:"TimesNewRomanPSMT";}.st4{font-size:28px;}</style><g clip-path="url(#clip0_3_2)"><path d="M720.4 0.400024H0.399994V720.4H720.4V0.400024Z" fill="black"/><path d="M303.9 -0.400024H0V305.3L304.8 0.499976L303.9 -0.400024Z" fill="white"/><path d="M415.5 720.6H720.9V416.2L720.4 415.7L415.5 720.6Z" fill="white"/><text class="st2 st3 st4" text-anchor="middle" x="360" y="237.792">',
                banners[id][0],
                '</text><text class="st2 st3 st4" text-anchor="middle" x="360" y="305.792">',
                banners[id][1],
                '</text><text class="st2 st3 st4" text-anchor="middle" x="360" y="337.792">'
            )
        );

        output = string(
            abi.encodePacked(
                output,
                banners[id][2],
                '</text><text class="st2 st3 st4" text-anchor="middle" x="360" y="369.792">',
                banners[id][3],
                '</text><text class="st2 st3 st4" text-anchor="middle" x="360" y="431.792">',
                getColour(id),
                '</text><text class="st2 st3 st4" text-anchor="middle" x="360" y="463.792">'
            )
        );

        output = string(
            abi.encodePacked(
                output,
                getStyle(id),
                '</text><text class="st2 st3 st4" text-anchor="middle" x="360" y="499.792">',
                getHem(id),
                '</text></g><defs><clipPath id="clip0_3_2"><rect width="720" height="720" fill="white"/></clipPath></defs></svg>'
            )
        );

        return output;
    }

    /// @dev puts the attributes together based on the id
    function composeAttributes(uint256 id)
        private
        view
        returns (string memory)
    {
        string memory output = "";

        // populate element attribute
        output = string(
            abi.encodePacked(
                output,
                '{ "trait_type": "Element", "value": "',
                banners[id][1],
                '"},'
            )
        );
        output = string(
            abi.encodePacked(
                output,
                '{ "trait_type": "Element", "value": "',
                banners[id][2],
                '"},'
            )
        );
        output = string(
            abi.encodePacked(
                output,
                '{ "trait_type": "Element", "value": "',
                banners[id][3],
                '"},'
            )
        );

        // populate colour, style & finish attribute
        output = string(
            abi.encodePacked(
                output,
                '{ "trait_type": "Style", "value": "',
                getStyle(id),
                '"},'
            )
        );
        output = string(
            abi.encodePacked(
                output,
                '{ "trait_type": "Colour", "value": "',
                getColour(id),
                '"},'
            )
        );
        output = string(
            abi.encodePacked(
                output,
                '{ "trait_type": "Finish", "value": "',
                getHem(id),
                '"}'
            )
        );

        // populate family
        if (bytes(banners[id][7]).length > 0)
            output = string(
                abi.encodePacked(
                    output,
                    ', { "trait_type": "Family", "value": "',
                    banners[id][7],
                    '"}'
                )
            );
        if (bytes(banners[id][8]).length > 0)
            output = string(
                abi.encodePacked(
                    output,
                    ', { "trait_type": "Family", "value": "',
                    banners[id][8],
                    '"}'
                )
            );

        // populate Strength
        if (bytes(banners[id][9]).length > 0)
            output = string(
                abi.encodePacked(
                    output,
                    ', { "trait_type": "Strength", "value": "',
                    banners[id][9],
                    '"}'
                )
            );
        if (bytes(banners[id][10]).length > 0)
            output = string(
                abi.encodePacked(
                    output,
                    ', { "trait_type": "Strength", "value": "',
                    banners[id][10],
                    '"}'
                )
            );
        if (bytes(banners[id][11]).length > 0)
            output = string(
                abi.encodePacked(
                    output,
                    ', { "trait_type": "Strength", "value": "',
                    banners[id][11],
                    '"}'
                )
            );
        if (bytes(banners[id][12]).length > 0)
            output = string(
                abi.encodePacked(
                    output,
                    ', { "trait_type": "Strength", "value": "',
                    banners[id][12],
                    '"}'
                )
            );

        output = string(abi.encodePacked('"attributes": [', output, "],"));

        return output;
    }

    function tokenURI(uint256 id)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(_exists(id), "not exist");

        string memory outputImage = composeImage(id);
        string memory outputAttributes = composeAttributes(id);

        string memory json = Base64.encode(
            bytes(
                abi.encodePacked(
                    '{"name":"',
                    "Banner #",
                    banners[id][0],
                    '", "description": "25000 randomized text only NFTs. Society for Loot. Art for everyone. BannersNFT.com", "external_url":"https://bannersnft.com/token?id=',
                    banners[id][0],
                    '", ',
                    outputAttributes,
                    '"image": "data:image/svg+xml;base64,',
                    Base64.encode(bytes(outputImage)),
                    '"}'
                )
            )
        );

        string memory output = string(
            abi.encodePacked("data:application/json;base64,", json)
        );

        return output;
    }

    function populate(string[13][] memory _banners) public onlyOwner {
        for (uint256 i = 0; i < _banners.length; i++) {
            // do all the data population here
            _bannersCount.increment();

            uint256 id = _bannersCount.current();

            banners[id] = _banners[i];
        }
    }

    function withdraw() public onlyOwner {
        _withdraw(wETH);
    }
}
"
