    "contracts/MintSplitter.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract MintSplitter {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    // Mint distro percentages
    uint256 private banners = 83; // 83% or the rest...
    uint256 private lootDao = 5; // 5%
    uint256 private ghost = 4; // 4%
    uint256 private jax = 4; // 4%
    uint256 private fahim = 2; // 2%
    uint256 private divineDao = 2; // 2%

    // Mint distro addresses
    address private divineDaoAddy = 0x5C504C18A476f26c6ddc36cCe344a2d6e4A8a2bc; // Divine DAO War Chest
    address private lootDaoAddy = 0x8cFDF9E9f7EA8c0871025318407A6f1Fbc5d5a18; // Loot DAO War Chest
    address private ghostAddy = 0x5C37b9f5324bAFB44E10e35E9cB035425F24Ee4E; // Ghost address
    address private jaxAddy = 0xE6995fDB691aad061e45df31A7C407792A52565A; // Banners Chest
    address private fahimAddy = 0x76ac4a517DE5Cc1AE380ffbE84e8e6629aED1C5a; // Fahim address
    address private bannersWarChestAddy =
        0xA9EC89f38a1dC912F1726EE66409a840fFc6a709; // Ledger address for Banners

    /// @dev allows withdraw of any tokens getting stuck in contract
    function _withdraw(IERC20 wETH) internal {
        uint256 balance = wETH.balanceOf(address(this));

        require(balance > 0, "Nothing to withdraw");

        _receiveAndSplit(wETH, balance, address(this));
    }

    function _receiveAndSplit(
        IERC20 wETH,
        uint256 amountToSplit,
        address from
    ) internal {
        uint256 amountForGhost = uint256(amountToSplit.div(100).mul(ghost));
        uint256 amountForJax = uint256(amountToSplit.div(100).mul(jax));
        uint256 amountForFahim = uint256(amountToSplit.div(100).mul(fahim));
        uint256 amountForDivine = uint256(
            amountToSplit.div(100).mul(divineDao)
        );
        uint256 amountForLoot = uint256(amountToSplit.div(100).mul(lootDao));
        uint256 amountForBanners = uint256(amountToSplit.div(100).mul(banners));

        wETH.safeTransferFrom(from, ghostAddy, amountForGhost);
        wETH.safeTransferFrom(from, jaxAddy, amountForJax);
        wETH.safeTransferFrom(from, fahimAddy, amountForFahim);
        wETH.safeTransferFrom(from, divineDaoAddy, amountForDivine);
        wETH.safeTransferFrom(from, lootDaoAddy, amountForLoot);
        wETH.safeTransferFrom(from, bannersWarChestAddy, amountForBanners);
    }
}
"
    },
