/**
*   For StableR INRS Stablecoin - Know more about the project at StableR.in/links
*
*   This contract will make the StableR INRS as Stablecoin by pegging the value to Indian Rupee
*
*   It makes 1 INRS = 1 INR always.
*
*   This contract will  get the value of Indian Rupee via Chainlink oracle.
*
*   Using this contract, Users can exchange USDC to StableR INRS and vice versa.
*  
*   The values of INR/USDC pair price feed via Chain.link - AggregatorV3Interface.sol using INR/USD & USDC/USD pair price feeds.
*/



pragma solidity ^0.8.0;





contract StableR_INRS_USDC_Exchange {

    address payable public owner;
      

    event Transfer(address indexed from, address indexed to, uint256 value);

    mapping(address => uint) public balances;

 
    address public INRS;
    address public USDC;

    mapping (address => uint) public tokenBalances;
    mapping(address => uint) public balanceOf;
 
    mapping(address => bool) public isApproved;
    mapping(address => bool) internal whitelisted;
    
    
 

    
    bool public paused;

    AggregatorV3Interface internal inrUsdFeed;


  
    constructor()  {
        owner = payable (msg.sender);
         INRS = address(0xadA9C4D142B5e8A1e269B9546906804Cb934BD0D);
          USDC = address(0x2791Bca1f2de4661ED88A30C99A7a9449Aa84174);
                 /**
            *  Chain.link Price Aggregator
            * Network: Polygon Mainnet Network
            * Aggregator: INR/USD
            * Address: 0xDA0F8Df6F5dB15b346f4B8D1156722027E194E60
           
         */
           
         inrUsdFeed = AggregatorV3Interface(0xDA0F8Df6F5dB15b346f4B8D1156722027E194E60);



    }

    


    /**
     * Returns the latest INR/USD price.
     */

         function getINRUSDPrice() public view returns (uint) {
        (
            /* uint80 roundID */,
            int inrUsdprice,
            /*uint startedAt*/,
            /*uint timeStamp*/,
            /*uint80 answeredInRound*/
        ) = inrUsdFeed.latestRoundData();

       
        return uint(inrUsdprice);
    }
    
 

 
    

    function calculateUSDCForINRS (uint256 _INRSAmount) public view returns(int){

    require(!paused, "Contract is paused!");

    require(_INRSAmount > 0, "Input Amount must be greater than 0!");

    uint  INRUSDPrice =  getINRUSDPrice();

    require(INRUSDPrice > 0, "Exchange Rate Not Available at the moment. contact stabler.in/links !");

    uint256 USDCAmount = ((_INRSAmount  * INRUSDPrice) / 10 ** 8)  ;

    return int(USDCAmount);
    }



    function calculateINRSForUSDC (uint256 _USDCAmount) public view returns(int){

    require(!paused, "Contract is paused!");

    require(_USDCAmount > 0, "Input Amount must be greater than 0!");

    uint  INRUSDPrice =  getINRUSDPrice();

    require(INRUSDPrice > 0, "Exchange Rate Not Available at the moment. contact stabler.in/links !");

    uint256 INRSAmount = ((_USDCAmount * 10 ** 8) / INRUSDPrice);

    return int(INRSAmount);
    }



    function buyINRS(uint256 _INRSAmount) public {
         require(!paused, "Contract is paused!");
         address _sender = address (msg.sender);

         uint  INRUSDPrice =  getINRUSDPrice();
       
        require(_INRSAmount > 0, "Input Amount must be greater than 0!");

        require(INRUSDPrice > 0, "Exchange Rate Not Available at the moment. contact stabler.in/links !");
         
          require(IERC20(INRS).balanceOf(address(this)) >= _INRSAmount, " Contract INRS balance is Insufficient. Unable to proceed!");


             uint256 _USDCAmount = ((_INRSAmount  * INRUSDPrice)  / 10 ** 20) ;

              require(IERC20(USDC).balanceOf(_sender) >= _USDCAmount, "Your Account have Insufficient USDC balance to buy INRS!");

         require(IERC20(USDC).transferFrom(_sender, address(this), _USDCAmount), "Failed to Transfer USDC from your address to StableR_INRS_USDC_Exchange contract!" );

        require(IERC20(INRS).transfer(_sender, _INRSAmount), "Failed to Transfer INRS from StableR_INRS_USDC_Exchange contract to your address!");
     
       
       
    }

    function sellINRS(uint256 _INRSAmount) public {
         require(!paused, "Contract is paused!");
         address _sender = address (msg.sender);

         uint  INRUSDPrice =  getINRUSDPrice();

             require(INRUSDPrice > 0, "Exchange Rate Not Available at the moment. contact stabler.in/links !");

       
        require(_INRSAmount > 0, "Input Amount must be greater than 0");

          require(IERC20(INRS).balanceOf(_sender) >= _INRSAmount, "Your Account have Insufficient INRS balance");
       

            

        uint256 _USDCAmount = ((_INRSAmount  * INRUSDPrice)  / 10 ** 20) ;

           require(IERC20(USDC).balanceOf(address(this)) >= _USDCAmount, " USDC balance of the Exchange contract is Insufficient. Unable to proceed!");
        
        require(IERC20(INRS).transferFrom(_sender, address(this), _INRSAmount), "Failed to Transfer INRS from your address  to StableR_INRS_USDC_Exchange contract!");

        require(IERC20(USDC).transfer(_sender, _USDCAmount), "Failed to Transfer USDC from StableR_INRS_USDC_Exchange contract to your address!");

     
    }

        function depositMatic() public payable {
         require(!paused, "Contract is paused!");
        require(msg.value > 0, "Input Amount must be greater than 0!");    
        balanceOf[address(this)] += msg.value;
    }

        function depositMaticAmount(uint _amount) public payable {
         require(!paused, "Contract is paused!");
        require(msg.value > 0, "Input Amount must be greater than 0!");    
        balanceOf[address(this)] += _amount;
    }
    
    
            function DepositUSDC(uint256 _USDCAmount) public {
            require(!paused, "Contract is paused!");
            address _sender = address (msg.sender);
       
            require(_USDCAmount > 0, "Input Amount must be greater than 0!");
            require(IERC20(USDC).balanceOf(_sender) >= _USDCAmount, "Insufficient USDC balance to buy!");
         
            require(IERC20(USDC).transferFrom(_sender, address(this), _USDCAmount), "Failed to Transfer USDC from your address to StableR_INRS_USDC_Exchange contract!" );


}

            function DepositINRS(uint256 _INRSAmount) public {
            require(!paused, "Contract is paused!");
            address _sender = address (msg.sender);
       
            require(_INRSAmount > 0, "Input Amount must be greater than 0!");
            require(IERC20(INRS).balanceOf(_sender) >= _INRSAmount, "Insufficient INRS balance to buy!");
         
            require(IERC20(INRS).transferFrom(_sender, address(this), _INRSAmount),  "Failed to Transfer INRS from your address  to StableR_INRS_USDC_Exchange contract!");


}
        function withdrawERC20(address _token, uint256 _amount) public  {
        require(msg.sender == owner, "Only owner can withdraw!");
        require(_token != address(0), "Invalid token address!");

        require(IERC20(_token).balanceOf(address(this)) >= _amount, "Insufficient balance!");
    
        require(IERC20(_token).transfer(msg.sender, _amount), "Transfer failed.!");
    }
        
        
        function withdrawINRS(uint256 _amount) public  {
        require(msg.sender == owner, "Only owner can withdraw!");
   
        require(IERC20(INRS).balanceOf(address(this)) >= _amount, "Insufficient balance!");
    
        require(IERC20(INRS).transfer(msg.sender, _amount),  "Failed to Withdraw INRS from StableR_INRS_USDC_Exchange contract to your address!");
    }
    

        function withdrawUSDC(uint256 _amount) public  {
        require(msg.sender == owner, "Only owner can withdraw!");
   
        require(IERC20(USDC).balanceOf(address(this)) >= _amount, "Insufficient balance!");
    
        require(IERC20(USDC).transfer(msg.sender, _amount), "Failed to Transfer USDC from StableR_INRS_USDC_Exchange contract to your address!");
    }


        function withdrawMatic(uint256 _amount) public payable {
        require(msg.sender == owner, "Only owner can withdraw!");

        require(_amount <= tokenBalances[address(this)], "Insufficient balance!");

        payable(msg.sender).transfer(_amount);
        balanceOf[address(this)] -= _amount;
       
    }



     
    fallback() external payable {
    require(!paused, "Contract is paused!"); 
    balanceOf[address(this)] += msg.value;
    }

    receive() external payable {
        require(!paused, "Contract is paused!"); 
       
        balanceOf[address(this)] += msg.value;


    }
function MaticBalance() public view returns (uint) {
    return address(this).balance;
}

function INRSBalance() public view returns (uint) {
    return IERC20(INRS).balanceOf(address(this));
}

function DUSDBalance() public view returns (uint) {
    return IERC20(USDC).balanceOf(address(this));
}

function OtherTokenBalance(address _token) public view returns (uint) {
    require(_token != address(0), "Invalid token address!");
    return IERC20(_token).balanceOf(address(this));
}

    function addWhitelist(address _address) public{
    require(msg.sender == owner, "Only the current owner can add address to whitelist!");
    whitelisted[_address] = true;
    }
    
    function approveOtherTokens(address token) public {
        require(msg.sender == owner);
        isApproved[token] = true;
    }

    function changeOwner(address newOwner) public {
    require(whitelisted[newOwner], "Address not whitelisted!");
    require(msg.sender == owner, "Only the current owner can change the contract owner!");
    owner = payable (newOwner);
    }
    /**
     * Modifier to restrict function access to the owner
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "Unauthorized access!");
        _;
    }
           function pause() public {
        require(msg.sender == owner);
        paused = true;
    }

    function unpause() public {
        require(msg.sender == owner);
        paused = false;
    }
}
