    "contracts/WarriorGame.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.15;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";

contract WarriorGame is Ownable {
    struct Project {
        uint256 count;
        uint256 endTimestamp;
    }

    mapping(address => uint256) public whitelist; // End Timestamp
    address[] public allowedProjects;

    mapping(address => Project) private projects;

    function setWhitelist(
        address[] calldata addresses_,
        uint256 days_
    ) external onlyOwner {
        for (uint256 i = 0; i < addresses_.length; ) {
            whitelist[addresses_[i]] = block.timestamp + days_ * 86400;
            unchecked {
                ++i;
            }
        }
    }

    function setProjects(
        address[] calldata projects_,
        uint256[] calldata count_,
        uint256[] calldata days_
    ) external onlyOwner {
        require(projects_.length == count_.length);
        require(projects_.length == days_.length);

        allowedProjects = projects_;

        for (uint256 i = 0; i < projects_.length; ) {
            Project memory project = Project({
                count: count_[i],
                endTimestamp: block.timestamp + days_[i] * 86400
            });

            projects[allowedProjects[i]] = project;

            unchecked {
                ++i;
            }
        }
    }

    function hasAccess(address _address) external view returns (bool) {
        uint256 endTimestamp = whitelist[_address];
        if (endTimestamp > block.timestamp) return true;

        for (uint256 i = 0; i < allowedProjects.length; i++) {
            Project memory project = projects[allowedProjects[i]];
            uint256 balance = IERC721(allowedProjects[i]).balanceOf(_address);
            if (project.count > balance) continue;
            if (project.endTimestamp > block.timestamp) return true;
        }

        return false;
    }
}
