    "contracts/FactoryOfLazyERC1155.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.17;

import "./LazyERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract FactoryOfLazyERC1155 is Ownable {
    address public controlAddress;

    uint96 public defaultRoyalty = 1000;

    mapping(string => address) public postToAddress;

    event CreateBaseERC1155(address indexed _address);

    constructor(address _controlAddress) {
        controlAddress = _controlAddress;
    }

    modifier onlyController() {
        require(
            IWhitelist(controlAddress).isWhitelist(msg.sender) == true,
            "Only Controller"
        );
        _;
    }

    function mint(
        string memory postId,
        string memory name,
        string memory symbol,
        string memory baseUri,
        bool isSBT,
        address newOwner,
        address receiver,
        uint256 tokenId,
        uint256 mintAmount
    ) external onlyController returns (address) {
        address nft1155 = getOrCreateNFT(postId, name, symbol, baseUri, isSBT, newOwner);
        LazyERC1155(nft1155).mint(receiver,mintAmount,tokenId);
        return nft1155;
    }

    function setBaseURI(string memory postId,uint256 tokenId, string memory baseUri) external onlyController {
        address nft1155 = postToAddress[postId];
        require(nft1155 != address(0), "NFT1155 not found");
        LazyERC1155(nft1155).setBaseURI(tokenId, baseUri);
    }

    function setIsSBT(string memory postId, bool isSBT) external onlyController {
        address nft1155 = postToAddress[postId];
        require(nft1155 != address(0), "NFT1155 not found");
        LazyERC1155(nft1155).setIsSBT(isSBT);
    }

    function setDefaultRoyalty(string memory postId,address _receiver, uint96 _defaultRoyalty) external onlyController {
        address nft1155 = postToAddress[postId];
        require(nft1155 != address(0), "NFT1155 not found");
        LazyERC1155(nft1155).setDefaultRoyalty(_receiver,_defaultRoyalty);
    }

    function getOrCreateNFT(
        string memory postId,
        string memory name,
        string memory symbol,
        string memory baseUri,
        bool isSBT,
        address newOwner
    ) internal returns (address) {
        address nft1155 = postToAddress[postId];
        if (nft1155 == address(0)) {
            nft1155 = address(new LazyERC1155(name, symbol, baseUri, isSBT, newOwner, controlAddress, defaultRoyalty));
            emit CreateBaseERC1155(nft1155);
            postToAddress[postId] = nft1155;
        }
        return nft1155;
    }

    function setPostToAddress(string memory postId, address nft721) external onlyController {
        postToAddress[postId] = nft721;
    }

    function setBaseDefaultRoyalty(uint96 _defaultRoyalty) external onlyController {
        defaultRoyalty = _defaultRoyalty;
    }
}
"
