    "contracts/LazyERC1155.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Base64.sol";

interface IWhitelist {
    function isWhitelist(address _address) external view returns (bool);
}

contract LazyERC1155 is ERC1155, Ownable, ERC2981 {
    //
    //VARIANTS
    //
    string public name;

    string public symbol;

    address public whitelist;

    bool public isSBT;

    mapping(uint256 => string) public baseURI;

    //
    //MAPPING
    //
    //@param totalSupply: total supply of tokenId
    mapping(uint256 => uint256) public totalSupply;

    //
    //CONSTRUCTOR
    //
    constructor(
        string memory _name,
        string memory _symbol,
        string memory _baseUri,
        bool _isSBT,
        address _newOwner,
        address _whitelistAddress,
        uint96 _defaultRoyalty
    ) ERC1155("") {
        //コントラクトの名前の設定
        name = _name;

        //コントラクトのシンボルの設定
        symbol = _symbol;

        //コントラクトのSBTフラグの設定
        isSBT = _isSBT;

        //コントラクトのBaseURIの設定
        baseURI[1] = _baseUri;

        //ホワイトリストの設定
        whitelist = _whitelistAddress;

        //オーナー権限の移行
        _transferOwnership(_newOwner);

        //ロイヤリティの設定
        _setDefaultRoyalty(_newOwner, _defaultRoyalty);
    }

    //
    //MODIFIER
    //
    modifier onlyController() {
        require(
            IWhitelist(whitelist).isWhitelist(msg.sender) == true,
            "Only Controller"
        );
        _;
    }

    //
    //MAIN
    //
    function mint(
        address _receiver,
        uint256 _mintAmount,
        uint256 _tokenid
    ) external onlyController {
        totalSupply[_tokenid] += _mintAmount;

        //interaction
