    "contracts/strategies/Gravity/StrategyGFI.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../Common/StratFeeManager.sol";

import "../../interfaces/gravity/IGovernanceV2.sol";

import "../../interfaces/gravity/IUniswapRouterV2.sol";

contract StrategyGFI is StratFeeManager {
    using SafeERC20 for IERC20;

    // Tokens used
    address public want;

    address public gfiGov;
    address public gfiRouter;

    address public weth;

    bool public harvestOnDeposit;
    uint256 public lastHarvest;

    event StratHarvest(
        address indexed harvester,
        uint256 wantHarvested,
        uint256 tvl
    );
    event Deposit(uint256 tvl);
    event Withdraw(uint256 tvl);

    event ChargedFees(
        uint256 callFees,
        uint256 gravityFees,
        uint256 strategistFees
    );

    event ChargedHarvestFees(uint256 managerFee, uint256 partnerFee);
    event ChargedWithdrawalFees(uint256 managerFee, uint256 partnerFee);

    constructor(
        address _want,
        address _weth,
        address _gov,
        address _router,
        CommonAddresses memory _commonAddresses
    ) StratFeeManager(_commonAddresses) {
        want = _want;

        weth = _weth;
        gfiGov = _gov;
        gfiRouter = _router;
    }

    // puts the funds to work
    function deposit() public whenNotPaused {
        emit Deposit(balanceOf());
    }

    function withdraw(uint256 _amount) external {
        require(msg.sender == vault, "!vault");

        uint256 wantBal = balanceOfWant();

        if (wantBal > _amount) {
            wantBal = _amount;
        }

        if (tx.origin != owner() && !paused()) {
            uint256 withdrawalFeeAmount = (wantBal *
                getWithdrawalFee(tx.origin)) / WITHDRAWAL_MAX;
            if (withdrawalFeeAmount > 0) {
                wantBal = wantBal - withdrawalFeeAmount;
                chargeWithdrawalFees(withdrawalFeeAmount);
            }
        }

        IERC20(want).safeTransfer(vault, wantBal);

        emit Withdraw(balanceOf());
    }

    function chargeWithdrawalFees(uint256 _feeAmount) internal {
        uint256 partnerFee;
        uint256 managerFee;

        if (withdrawalFees.managerFee > 0) {
            managerFee =
                (_feeAmount * withdrawalFees.managerFee) /
                FULL_PERCENT;

            IERC20(want).safeTransfer(managerReceiver, managerFee);
        }

        if (withdrawalFees.partnerFee > 0) {
            partnerFee =
                (_feeAmount * withdrawalFees.partnerFee) /
                FULL_PERCENT;

            IERC20(want).safeTransfer(partnerReceiver, partnerFee);
        }

        emit ChargedWithdrawalFees(managerFee, partnerFee);
    }

    function beforeDeposit() external virtual override {
        if (harvestOnDeposit) {
            require(msg.sender == vault, "!vault");
            _harvest(tx.origin);
        }
    }

    function harvest() external virtual {
        _harvest(tx.origin);
    }

    function harvest(address callFeeRecipient) external virtual {
        _harvest(callFeeRecipient);
    }

    function managerHarvest() external onlyManager {
        _harvest(tx.origin);
    }

    // compounds earnings and charges performance fee
    function _harvest(address callFeeRecipient) internal whenNotPaused {
        uint256 initWethBal = IERC20(weth).balanceOf(address(this));

        if (IERC20(want).balanceOf(address(this)) > 0) {
            IGovernanceV2(gfiGov).claimFee();

            uint256 wethBalAfterHarvested = IERC20(weth).balanceOf(
                address(this)
            );

            if (wethBalAfterHarvested > 0) {
                uint256 wethHarvested = wethBalAfterHarvested - initWethBal;
                if (wethHarvested > 0) {
                    chargeFees(callFeeRecipient, wethHarvested);
                }
                uint256 before = balanceOfWant();
                compound();
                uint256 wantHarvested = balanceOfWant() - before;

                lastHarvest = block.timestamp;
                emit StratHarvest(msg.sender, wantHarvested, balanceOf());
            }
        }
    }

    // performance fees
    function chargeFees(
        address callFeeRecipient,
        uint256 wethHarvested
    ) internal {
        uint256 managerFeeAmount;
        uint256 partnerAmount;

        if (harvestFees.partnerFee > 0) {
            partnerAmount =
                (wethHarvested * harvestFees.partnerFee) /
                FULL_PERCENT;
            IERC20(weth).safeTransfer(partnerReceiver, partnerAmount);
        }
        if (harvestFees.managerFee > 0) {
            managerFeeAmount =
                (wethHarvested * harvestFees.managerFee) /
                FULL_PERCENT;
            IERC20(weth).safeTransfer(managerReceiver, managerFeeAmount);
        }

        emit ChargedHarvestFees(managerFeeAmount, partnerAmount);
    }

    // buy more GFI with the weth earned as fees
    function compound() internal {
        uint256 wethBal = IERC20(weth).balanceOf(address(this));
        if (wethBal > 0) {
            address[] memory path = new address[](2);
            path[0] = weth;
            path[1] = want;

            IERC20(weth).approve(gfiRouter, wethBal);

            IUniswapV2Router02(gfiRouter)
                .swapExactTokensForTokensSupportingFeeOnTransferTokens(
                    wethBal,
                    0,
                    path,
                    address(this),
                    block.timestamp
                );
        }
    }

    // calculate the total underlaying 'want' held by the strat.
    function balanceOf() public view returns (uint256) {
        return balanceOfWant() + balanceOfPool();
    }

    // it calculates how much 'want' this contract holds.
    function balanceOfWant() public view returns (uint256) {
        return IERC20(want).balanceOf(address(this));
    }

    // it calculates how much 'want' the strategy has working in the farm.
    function balanceOfPool() public pure returns (uint256) {
        return 0;
    }

    // returns rewards unharvested
    function rewardsAvailable() public view returns (uint256) {
        return IGovernanceV2(gfiGov).pendingEarnings(address(this));
    }

    function callReward() public view returns (uint256) {
        IFeeConfig.FeeCategory memory fees = getFees();
        uint256 wethBal = rewardsAvailable();

        return (((wethBal * fees.total) / DIVISOR) * fees.call) / DIVISOR;
    }

    function setHarvestOnDeposit(bool _harvestOnDeposit) external onlyManager {
        harvestOnDeposit = _harvestOnDeposit;
    }

    // called as part of strat migration. Transfers all want, MVLP, esMVX and MP to new strat.
    function retireStrat() external {
        require(msg.sender == vault, "!vault");

        uint256 wantBal = IERC20(want).balanceOf(address(this));
        IERC20(want).transfer(vault, wantBal);
    }

    // pauses deposits and withdraws all funds from third party systems.
    function panic() public onlyManager {
        pause();
    }

    function pause() public onlyManager {
        _pause();
    }

    function unpause() external onlyManager {
        _unpause();
    }

    function setWant(address _want) external onlyManager {
        want = _want;
    }

    function setWETH(address _weth) external onlyManager {
        weth = _weth;
    }

    function setGFIGov(address _gfiGov) external onlyManager {
        gfiGov = _gfiGov;
    }

    function setGFIRouterV2(address _router) external onlyManager {
        gfiRouter = _router;
    }
}
