    "contracts/Collection.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol"; //NFT
import "@openzeppelin/contracts/token/common/ERC2981.sol"; //Royalties
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./ICollection.sol";
import "./IReveal.sol";
import "./CollectionRevealer.sol";
import "./Permissions.sol";

contract Collection is ERC721, ERC2981, ICollection, ReentrancyGuard, IReveal {
  uint256 public itemsCreated;

  Product[] private _products;
  mapping(uint256 => Item) private _items;
  mapping(address => mapping(uint256 => string)) private customItemURIs;

  Permissions public permissions;
  CollectionRevealer public collectionRevealer;

  bytes32 public constant PRODUCT_MANAGER = keccak256("PRODUCT_MANAGER");
  bytes32 public constant ROYALTY_MANAGER = keccak256("ROYALTY_MANAGER");
  bytes32 public constant PRICE_MANAGER = keccak256("PRICE_MANAGER");
  bytes32 public constant MINTER = keccak256("MINTER");
  bytes32 public constant METADATA_MANAGER = keccak256("METADATA_MANAGER");
  bytes32 public constant STOCK_MANAGER = keccak256("STOCK_MANAGER");

  event PermissionsChanged(
    address indexed sender,
    address indexed oldAddress,
    address indexed newAddress
  );
  event CollectionRevealerSet(
    address indexed sender,
    address indexed oldAddress,
    address indexed newAddress
  );
  event NewProduct(uint256 indexed productId);
  event PriceUpdated(
    uint256 indexed productId,
    uint256 oldPrice,
    uint256 newPrice
  );
  event RevealPrepared(uint256 indexed productId, string URI);
  event Reveal(uint256 indexed productId, uint256 randomness);
  event AddProductMetadata(uint256 indexed productId, string URI);
  event AddItemMetadata(uint256 indexed itemId, string URI);
  event AddCustomItemMetadata(uint256 indexed itemId, string URI);
  event StockChanged(
    uint256 indexed productId,
    uint256 oldStock,
    uint256 stock
  );
  event DefaultRoyaltySet(
    address indexed sender,
    address indexed reeceiver,
    uint96 feeNumerator
  );
  event DefaultRoyaltyDeleted(address indexed sender);
  event TokenRoyaltySet(
    address indexed sender,
    uint256 indexed tokenId,
    address receiver
  );
  event TokenRoyaltyReset(address indexed sender, uint256 indexed tokenId);

  constructor(
    string memory name,
    string memory symbol,
    address collectionRevealerAddress,
    address permissionsAddress
  ) ERC721(name, symbol) {
    permissions = Permissions(permissionsAddress);
    collectionRevealer = CollectionRevealer(collectionRevealerAddress);
  }

  function setPermissions(
    address permissionsAddress
  ) external hasAccess(permissions.DEFAULT_ADMIN_ROLE()) {
    address oldAddress = address(permissions);
    permissions = Permissions(permissionsAddress);
    emit PermissionsChanged(msg.sender, oldAddress, permissionsAddress);
  }

  function setCollectionRevealer(
    address collectionRevealerAddress
  ) external hasAccess(permissions.DEFAULT_ADMIN_ROLE()) {
    address oldAddress = address(collectionRevealer);
    collectionRevealer = CollectionRevealer(collectionRevealerAddress);
    emit CollectionRevealerSet(
      msg.sender,
      oldAddress,
      collectionRevealerAddress
    );
  }

  modifier validProductId(uint256 id) {
    require(id >= 1 && id <= _products.length, "INVALID_PRODUCT_ID");
    _;
  }

  modifier validItemId(uint256 id) {
    require(id >= 1 && id <= itemsCreated, "INVALID_ITEM_ID");
    _;
  }

  modifier hasAccess(bytes32 access) {
    require(
      permissions.addressHasAccess(access, msg.sender),
      "UNAUTHORIZED_ACCESS"
    );
    _;
  }

  function getAllProducts() external view returns (Product[] memory) {
    return _products;
  }

  function products(
    uint256 productId
  ) external view validProductId(productId) returns (Product memory) {
    return _products[productId - 1];
  }

  function getProducts(
    uint256 startProductId,
    uint256 endProductId
  ) external view returns (Product[] memory) {
    require(startProductId > 0, "INVALID_PRODUCT_ID");
    require(startProductId <= endProductId, "INVALID_PRODUCT_ID");

    endProductId = endProductId > _products.length
      ? _products.length
      : endProductId;

    Product[] memory result = new Product[](endProductId - startProductId + 1);

    for (uint256 i = startProductId; i <= endProductId; i++) {
      result[i - startProductId] = (_products[i - 1]);
    }

    return result;
  }

  function items(
    uint256 itemId
  ) external view validItemId(itemId) returns (Item memory) {
    return _items[itemId];
  }

  function newProduct(
    uint256 stock,
    bool forSale,
    uint256 price,
    string memory URI
  ) external hasAccess(PRODUCT_MANAGER) returns (uint256 productId) {
    productId = _products.length + 1;
    Product memory product;
    product.id = productId;
    product.forSale = forSale;
    product.URI = URI;
    product.stockLimit = stock;
    product.price = price;
    _products.push(product);

    emit NewProduct(productId);
  }

  function updatePrice(
    uint256 productId,
    uint256 newPrice
  ) external hasAccess(PRICE_MANAGER) validProductId(productId) {
    Product storage product = _products[productId - 1];
    uint256 oldPrice = product.price;
    product.price = newPrice;
    emit PriceUpdated(productId, oldPrice, newPrice);
  }

  function tokenURI(
    uint256 itemId
  ) public view virtual override validItemId(itemId) returns (string memory) {
    Item storage item = _items[itemId];
    Product storage product = _products[item.productId - 1];

    if (bytes(item.URI).length > 0) {
      return item.URI;
    } else {
      if (product.revealed) {
        unchecked {
          uint256 tokenRevealedIndex = ((product.seed + (itemId - 1)) %
            product.stockLimit) + 1;

          return
            string(
              abi.encodePacked(
                product.URI,
                "/",
                Strings.toString(tokenRevealedIndex),
                ".json"
              )
            );
        }
      } else {
        return product.URI;
      }
    }
  }

  function mint(
    address to,
    uint256 productId,
    uint256 amount
  ) external returns (uint256[] memory) {
    //This call is secure, we don't need any checks in mint
    return mintWithItemMetadata(to, productId, amount, "");
  }

  function mintWithItemMetadata(
    address to,
    uint256 productId,
    uint256 amount,
    string memory URI
  )
    public
    hasAccess(MINTER)
    validProductId(productId)
    nonReentrant
    returns (uint256[] memory)
  {
    require(amount > 0, "ERROR_AMOUNT_ZERO");
    Product storage product = _products[productId - 1];
    require(product.id != 0, "ERROR_PRODUCT_NOT_EXISTS");

    if (product.stockLimit > 0) {
      require(
        product.stockLimit >= product.mintedItems + amount,
        "ERROR_NO_STOCK"
      );
    }

    uint256[] memory mintedItems = new uint256[](amount);
    uint256 index;
    while (amount > 0) {
      amount--;
      product.mintedItems++;
      uint256 itemId = ++itemsCreated;
      mintedItems[index++] = itemId;
      Item storage item = _items[itemId];
      item.id = itemId;
      item.URI = URI;
      item.productId = productId;
      _safeMint(to, itemId);
    }

    return mintedItems;
  }

  function prepareReveal(
    uint256 productId,
    string memory URI
  ) external override validProductId(productId) {
    require(
      msg.sender == address(collectionRevealer),
      "The reveal can only be triggered by the reveal contract"
    );
    require(revealAllowed(productId), "Reveal is not allowed");
    _products[productId - 1].URI = URI;
    emit RevealPrepared(productId, URI);
  }

  function revealAllowed(
    uint256 productId
  ) public view override returns (bool) {
    return !_products[productId - 1].revealed;
  }

  function reveal(uint256 productId, uint256 randomness) external override {
    require(
      msg.sender == address(collectionRevealer),
      "The reveal can only be triggered by the reveal contract"
    );
    Product storage product = _products[productId - 1];
    product.revealed = true;
    product.seed = randomness;
    emit Reveal(productId, randomness);
  }

  function addProductMetadata(
    uint256 productId,
    string memory URI
  ) external hasAccess(METADATA_MANAGER) validProductId(productId) {
    _products[productId - 1].additionalURIs.push(URI);
    emit AddProductMetadata(productId, URI);
  }

  function addItemMetadata(
    uint256 itemId,
    string memory URI
  ) external hasAccess(METADATA_MANAGER) {
    _items[itemId].additionalURIs.push(URI);
    emit AddItemMetadata(itemId, URI);
  }

  function updateCustomMetadata(uint256 itemId, string memory URI) external {
    customItemURIs[msg.sender][itemId] = URI;
    emit AddCustomItemMetadata(itemId, URI);
  }

  function getCustomMetadata(
    address creator,
    uint256 itemId
  ) external view returns (string memory) {
    return customItemURIs[creator][itemId];
  }

  function setStock(
    uint256 productId,
    uint256 stock
  ) public hasAccess(STOCK_MANAGER) validProductId(productId) {
    Product storage product = _products[productId - 1];
    uint256 oldStock = product.stockLimit;
    product.stockLimit = stock;
    emit StockChanged(productId, oldStock, stock);
  }

  function addStock(
    uint256 productId,
    uint256 amount
  ) external validProductId(productId) {
    //Stock management access checked in setStock
    Product storage product = _products[productId - 1];
    setStock(productId, product.stockLimit + amount);
  }

  function removeStock(
    uint256 productId,
    uint256 amount
  ) external validProductId(productId) {
    //Stock management access checked in setStock
    Product storage product = _products[productId - 1];
    setStock(productId, product.stockLimit - amount);
  }

  function setForSale(
    uint256 productId,
    bool open
  ) external hasAccess(PRODUCT_MANAGER) validProductId(productId) {
    _products[productId - 1].forSale = open;
  }

  function productsCreated() external view returns (uint256) {
    return _products.length;
  }

  function setDefaultRoyalty(
    address receiver,
    uint96 feeNumerator
  ) external hasAccess(ROYALTY_MANAGER) {
    super._setDefaultRoyalty(receiver, feeNumerator);
    emit DefaultRoyaltySet(msg.sender, receiver, feeNumerator);
  }

  function deleteDefaultRoyalty() external hasAccess(ROYALTY_MANAGER) {
    super._deleteDefaultRoyalty();
    emit DefaultRoyaltyDeleted(msg.sender);
  }

  function setTokenRoyalty(
    uint256 tokenId,
    address receiver,
    uint96 feeNumerator
  ) external hasAccess(ROYALTY_MANAGER) {
    super._setTokenRoyalty(tokenId, receiver, feeNumerator);
    emit TokenRoyaltySet(msg.sender, tokenId, receiver);
  }

  function resetTokenRoyalty(
    uint256 tokenId
  ) external hasAccess(ROYALTY_MANAGER) {
    super._resetTokenRoyalty(tokenId);
    emit TokenRoyaltyReset(msg.sender, tokenId);
  }

  function supportsInterface(
    bytes4 interfaceId
  ) public view virtual override(ERC721, ERC2981) returns (bool) {
    return
      ERC721.supportsInterface(interfaceId) ||
      ERC2981.supportsInterface(interfaceId);
  }
}
