pragma solidity ^0.8.9;

interface IReveal {
  function prepareReveal(uint256 productId, string memory URI) external;

  function revealAllowed(uint256 productId) external view returns (bool);

  function reveal(uint256 productId, uint256 seed) external;
}
"
