    "contracts/CollectionRevealer.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./IReveal.sol";
import "./RandomGenerator.sol";

contract CollectionRevealer is RandomGenerator, Ownable {
  constructor(
    address _vrfCoordinator,
    address _linkToken,
    bytes32 _keyHash
  ) RandomGenerator(_vrfCoordinator, _linkToken, _keyHash) {}

  function withdrawToken(address token) external onlyOwner {
    IERC20(token).transfer(msg.sender, IERC20(token).balanceOf(address(this)));
  }

  function requestReveal(
    address collectionAddress,
    uint256 productId,
    string memory URI
  ) external onlyOwner {
    IReveal collection = IReveal(collectionAddress);
    collection.prepareReveal(productId, URI);
    super.requestRandomReveal(collectionAddress, productId);
  }

  function revealAllowed(
    address collectionAddress,
    uint256 productId
  ) internal view override returns (bool) {
    IReveal collection = IReveal(collectionAddress);
    return collection.revealAllowed(productId);
  }

  function reveal(
    address collectionAddress,
    uint256 productId,
    uint256 randomness
  ) internal override {
    IReveal collection = IReveal(collectionAddress);
    return collection.reveal(productId, randomness);
  }
}
"
    },
    "contracts/Permissions.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/access/AccessControl.sol";

contract Permissions is AccessControl {
  mapping(bytes32 => bool) public anonymousAccess;

  event AnonymousAccessChanged(
    bytes32 indexed role,
    address indexed sender,
    bool value
  );

  constructor() {
    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
  }

  function setAnonymousAccess(bytes32 access, bool value) external {
    require(hasRole(getRoleAdmin(access), msg.sender), "ROLE_ADMIN_REQUIRED");
    anonymousAccess[access] = value;
    emit AnonymousAccessChanged(access, msg.sender, value);
  }

  function addressHasAccess(
    bytes32 access,
    address addr
  ) public view returns (bool) {
    return anonymousAccess[access] || hasRole(access, addr);
  }
}
"
    },
