    "contracts/RandomGenerator.sol": {
      "content": "pragma solidity ^0.8.9;

// SPDX-License-Identifier: MIT

import "@chainlink/contracts/src/v0.8/VRFConsumerBase.sol";
import "./VRFCoordinatorServiceAgreements.sol";

abstract contract RandomGenerator is VRFConsumerBase {
  bytes32 public keyHash;
  address public vrfCoordinator;
  uint256 public fee;

  struct CollectionReference {
    address collectionAddress;
    uint256 productId;
  }

  mapping(bytes32 => bytes32) private productToRequest;
  mapping(bytes32 => CollectionReference) private requestToProduct;

  bytes32 public lastRequestId;

  event LINKFeeUpdated(uint256 oldFee, uint256 newFee);
  event RevealRequested(
    address indexed collectionAddress,
    uint256 indexed productId,
    bytes32 indexed requestId
  );
  event FulfillRandomness(
    address indexed collectionAddress,
    uint256 indexed productId,
    bytes32 indexed requestId,
    uint256 randomness
  );

  constructor(
    address _vrfCoordinator,
    address _linkToken,
    bytes32 _keyHash
  ) VRFConsumerBase(_vrfCoordinator, _linkToken) {
    vrfCoordinator = _vrfCoordinator;
    keyHash = _keyHash;
    updateLinkFee();
  }

  function updateLinkFee() public {
    VRFCoordinatorServiceAgreements agreements = VRFCoordinatorServiceAgreements(
        vrfCoordinator
      );
    uint256 oldFee = fee;
    uint256 newFee = agreements.serviceAgreements(keyHash).fee;
    if (fee != newFee) {
      fee = newFee;
      emit LINKFeeUpdated(oldFee, newFee);
    }
  }

  function encodeCollectionRef(
    CollectionReference memory ref
  ) private pure returns (bytes32) {
    return keccak256(abi.encode(ref));
  }

  function requestRandomReveal(
    address collectionAddress,
    uint256 productId
  ) internal returns (bytes32 requestId) {
    require(
      LINK.balanceOf(address(this)) >= fee,
      "Not enough LINK - fill contract with faucet"
    );

    requestId = requestRandomness(keyHash, fee);
    lastRequestId = requestId;
    CollectionReference memory collectionReference = CollectionReference(
      collectionAddress,
      productId
    );
    requestToProduct[requestId] = collectionReference;
    productToRequest[encodeCollectionRef(collectionReference)] = requestId;

    emit RevealRequested(collectionAddress, productId, requestId);
  }

  function fulfillRandomness(
    bytes32 requestId,
    uint256 randomness
  ) internal override {
    CollectionReference storage collectionReference = requestToProduct[
      requestId
    ];
    bytes32 lastRequest = productToRequest[
      encodeCollectionRef(collectionReference)
    ];
    require(requestId == lastRequest, "This VRF request is already obsolete");
    require(
      revealAllowed(
        collectionReference.collectionAddress,
        collectionReference.productId
      ),
      "Reveal is no longer allowed for this product"
    );
    reveal(
      collectionReference.collectionAddress,
      collectionReference.productId,
      randomness
    );

    emit FulfillRandomness(
      collectionReference.collectionAddress,
      collectionReference.productId,
      requestId,
      randomness
    );
  }

  function revealAllowed(
    address collectionAddress,
    uint256 productId
  ) internal virtual returns (bool);

  function reveal(
    address collectionAddress,
    uint256 productId,
    uint256 randomness
  ) internal virtual;
}
"
