import { ERC20 } from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import { IERC721 } from "@openzeppelin/contracts/token/ERC721/IERC721.sol";

import { ITuschayCoin } from "./ITuschayCoin.sol";

error TuschayCoin_FunctionLocked();
error TuschayCoin_InsufficientSupply();
error TuschayCoin_SenderNotAllowed();

/**
 * @title TuschayCoin
 * @author Tuschay Studios
 */
contract TuschayCoin is ITuschayCoin, ERC20, Ownable, AccessControl {
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    IERC721 public immutable FUNGUY_FAMILY;
    uint256 public immutable BASE_RATE;
    uint256 public immutable MAX_SUPPLY;
    uint256 public immutable START_TIME;
    uint256 public immutable MONTH = 4 weeks;

    uint256 public totalClaimed;
    mapping(address => uint256) public stash;
    mapping(address => uint256) public lastUpdate;
    mapping(bytes4 => bool) public functionLocked;

    constructor(
        IERC721 _funguyFamily,
        uint256 _baseRate,
        uint256 _maxSupply,
        uint256 _backdateBy
    )
        ERC20("Tuschay Coin", "TSHY")
    {
        FUNGUY_FAMILY = _funguyFamily;
        BASE_RATE = _baseRate;
        MAX_SUPPLY = _maxSupply;
        START_TIME = block.timestamp - _backdateBy;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice Modifier applied to functions that will be disabled when they're no longer needed
     */
    modifier lockable() {
        if (functionLocked[msg.sig]) revert TuschayCoin_FunctionLocked();
        _;
    }

    /**
     * @notice Returns amount of tokens available to claim for a specified account
     * @param account Address to return the pending tokens for
     * @return Pending tokens to claim
     */
    function pending(address account) public view returns (uint256) {
        uint256 timePassed = block.timestamp - (lastUpdate[account] == 0 ? START_TIME : lastUpdate[account]);

        return BASE_RATE * FUNGUY_FAMILY.balanceOf(account) * timePassed / MONTH;
    }

    /**
     * @notice Mint a sender's pending token balance
     */
    function claim() public payable {
        uint256 amount = pending(msg.sender) + stash[msg.sender];

        lastUpdate[msg.sender] = block.timestamp;
        stash[msg.sender] = 0;

        if (totalClaimed < MAX_SUPPLY) {
            _mint(
                msg.sender,
                totalClaimed + amount < MAX_SUPPLY ? amount : MAX_SUPPLY - totalClaimed
            );

            totalClaimed += amount;
        }
    }

    /**
     * @notice Called on ERC721 token transfer to update pending token balances
     * @param from Account sending a token
     * @param to Account receiving a token
     */
    function update(address from, address to) public payable override {
        if (msg.sender != address(FUNGUY_FAMILY)) revert TuschayCoin_SenderNotAllowed();

        stash[from] = pending(from);
        lastUpdate[from] = block.timestamp;

        stash[to] = pending(to);
        lastUpdate[to] = block.timestamp;
    }

    /**
     * @notice Mint a specified amount of tokens to specified receivers
     * @dev Should be locked after airdrop is complete so no need for supply check
     * @param receivers Receivers of the airdrop
     * @param amounts Amounts of tokens to airdrop for corresponding receivers
     */
    function airdrop(
        address[] calldata receivers,
        uint256[] calldata amounts
    )
        public
        payable
        lockable
        onlyOwner
    {
        for (uint256 i; i < receivers.length;) {
            _mint(receivers[i], amounts[i]);

            unchecked {
                totalClaimed += amounts[i];
                ++i;
            }
        }
    }

    /**
     * @notice Burn a specified amount of tokens
     * @param from Address to burn tokens from
     * @param amount Amount of tokens to burn
     */
    function burn(address from, uint256 amount) public payable lockable onlyRole(BURNER_ROLE) {
        _burn(from, amount);
    }

    /**
     * @notice Lock individual functions that are no longer needed.
     * @dev WARNING: THIS CANNOT BE UNDONE
     * @dev Only affects functions with the lockable modifier
     * @param id First 4 bytes of the calldata (i.e. function identifier)
     */
    function lockFunction(bytes4 id) public payable onlyOwner {
        functionLocked[id] = true;
    }
}"
    }
  },
