    "src/crate/CrateOpener.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;

import "@openzeppelin/contracts/token/ERC1155/ERC1155Holder.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "../utils/AccessProtected.sol";

contract CrateOpener is
    ERC1155Holder,
    ReentrancyGuard,
    AccessProtected,
    Pausable
{
    IERC1155 public immutable crate;
    IERC1155 public immutable lantern;

    uint256 public crateId;
    uint256 public lanternId;

    uint256 public totalCratesOpened;

    event CrateOpened(address indexed user, uint256 timestamp);

    constructor(
        address _lantern,
        address _crate,
        uint256 _crateId,
        uint256 _lanternId
    ) {
        require(
            _lantern != address(0) && _crate != address(0),
            "Invalid addresses"
        );
        lantern = IERC1155(_lantern);
        crate = IERC1155(_crate);
        crateId = _crateId;
        lanternId = _lanternId;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function open(address user)
        external
        whenNotPaused
        onlyAdmin
        returns (bool)
    {
        require(
            crate.balanceOf(user, crateId) > 0,
            "Caller does not own a crate"
        );
        require(
            lantern.balanceOf(address(this), lanternId) > 0,
            "Max Crates Opened"
        );
        crate.safeTransferFrom(user, address(this), crateId, 1, "");
        totalCratesOpened++;
        lantern.safeTransferFrom(address(this), user, lanternId, 1, "");
        emit CrateOpened(user, block.timestamp);
        return true;
    }

    function withdraw(
        address token,
        address user,
        uint256 amount
    ) external onlyOwner returns (uint256) {
        uint256 bal = IERC20(token).balanceOf(address(this));
        require(amount <= bal, "Invalid amount");
        IERC20(token).transfer(user, amount);
        return amount;
    }

    function withdraw(
        address token,
        uint256 id,
        address user,
        uint256 amount
    ) external onlyOwner returns (uint256) {
        uint256 bal = IERC1155(token).balanceOf(address(this), id);
        require(amount <= bal, "Invalid amount");
        IERC1155(token).safeTransferFrom(address(this), user, id, amount, "");
        return amount;
    }
}
"
