        uint256 protocolFeePaid               // Amount of eth or weth paid to the staking contract.
    );*/

    event Fill(
        address indexed makerAddress,         // Address that created the order.
        address indexed feeRecipientAddress,  // Address that received fees.
        bytes makerAssetData,                 // Encoded data specific to makerAsset.
        bytes takerAssetData,                 // Encoded data specific to takerAsset.
        bytes makerFeeAssetData,              // Encoded data specific to makerFeeAsset.
        bytes takerFeeAssetData,              // Encoded data specific to takerFeeAsset.
        bytes32 indexed orderHash,            // EIP712 hash of order (see LibOrder.getTypedDataHash).
        address takerAddress,                 // Address that filled the order.
        address senderAddress,                // Address that called the Exchange contract (msg.sender).
        uint makerAssetFilledAmount,       // Amount of makerAsset sold by maker and bought by taker.
        uint makerAssetPaid
    );

	constructor(string memory name, string memory version) EIP712(name, version) {
		//WRAPPED = _wrapped;
        operator = msg.sender;
	}

	/// @dev Registers an asset proxy to its asset proxy id.
	///      Once an asset proxy is registered, it cannot be unregistered.
	/// @param assetProxy Address of new asset proxy to register

    function registerAssetProxy(address assetProxy) external onlyOwner {
        // Ensure that no asset proxy exists with current id.
        bytes4 assetProxyId = IAssetProxy(assetProxy).getProxyId();
        address currentAssetProxy = _assetProxies[assetProxyId];
        if (currentAssetProxy != address(0)) {
            // LibRichErrors.rrevert(LibExchangeRichErrors.AssetProxyExistsError(
            //     assetProxyId,
            //     currentAssetProxy
            // ));
            revert("error");
        }

        // Add asset proxy and log registration.
        _assetProxies[assetProxyId] = assetProxy;
        // emit AssetProxyRegistered(
        //     assetProxyId,
        //     assetProxy
        // );
    }

    function getAssetProxy(bytes4 assetProxyId)
		external
		view
		returns (address assetProxy)
	{
		return _assetProxies[assetProxyId];
	}

	modifier refundRemainingBalance() {
		_;
		uint256 balance = address(this).balance;
		if (balance > 0) {
			payable(msg.sender).transfer(balance);
		}
	}

  /// @dev Match two complementary orders that have a profitable spread.
	///      Each order is filled at their respective price point. However, the calculations are
	///      carried out as though the orders are both being filled at the right order's price point.
	///      The profit made by the left order goes to the taker (who matched the two orders).
	/// @param leftOrder First order to match.
	/// @param rightOrder Second order to match.
	/// @param leftSignature Proof that order was created by the left maker.
	/// @param rightSignature Proof that order was created by the right maker.
	// / @return matchedFillResults Amounts filled and fees paid by maker and taker of matched orders.
	function matchOrders(
		LibOrder.Order memory leftOrder,
		LibOrder.Order memory rightOrder,
		bytes memory leftSignature,
		bytes memory rightSignature
	)
		public
		payable
		nonReentrant
		refundRemainingBalance
		/// returns (LibFillResults.MatchedFillResults memory matchedFillResults)
	{
        _matchOrders(leftOrder, rightOrder, leftSignature, rightSignature);
    }

	function _matchOrders(
		LibOrder.Order memory leftOrder,  //seller
		LibOrder.Order memory rightOrder, //buyer
		bytes memory leftSignature,
		bytes memory rightSignature
	) internal
    {
		address signer = getSigner();
		verify(leftSignature,leftOrder.makerAddress,leftOrder);        
		verify(rightSignature,signer,rightOrder);

        // Get left & right order info
        LibOrder.OrderInfo memory leftOrderInfo = getOrderInfo(leftOrder);
        LibOrder.OrderInfo memory rightOrderInfo = getOrderInfo(rightOrder);

        // Either our context is valid or we revert
        _assertFillableOrder(
            leftOrder,
            leftOrderInfo,
            leftOrder.makerAddress,
            leftSignature
        );

        _assertFillableOrder(
            rightOrder,
            rightOrderInfo,
            rightOrder.makerAddress,
            rightSignature
        );
        // init asset fill
        // /*

        // Fetch taker address
        address takerAddress = _getCurrentContextAddress();

        // Update exchange state
        _updateFilledState(
            leftOrder,
            takerAddress,
            leftOrderInfo.orderHash,
            leftOrderInfo.orderTakerAssetFilledAmount,
            rightOrder.takerAssetAmount,
            leftOrder.makerAssetAmount
        );

        _updateFilledState(
            rightOrder,
            takerAddress,
            rightOrderInfo.orderHash,
            rightOrderInfo.orderTakerAssetFilledAmount,
            rightOrder.makerAssetAmount,
            rightOrder.makerAssetAmount
        );

        // */
        // end assser fill

		bytes32 orderHash = LibOrder.getStructHash(leftOrder);

        _dispatchTransferFrom(
            orderHash,
            rightOrder.takerAssetData, //bytes memory assetData,
            leftOrder.makerAddress, //address from,
            rightOrder.makerAddress, //address to,
            1
        );

        _dispatchTransferFrom(
            orderHash,
            rightOrder.makerAssetData,//bytes memory assetData,
            rightOrder.makerAddress,  //address from,
            leftOrder.makerAddress,//address to,
            1
        );

        payFee(
            orderHash,
            rightOrder.makerFeeAssetData,//bytes memory assetData,
            rightOrder.makerAddress,  //address from,
            leftOrder.feeRecipientAddress,//address to,
            1
        );
    }

    function _dispatchTransferFrom(
        bytes32 orderHash,
        bytes memory assetData,
        address from,
        address to,
        uint256 amount
    ) internal {
        bytes4 proxyId = assetData.readBytes4(0);
        if (proxyId == bytes4(0)) {
            (address assetAddress, uint256 _amount) = abi.decode(
                assetData.sliceDestructive(4, assetData.length),
                (address, uint256)
            );
            payable(to).transfer(_amount);
        } else {
            address assetProxy = _assetProxies[proxyId];

            // Construct the calldata for the transferFrom call.
            bytes memory proxyCalldata = abi.encodeWithSelector(
                IAssetProxy(address(0)).transferFrom.selector,
                assetData,
                from,
                to,
                amount
            );
            // Call the asset proxy's transferFrom function with the constructed calldata.
            (bool didSucceed, bytes memory returnData) = assetProxy.call(
                proxyCalldata
            );

            // If the transaction did not succeed, revert with the returned data.
            if (!didSucceed) {
                revert("!didSucceed");
            }
        }
    }

    function payFee(
        bytes32 orderHash,
        bytes memory assetData,
        address from,
        address to,
        uint256 amount
    ) internal {
        bytes4 proxyId = assetData.readBytes4(0);
        if (proxyId == bytes4(0)) {
            (, address[] memory receivers, uint256[] memory _amounts) = abi.decode(
                assetData.sliceDestructive(4, assetData.length),
                (address, address[], uint256[])
            );
            for(uint i; i < receivers.length; i++) {
                payable(receivers[i]).transfer(_amounts[i]);
            }
        } else {
            address assetProxy = _assetProxies[proxyId];

            // Construct the calldata for the transferFrom call.
            bytes memory proxyCalldata = abi.encodeWithSelector(
                IAssetProxy(address(0)).transferFrom.selector,
                assetData,
                from,
                to,
                amount
            );
            // Call the asset proxy's transferFrom function with the constructed calldata.
            (bool didSucceed, bytes memory returnData) = assetProxy.call(
                proxyCalldata
            );

            // If the transaction did not succeed, revert with the returned data.
            if (!didSucceed) {
                revert("!didSucceed fee");
            }
        }
    }

    function getSigner( ) public view returns (address) {
        return operator;
    }

    function setSigner( address operator_ ) external view  onlyOwner returns (address) {
        return operator_;
    }

    function verify(
        bytes memory signature,
        address signer,
        LibOrder.Order memory order
    ) public view returns (bool) {
        //evan: internal
        bytes32 META_TRANSACTION_TYPEHASH = 0xf80322eb8376aafb64eadf8f0d7623f22130fd9491a221e902b713cb984a7534;
        bytes32 digest = _hashTypedDataV4(LibOrder.getStructHash(order));
        address recoveredSigner = ECDSA.recover(digest, signature);
        require(recoveredSigner == signer, "INVALID_SIGNATURE");

        return recoveredSigner == signer;
    }

    function getChainId() external view returns (uint256) {
        return block.chainid;
    }
