	function getOrderInfo(LibOrder.Order memory order)
		public
		view
		returns (LibOrder.OrderInfo memory orderInfo)
	{
		// Compute the order hash and fetch the amount of takerAsset that has already been filled
		(orderInfo.orderHash, orderInfo.orderTakerAssetFilledAmount) = _getOrderHashAndFilledAmount(order);

        // Validate order availability
        if (orderInfo.orderTakerAssetFilledAmount >= order.makerAssetAmount) {
            orderInfo.orderStatus = LibOrder.OrderStatus.FULLY_FILLED;
            return orderInfo;
        }

		// Validate order expiration
		// solhint-disable-next-line not-rely-on-time
		if (block.timestamp >= order.expirationTimeSeconds) {
			orderInfo.orderStatus = LibOrder.OrderStatus.EXPIRED;
			return orderInfo;
		}


		// Check if order has been cancelled
		if (isCanceled(orderInfo.orderHash)) {
			orderInfo.orderStatus = LibOrder.OrderStatus.CANCELLED;
			return orderInfo;
		}
		// if (orderEpoch[order.makerAddress][order.senderAddress] > order.salt) {
		//     orderInfo.orderStatus = LibOrder.OrderStatus.CANCELLED;
		//     return orderInfo;
		// }

		// All other statuses are ruled out: order is Fillable
		orderInfo.orderStatus = LibOrder.OrderStatus.FILLABLE;
		return orderInfo;
	}

    function _assertFillableOrder (
        LibOrder.Order memory order,
        LibOrder.OrderInfo memory orderInfo,
        address takerAddress,
        bytes memory signature
    )
        public
        pure
    {
        // An order can only be filled if its status is FILLABLE.
        if (orderInfo.orderStatus != LibOrder.OrderStatus.FILLABLE) {
            revert("Order is not fillable");// readl: custom
        }
    }


    // bytes32 EIP712_EXCHANGE_DOMAIN_HASH=0xf80322eb8376aafb64eadf8f0d7623f22130fd9491a221e902b713cb984a7534;
	function _getOrderHashAndFilledAmount(LibOrder.Order memory order)
		internal
		view
		returns (bytes32 orderHash, uint256 orderTakerAssetFilledAmount)
	{
		orderHash = LibOrder.getStructHash(order);
		orderTakerAssetFilledAmount = getFilled(orderHash);
		return (orderHash, orderTakerAssetFilledAmount);
	}

    function getOrderHashAndFilledAmount(LibOrder.Order memory order)
		external
		view
		returns (bytes32 orderHash, uint256 orderTakerAssetFilledAmount)
	{
		orderHash = LibOrder.getStructHash(order);
		orderTakerAssetFilledAmount = getFilled(orderHash);
		return (orderHash, orderTakerAssetFilledAmount);
	}

	function verify2(
		LibOrder.Order memory leftOrder,  //seller
		LibOrder.Order memory rightOrder, //buyer
		bytes memory leftSignature,
		bytes memory rightSignature
	) public view returns(bool) {		
		verify(leftSignature,leftOrder.makerAddress,leftOrder);
		verify(rightSignature,getSigner(),rightOrder);
		return true;
	}

	/// @dev After calling, the order can not be filled anymore.
    /// @param order Order struct containing order specifications.
    function cancelOrder(LibOrder.Order memory order)
        public
        nonReentrant
    {
        _cancelOrder(order);
    }


    /// @dev Executes multiple calls of cancelOrder.
    /// @param orders Array of order specifications.
    function batchCancelOrders(LibOrder.Order[] memory orders)
        public
        nonReentrant
    {
        uint256 ordersLength = orders.length;
        for (uint256 i = 0; i != ordersLength; i++) {
            _cancelOrder(orders[i]);
        }
    }

    function _cancelOrder(LibOrder.Order memory order)
        internal
    {
        // Fetch current order status
        LibOrder.OrderInfo memory orderInfo = getOrderInfo(order);

        // Validate context
        _assertValidCancel(order, orderInfo);

        // Noop if order is already unfillable
        if (orderInfo.orderStatus != LibOrder.OrderStatus.FILLABLE) {
            return;
        }

        // Perform cancel
        _updateCancelledState(order, orderInfo.orderHash);
    }

    /// @dev Validates context for cancelOrder. Succeeds or throws.
    /// @param order to be cancelled.
    /// @param orderInfo OrderStatus, orderHash, and amount already filled of order.
    function _assertValidCancel(
        LibOrder.Order memory order,
        LibOrder.OrderInfo memory orderInfo
    )
        internal
        view
    {
        // Validate sender is allowed to cancel this order
        if (order.senderAddress != address(0)) {
            if (order.senderAddress != msg.sender) {
                LibRichErrors.rrevert(LibExchangeRichErrors.ExchangeInvalidContextError(
                    LibExchangeRichErrors.ExchangeContextErrorCodes.INVALID_SENDER,
                    orderInfo.orderHash,
                    msg.sender
                ));
            }
        }

        // Validate transaction signed by maker
        address makerAddress = _getCurrentContextAddress();
        if (order.makerAddress != makerAddress) {
            LibRichErrors.rrevert(LibExchangeRichErrors.ExchangeInvalidContextError(
                LibExchangeRichErrors.ExchangeContextErrorCodes.INVALID_MAKER,
                orderInfo.orderHash,
                makerAddress
            ));
        }
    }

    /// @dev Updates state with results of cancelling an order.
    ///      State is only updated if the order is currently fillable.
    ///      Otherwise, updating state would have no effect.
    /// @param order that was cancelled.
    /// @param orderHash Hash of order that was cancelled.
    function _updateCancelledState(
        LibOrder.Order memory order,
        bytes32 orderHash
    )
        internal
    {
        // Perform cancel
        cancelled[orderHash] = true;

        // Log cancel
        emit Cancel(
            order.makerAddress,
            order.feeRecipientAddress,
            order.makerAssetData,
            order.takerAssetData,
            msg.sender,
            orderHash
        );
    }

    /// @dev The current function will be called in the context of this address (either 0x transaction signer or `msg.sender`).
    ///      If calling a fill function, this address will represent the taker.
    ///      If calling a cancel function, this address will represent the maker.
    /// @return Signer of 0x transaction if entry point is `executeTransaction`.
    ///         `msg.sender` if entry point is any other function.
    function _getCurrentContextAddress()
        internal
        view
        returns (address)
    {
        address currentContextAddress_ = currentContextAddress;
        address contextAddress = currentContextAddress_ == address(0) ? msg.sender : currentContextAddress_;
        return contextAddress;
    }

    /// @dev Updates state with results of a fill order.
    /// @param orderTakerAssetFilledAmount Amount of order already filled.
    function _updateFilledState(
        LibOrder.Order memory order,
        address takerAddress,
        bytes32 orderHash,
        uint orderTakerAssetFilledAmount,
        uint amount,
        uint maxAmount
    )
        internal
    {
        // Update state
        filled[orderHash] = orderTakerAssetFilledAmount.safeAdd(amount);
        require(filled[orderHash] <= maxAmount, "Order filled amount exceeds max amount");
        emit Fill(
            order.makerAddress,
            order.feeRecipientAddress,
            order.makerAssetData,
            order.takerAssetData,
            order.makerFeeAssetData,
            order.takerFeeAssetData,
            orderHash,
            takerAddress,
            msg.sender,
            filled[orderHash],
            amount
        );
    }

    function isCanceled(bytes32 orderHash)
        public
        view
        returns (bool)
    {
        if(address(exchangeHelpler) != address(0)){
            return cancelled[orderHash] || exchangeHelpler.cancelled(orderHash);
        }
        return cancelled[orderHash];
    }

    function getFilled(bytes32 orderHash)
        public
        view
        returns (uint)
    {
        if(address(exchangeHelpler) != address(0) && filled[orderHash] == 0) {
            return exchangeHelpler.filled(orderHash);
        }
        return filled[orderHash];
    }

}


interface IAssetProxy {
    /// @dev Transfers assets. Either succeeds or throws.
    /// @param assetData Byte array encoded for the respective asset proxy.
    /// @param from Address to transfer asset from.
    /// @param to Address to transfer asset to.
    /// @param amount Amount of asset to transfer.
    function transferFrom(
        bytes calldata assetData,
        address from,
        address to,
        uint256 amount
    ) external;

    /// @dev Gets the proxy id associated with the proxy address.
    /// @return Proxy id.
    function getProxyId() external pure returns (bytes4);
}
