pragma solidity 0.8.19;

import "./AccessControl.sol";
import "./IERC721A.sol";
import "./IERC1155.sol";

contract Distribution is AccessControl {
    bytes32 public constant DISTRIBUTION_ROLE = keccak256("DISTRIBUTION_ROLE");
    bytes32 public constant MULTISIG_WALLET = keccak256("MULTISIG_WALLET");

    event PackDistributed(address indexed recipient, uint256 startingIndex, uint256 endingIndex);

    // the last ID distributed by this contract
    uint256 public nextDistId;
    mapping(address => uint256) public redemptions;

    IERC721A public cards;
    IERC1155 public packs;

    constructor(address msig, address distributeRole, address cardContract, address packContract) {
        _grantRole(DEFAULT_ADMIN_ROLE, msig);
        _grantRole(MULTISIG_WALLET, msig);
        _grantRole(DISTRIBUTION_ROLE, distributeRole);

        cards = IERC721A(cardContract);
        packs = IERC1155(packContract);
    }

    function DistributePack(address _recipient) external payable onlyRole(DISTRIBUTION_ROLE) {
        uint256 startingIndex = nextDistId;

        for(uint i = 0; i < 5; i++) {
            uint256 nextIndex = startingIndex + i;
            require(cards.ownerOf(nextIndex) == address(this), "Contract does not own the token it's trying to transfer");
            cards.transferFrom(address(this), _recipient, nextIndex);
        }

        nextDistId += 5;
        redemptions[_recipient] += 1;

        emit PackDistributed(_recipient, startingIndex, nextDistId - 1);
    }

    function claimPack(uint256 packId) external {
        bool burn = _burnPack(_msgSender(), packId);
        require(burn, "Burn failed");

        _distributePack(_msgSender());
    }

    function _distributePack(address _recipient) internal {
        uint256 startingIndex = nextDistId;

        for(uint i = 0; i < 5; i++) {
            uint256 nextIndex = startingIndex + i;
            require(cards.ownerOf(nextIndex) == address(this), "Contract does not own the token it's trying to transfer");
            cards.transferFrom(address(this), _recipient, nextIndex);
        }

        nextDistId += 5;
        redemptions[_recipient] += 1;
    }

    function _burnPack(address from, uint256 packId) internal returns(bool){
        address burnAddress = 0x000000000000000000000000000000000000dEaD;

        packs.safeTransferFrom(from, burnAddress, packId, 1, "");

        return true;
    }

    function updateNextDistId(uint256 _nextDistId) external onlyRole(DISTRIBUTION_ROLE) {
        nextDistId = _nextDistId;
    }

    function setDistributionRole(address account) external onlyRole(MULTISIG_WALLET) {
        _grantRole(DISTRIBUTION_ROLE, account);
    }

    function revokeDistributionRole(address account) external onlyRole(MULTISIG_WALLET) {
        _revokeRole(DISTRIBUTION_ROLE, account);
    }
}"},"ERC165.sol":{"content":"// SPDX-License-Identifier: MIT
