    "contracts/Implem/EIP2981PerTokenPBW.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '@openzeppelin/contracts/utils/introspection/ERC165.sol';

import './EIP2981.sol';

/// @dev This is a contract used to add ERC2981 support to ERC721 and 1155
abstract contract EIP2981PerTokenRoyalties is IERC2981  {
    mapping(uint256 => RoyaltyInfo) internal _royalties;
    address payable public pbwTreasury;
    event TreasuryTransferEvent(address oldTreasury, address newTreasury);


    function transferTreasury(address _newTreasury) public {
        require(msg.sender == pbwTreasury, "Only current treasury can use this action");
        require(_newTreasury != pbwTreasury, "New treasury is same with current treasury");

        address oldTreasury = pbwTreasury;
        pbwTreasury = payable(_newTreasury);
        emit TreasuryTransferEvent(oldTreasury, _newTreasury);
    }

    /// @inheritdoc	IERC2981Royalties
    function royaltyInfo(uint256 tokenId, uint256 value)
        external
        view
        override
        returns (address receiver, uint256 royaltyAmount)
    {
        receiver = pbwTreasury;
        royaltyAmount = value;
    }
}"
    },
    "contracts/Implem/Interfaces/IEIP2981.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

/// @title IERC2981Royalties
/// @dev Interface for the ERC2981 - Token Royalty standard
interface IERC2981Royalties {
    /// @notice Called with the sale price to determine how much royalty
    //          is owed and to whom.
    /// @param _tokenId - the NFT asset queried for royalty information
    /// @param _value - the sale price of the NFT asset specified by _tokenId
    /// @return _receiver - address of who should be sent the royalty payment
    /// @return _royaltyAmount - the royalty payment amount for value sale price
    function royaltyInfo(uint256 _tokenId, uint256 _value)
        external
        view
        returns (address _receiver, uint256 _royaltyAmount);
}"
    },
    "contracts/PBW/PBWCommemorativeNFT.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "erc721a/contracts/ERC721A.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../Implem/EIP2981PerTokenPBW.sol"; 

contract PBWCommemorativeNFT is ERC721A, EIP2981PerTokenRoyalties, Ownable {
  bool public isMintEnabled;

  //events
  event MintToggleEvent(bool isEnabled);
  mapping(uint256 => string) private _tokenURIs;

  constructor(string memory _name, address _pbwTreasury) ERC721A(_name, "PBW") {
    pbwTreasury = payable(_pbwTreasury);
  }

  /// @inheritdoc	ERC165
  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(ERC721A, IERC2981)
    returns (bool)
  {
    if(interfaceId == type(IERC721).interfaceId) {
      return true;
    }
    return super.supportsInterface(interfaceId);
  }
  
  function _disableMinting() private {
    isMintEnabled = false;
    emit MintToggleEvent(false);
  }

  function disableMinting() external onlyOwner {
    require(isMintEnabled, 'Minting is currently disabled');

    _disableMinting();
  }

  function _enableMinting() private {
    require(!isMintEnabled, 'Minting is currently enabled');
    isMintEnabled = true;
    emit MintToggleEvent(true);
  }
  
  function enableMinting() public onlyOwner {
    _enableMinting();
  }

  function mintToken(address nftOwner, string memory _tokenURI) external payable onlyOwner returns (uint256) {
    require(isMintEnabled, "Minting is not open");
    uint256 newItemId = _nextTokenId();

    _tokenURIs[newItemId] = _tokenURI;

    _safeMint(nftOwner, 1);
    return newItemId;  
  }

  function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
    require(_exists(tokenId), 'Token not yet existing');        
    return _tokenURIs[tokenId];
  }
}"
    },
