    "contracts/UniqueDAO.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.4;
import "./interfaces/IERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract UniqueDAO is AccessControl {
    bytes32 public constant RELAY_ROLE = keccak256("RELAY_ROLE");
    // 0x077a1d526a4ce8a773632ab13b4fbbf1fcc954c3dab26cd27ea0e2a6750da5d7
    uint256 public nextProposal;
    uint256 payableAmount = 1;
    address private LLPT;
    // 7 day millisecond timestamp
    uint256 public constant VOTING_PERIOD = 604800;
    uint256 public constant MULTIPLIER = 10 ** 20;
    mapping(uint256 => ProposalInfo) public Proposals;

    constructor(address _LLPT) {
        LLPT = _LLPT;
        nextProposal = 1;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    struct ProposalInfo {
        uint256 id;
        string description;
        uint deadline;
        uint256 votesUp;
        uint256 votesDown;
        mapping(address => bool) voteStatus;
        bool countConducted;
        bool quorumMet;
        uint256 totalVotes;
        uint256 totalCount;
        uint256 requiredQuorum;
        bool passed;
        bool vetoUsed;
        address proposedBy;
    }
    struct SingleProposal {
        uint256 id;
        string description;
        uint deadline;
        uint256 votesUp;
        uint256 votesDown;
        bool voteStatus;
        bool countConducted;
        bool quorumMet;
        uint256 totalVotes;
        uint256 totalCount;
        uint256 requiredQuorum;
        bool passed;
        bool vetoUsed;
        address proposedBy;
    }

    event proposalCreated(uint256 id, string description, address proposer);

    event newVote(
        uint256 votesUp,
        uint256 votesDown,
        address voter,
        uint256 proposal,
        bool votedFor,
        uint256 totalVotes
    );

    event proposalCount(uint256 id, bool passed);

    receive() external payable {
        // assert(msg.sender == WETH); // only accept ETH via fallback from the WETH contract
    }

    // function checkProposalEligibility(address _proposalist) private view returns (
    //     bool
    // ){
    //     for(uint i = 0; i < validTokens.length; i++){
    //         if(daoContract.balanceOf(_proposalist, validTokens[i]) >= 1){
    //             return true;
    //         }
    //     }
    //     return false;
    // }

    function checkVoteEligibility(address _voter) private view returns (bool) {
        uint256 lpTokens = IERC20(LLPT).balanceOf(_voter);
        if (lpTokens > 0) {
            return true;
        }
        return false;
    }

    function getAllProposal() public view returns (SingleProposal[] memory) {
        if (nextProposal == 1) {
            // means no data
            return new SingleProposal[](0);
        }
        SingleProposal[] memory briefData = new SingleProposal[](
            (nextProposal - 1)
        );
        for (uint256 i = 0; i < (nextProposal - 1); i++) {
            briefData[i].id = Proposals[i + 1].id;
            briefData[i].description = Proposals[i + 1].description;
            briefData[i].deadline = Proposals[i + 1].deadline;
            briefData[i].votesUp = Proposals[i + 1].votesUp;
            briefData[i].votesDown = Proposals[i + 1].votesDown;
            briefData[i].voteStatus = Proposals[i + 1].voteStatus[msg.sender];
            briefData[i].countConducted = Proposals[i + 1].countConducted;
            briefData[i].quorumMet = Proposals[i + 1].quorumMet;
            briefData[i].totalVotes = Proposals[i + 1].totalVotes;
            briefData[i].totalCount = Proposals[i + 1].totalCount;
            briefData[i].requiredQuorum = Proposals[i + 1].requiredQuorum;
            briefData[i].passed = Proposals[i + 1].passed;
            briefData[i].vetoUsed = Proposals[i + 1].vetoUsed;
            briefData[i].proposedBy = Proposals[i + 1].proposedBy;
        }
        return briefData;
    }

    function getProposals(
        uint id, // 25
        uint count // 10
    ) public view returns (SingleProposal[] memory) {
        if (nextProposal == 1) {
            // means no data
            return new SingleProposal[](0);
        }
        require(id < nextProposal, "This Proposal does not exist.");
        require(id >= count, "Count must be less than proposal ID.");
        SingleProposal[] memory briefData = new SingleProposal[](count);
        uint m = 0;
        for (uint256 i = id; i > (id - count); i--) {
            briefData[m].id = Proposals[(id - m)].id;
            briefData[m].description = Proposals[(id - m)].description;
            briefData[m].deadline = Proposals[(id - m)].deadline;
            briefData[m].votesUp = Proposals[(id - m)].votesUp;
            briefData[m].votesDown = Proposals[(id - m)].votesDown;
            briefData[m].voteStatus = Proposals[(id - m)].voteStatus[
                msg.sender
            ];
            briefData[m].countConducted = Proposals[(id - m)].countConducted;
            briefData[m].quorumMet = Proposals[(id - m)].quorumMet;
            briefData[m].totalVotes = Proposals[(id - m)].totalVotes;
            briefData[m].totalCount = Proposals[(id - m)].totalCount;
            briefData[m].requiredQuorum = Proposals[(id - m)].requiredQuorum;
            briefData[m].passed = Proposals[(id - m)].passed;
            briefData[m].vetoUsed = Proposals[(id - m)].vetoUsed;
            briefData[m].proposedBy = Proposals[(id - m)].proposedBy;
            m++;
        }
        return briefData;
    }

    function checkVotePercent(address _voter) public view returns (uint256) {
        uint256 lpTokens = IERC20(LLPT).balanceOf(_voter);
        if (lpTokens > 0) {
            uint256 totalSupply = IERC20(LLPT).totalSupply();
            uint256 percent = (lpTokens * MULTIPLIER) / totalSupply;
            return percent;
        } else {
            return 0;
        }
    }

    function checkVotes(address _voter) public view returns (uint256) {
        uint256 lpTokens = IERC20(LLPT).balanceOf(_voter);
        return lpTokens;
    }

    function createProposal(string memory _description) public payable {
        // msg.value
        require(payableAmount <= msg.value, "Payable amount is not enough.");

        uint256 lpTokens = IERC20(LLPT).balanceOf(msg.sender);
        require(lpTokens > 0, "You must hold LLPT to create proposal.");

        ProposalInfo storage newProposal = Proposals[nextProposal];
        newProposal.id = nextProposal;
        newProposal.description = _description;
        newProposal.deadline = block.timestamp + VOTING_PERIOD;
        newProposal.proposedBy = msg.sender;

        emit proposalCreated(nextProposal, _description, msg.sender);
        nextProposal++;
    }

    function voteOnProposal(uint256 _id, bool _vote) public {
        require(_id < nextProposal, "This Proposal does not exist");
        require(
            checkVoteEligibility(msg.sender),
            "You can not vote on this Proposal"
        );
        require(
            !Proposals[_id].voteStatus[msg.sender],
            "You have already voted on this Proposal"
        );
        require(
            block.timestamp <= Proposals[_id].deadline,
            "The deadline has passed for this Proposal"
        );

        ProposalInfo storage p = Proposals[_id];
        uint256 llpTokens = IERC20(LLPT).balanceOf(msg.sender);

        if (_vote) {
            p.votesUp = p.votesUp + llpTokens;
        } else {
            p.votesDown = p.votesDown + llpTokens;
        }

        p.voteStatus[msg.sender] = true;

        emit newVote(p.votesUp, p.votesDown, msg.sender, _id, _vote, llpTokens);
    }

    function countVotes(uint256 _id) public payable {
        require(payableAmount <= msg.value, "Payable amount is not enough.");
        // require(msg.sender == owner, "Only Owner Can Count Votes");
        require(_id < nextProposal, "This Proposal does not exist");
        require(
            block.timestamp > Proposals[_id].deadline,
            "Voting has not concluded"
        );
        require(!Proposals[_id].countConducted, "Count already conducted");

        ProposalInfo storage p = Proposals[_id];

        uint256 _totalSupply = IERC20(LLPT).totalSupply();
        // quorum is 51% of eligible voters
        uint256 quorum = (_totalSupply * 5001) / 10000;
        uint256 totalCounts = p.votesUp + p.votesDown;
        p.vetoUsed = false;
        p.countConducted = true;
        p.totalVotes = _totalSupply;
        p.requiredQuorum = quorum;
        p.totalCount = totalCounts;

        if (totalCounts < quorum) {
            // quorum not reached
            p.passed = false;
            p.quorumMet = false;
        }
        require(totalCounts >= quorum, "Quorum not reached.");
        p.quorumMet = true;

        if (p.votesUp > p.votesDown) {
            p.passed = true;
        } else {
            p.passed = false;
        }

        emit proposalCount(_id, p.passed);
    }

    // function readVotes(
    //     uint256 _id
    // ) public view returns (bool, uint256, uint256) {
    //     require(Proposals[_id].exists, "This Proposal does not exist");
    //     // p.votesUp
    //     // p.votesDown
    //     // total counts
    //     // quorum
    //     // deadline
    // }

    // function read(
    //     uint256 lastProposalCount
    // ) public view returns (bool, uint256, uint256) {
    //     require(Proposals[_id].exists, "This Proposal does not exist");
    // }

    function ethTransfer(
        address to,
        uint256 amount
    ) public onlyRole(RELAY_ROLE) {
        payable(to).transfer(amount);
    }

    function vetoOnProposal(uint256 _id) public onlyRole(RELAY_ROLE) {
        require(!Proposals[_id].vetoUsed, "Veto already used");
        require(Proposals[_id].countConducted, "Count should be conducted");
        Proposals[_id].vetoUsed = true;
        Proposals[_id].passed = !Proposals[_id].passed;
    }

    function changePayable(uint256 valuePayable) public onlyRole(RELAY_ROLE) {
        payableAmount = valuePayable;
    }
}"
    }
