pragma solidity ^0.8.4;





contract Merger is Ownable, VRFConsumerBaseV2 {
    bytes32 keyHash = 0xcc294a196eeeb44da2888d17c0625cc88d70d9760a69d58d853ba6581a9ab0cd;

    uint16 requestConfirmations = 3;
    uint32 callbackGasLimit = 100000;
    uint32 numWords = 1;
    uint64 s_subscriptionId;

    uint256 public s_requestId;
    uint256 public lastBlockRandomRequest;
    uint256 public THRESHOLD_BLOCK_GAP = 1000;

    uint256[] public randomSeed = [412312313];
    uint256[] public mergeFee = [50 * 1e18, 100 * 1e18, 200 * 1e18, 300 * 1e18, 400 * 1e18, 500 * 1e18, 600 * 1e18, 700 * 1e18, 800 * 1e18, 900 * 1e18];


    uint256 public mergeGenerationLimit = 5;
    uint256 public mergeCountRequired = 2;
    uint256 public mergeCounter;

    address public treasury;
    address vrfCoordinator = 0xAE975071Be8F8eE67addBC1A82488F1C24858067;

    IVenlyERC1155 public NFTContract;
    IERC20 public KnightTokenContract;
    VRFCoordinatorV2Interface COORDINATOR;

    mapping(uint256 => uint256) public generations;
    mapping(uint256 => uint256) public categories;
    mapping(uint256 => uint256) public rarities;

    event Merge(address indexed owner, uint256 indexed mainNFT, uint256[] components, uint256 newGeneration, uint256 randomNumber, uint256 mergeID);
    event RandomSeed(uint256 randomNumber, uint256 requestId);

    constructor (address _NFTContractAddress, address _knightTokenAddress, uint64 _subscriptionId, address _treasury) VRFConsumerBaseV2(vrfCoordinator){
        NFTContract = IVenlyERC1155(_NFTContractAddress);
        KnightTokenContract = IERC20(_knightTokenAddress);
        s_subscriptionId = _subscriptionId;
        COORDINATOR = VRFCoordinatorV2Interface(vrfCoordinator);
        treasury = _treasury;
    }

    function requestRandomNumber() internal {
        if (lastBlockRandomRequest > block.number - THRESHOLD_BLOCK_GAP) {
            return;
        }
        s_requestId = COORDINATOR.requestRandomWords(
            keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );
        lastBlockRandomRequest = block.number;
    }


    function merge(uint256 mainNFT, uint256[] memory components) public {
        require(components.length == mergeCountRequired, "Merger:Merge:: Not enough components to merge");
        require(NFTContract.balanceOf(msg.sender, mainNFT) == 1, "Merger:Merge:: Main NFT not owned by sender");
        uint256 tokenTypeId = NFTContract.typeForToken(mainNFT);
        uint256 NFTCategory = categories[tokenTypeId];
        uint256 NFTRarity = rarities[tokenTypeId];
        uint256 NFTGeneration = generations[mainNFT];
        require(NFTGeneration < mergeGenerationLimit, "Merger:Merge:: NFT generation limit reached");
        require(NFTCategory > 0, "Merger:Merge:: NFT category not set");
        require(NFTRarity > 0, "Merger:Merge:: NFT rarity not set");
        for (uint256 i = 0; i < components.length; i++) {
            uint256 componentTokenTypeId = NFTContract.typeForToken(components[i]);
            require(NFTCategory == categories[componentTokenTypeId], "Merger:Merge:: NFT category mismatch");
            require(NFTRarity == rarities[componentTokenTypeId], "Merger:Merge:: NFT rarity mismatch");
            require(NFTGeneration == generations[components[i]], "Merger:Merge:: NFT generation mismatch");
        }
        require(KnightTokenContract.transferFrom(msg.sender, treasury, mergeFee[NFTGeneration]), "Merger:Merge:: Failed to transfer fee");
        generations[mainNFT] = NFTGeneration + 1;
        uint256[] memory amounts = new uint[](mergeCountRequired);
        for (uint256 i = 0; i < components.length; i++) {
            amounts[i] = 1;
        }
        NFTContract.burnBatch(components, msg.sender, amounts);
        requestRandomNumber();
        mergeCounter++;
        emit Merge(msg.sender, mainNFT, components, NFTGeneration + 1, randomSeed[0] + mainNFT, mergeCounter);
    }

    function setTypeInfo(uint256[] memory typeId, uint256[] memory category, uint256[] memory rarity) public onlyOwner {
        for (uint256 i = 0; i < typeId.length; i++) {
            categories[typeId[i]] = category[i];
            rarities[typeId[i]] = rarity[i];
        }
    }

    function setMergeGenerationLimit(uint256 _mergeGenerationLimit) public onlyOwner {
        mergeGenerationLimit = _mergeGenerationLimit;
    }


    function setMergeCountRequired(uint256 _mergeCountRequired) public onlyOwner {
        mergeCountRequired = _mergeCountRequired;
    }

    function setMergeFee(uint256 generation, uint256 _mergeFee) public onlyOwner {
        mergeFee[generation] = _mergeFee;
    }

    function setThresholdBlockGap(uint256 threshold) public onlyOwner {
        THRESHOLD_BLOCK_GAP = threshold;
    }

    function setTreasury(address _treasury) public onlyOwner {
        treasury = _treasury;
    }

    function fulfillRandomWords(
        uint256, /* requestId */
        uint256[] memory randomWords
    ) internal override {
        randomSeed = randomWords;
        emit RandomSeed(randomSeed[0], s_requestId);
    }

}
