pragma solidity ^0.8.7;

contract BONES is ERC20, ERC20Burnable, Ownable {
    using SafeMath for uint256;
    using ECDSA for bytes32;
    mapping(uint256 => uint256) public bonesClaimed;
    address private PRIVATE_SIGNER = 0xdF84f5276A3737c5F4f032ED83b81247ee87EC81;
    uint256 public maxRewards = 30_000_000;
    uint256 public bonesPerReward = 1 ether;
    uint256 public mintedRewards;

    constructor() ERC20("$BONES", "$BONES") {}

    modifier notContract() {
        require(
            (!_isContract(msg.sender)) && (msg.sender == tx.origin),
            "Contracts not allowed"
        );
        _;
    }

    function _isContract(address addr) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(addr)
        }
        return size > 0;
    }

    function bonesCollectedBy(uint256[] memory tokens)
        external
        view
        returns (uint256[] memory)
    {
        uint256[] memory tokensList = new uint256[](
            tokens.length
        );
        uint256 currentIndex;
        for (uint256 index = 0; index < tokensList.length; index++) {
            tokensList[currentIndex] = bonesClaimed[tokens[currentIndex]];
            currentIndex++;
        }
        return tokensList;
    }

    function changeSigner(address newSigner) external onlyOwner {
        PRIVATE_SIGNER = newSigner;
    }

    function changeMaxRewards(uint256 newMax) external onlyOwner {
        maxRewards = newMax;
    }

    function changeBonesPerReward(uint256 newBonesPerReward) external onlyOwner {
        bonesPerReward = newBonesPerReward;
    }

    function matchAddresSigner(
        bytes memory signature,
        uint256 tokenId,
        uint256 rewardCount
    ) private view returns (bool) {
        bytes32 hash = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(abi.encodePacked(msg.sender, tokenId, rewardCount))
            )
        );
        return PRIVATE_SIGNER == hash.recover(signature);
    }

    function claim(
        bytes[] calldata signatures,
        uint256[] calldata tokensIds,
        uint256[] calldata rewardCount
    ) external notContract {
        require(signatures.length == tokensIds.length, "NOT_SAME_LENGTH");
        require(signatures.length == rewardCount.length, "NOT_SAME_LENGTH");

        uint256 claimable = 0;
        for (uint256 i = 0; i < signatures.length; i++) {
            if (
                matchAddresSigner(signatures[i], tokensIds[i], rewardCount[i])
            ) {
                claimable += rewardCount[i] - bonesClaimed[tokensIds[i]];
                bonesClaimed[tokensIds[i]] = rewardCount[i];
            }
        }
        require(mintedRewards + claimable <= maxRewards, "EXCEED_MAX");
        require(claimable > 0, "ZERO_NOT_ALLOWED");

        mintedRewards += claimable;
        _mint(msg.sender, claimable * bonesPerReward);
    }

    function founderMint(uint256 quantity) external onlyOwner {
        _mint(msg.sender, quantity);
    }

    function airdrop(
        uint256[] memory amounts,
        address[] memory receivers
    ) external onlyOwner {
        require(amounts.length == receivers.length, "NOT_SAME_LENGTH");

        for (uint256 index = 0; index < receivers.length; index++) {
            _mint(receivers[index], amounts[index]);
        }
    }
}
