      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {ERC2771Context, Context} from "@openzeppelin/contracts/metatx/ERC2771Context.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {ERC1155OpenZeppelin, ERC1155C} from "@limitbreak/creator-token-contracts/contracts/erc1155c/ERC1155C.sol";
import {ERC2981, BasicRoyalties} from "@limitbreak/creator-token-contracts/contracts/programmable-royalties/BasicRoyalties.sol";
import {IValeriaBoosterPacks} from "./interfaces/IValeriaBoosterPacks.sol";

/**
 __     __    _           _
 \ \   / /_ _| | ___ _ __(_) __ _
  \ \ / / _` | |/ _ \ '__| |/ _` |
   \ V / (_| | |  __/ |  | | (_| |
    \_/ \__,_|_|\___|_|  |_|\__,_|
*/

/// @title ValeriaBoosterPacks
/// @notice The Booster Packs for Valeria games ecosystem. Purchasable with $VAL on our marketplace.
/// @author @ValeriaStudios
contract ValeriaBoosterPacks is
    AccessControl,
    ERC2771Context,
    ERC1155C,
    BasicRoyalties,
    IValeriaBoosterPacks
{
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");

    bytes32 public constant EXTERNAL_CONTRACT_ROLE =
        keccak256("EXTERNAL_CONTRACT_ROLE");

    constructor(
        address _trustedForwarder,
        string memory _uri,
        address royaltyReceiver_,
        uint96 royaltyFeeNumerator_
    )
        ERC2771Context(_trustedForwarder)
        ERC1155OpenZeppelin(_uri)
        BasicRoyalties(royaltyReceiver_, royaltyFeeNumerator_)
    {
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(MODERATOR_ROLE, _msgSender());
    }

    function name() external pure returns (string memory) {
        return "Valeria Booster Packs";
    }

    function symbol() external pure returns (string memory) {
        return "VALBPK";
    }

    /**
     * EXTERNAL CONTRACT INTEGRATION
     */

    /// @dev Allows an external contract w/ role to burn an item
    function burnItem(
        address owner,
        uint256 typeId,
        uint256 amount
    ) external onlyRole(EXTERNAL_CONTRACT_ROLE) {
        _burn(owner, typeId, amount);
    }

    /// @dev Allows an external contract w/ role to burn in batch
    function burnItems(
        address owner,
        uint256[] calldata typeIds,
        uint256[] calldata amounts
    ) external onlyRole(EXTERNAL_CONTRACT_ROLE) {
        _burnBatch(owner, typeIds, amounts);
    }

    /// @dev Allows an external contract w/ role to mint an item
    function mintItem(
        address to,
        uint256 typeId,
        uint256 amount
    ) external onlyRole(EXTERNAL_CONTRACT_ROLE) {
        _mint(to, typeId, amount, "");
    }

    /// @dev Allows an external contract w/ role to mint a batch of items to the same address
    function mintItems(
        address to,
        uint256[] calldata typeIds,
        uint256[] calldata amounts
    ) external onlyRole(EXTERNAL_CONTRACT_ROLE) {
        _mintBatch(to, typeIds, amounts, "");
    }

    /// @dev Allows a bulk transfer
    function bulkSafeTransfer(
        uint256 typeId,
        uint256 amount,
        address[] calldata recipients
    ) external {
        for (uint256 i; i < recipients.length; ) {
            safeTransferFrom(_msgSender(), recipients[i], typeId, amount, "");
            unchecked {
                ++i;
            }
        }
    }

    /**
     * MODERATOR
     */

    /// @dev Permissions check
    function _requireCallerIsContractOwner() internal view virtual override {
        _checkRole(MODERATOR_ROLE);
    }

    /// @dev Allows a moderator to change the base uri
    function setURI(string memory _uri) public onlyRole(MODERATOR_ROLE) {
        _setURI(_uri);
    }

    /// @dev Configure default royalty
    function setDefaultRoyalty(
        address receiver,
        uint96 feeNumerator
    ) public onlyRole(MODERATOR_ROLE) {
        _setDefaultRoyalty(receiver, feeNumerator);
    }

    /// @dev Configure ongoing royalties
    function setTokenRoyalty(
        uint256 tokenId,
        address receiver,
        uint96 feeNumerator
    ) public onlyRole(MODERATOR_ROLE) {
        _setTokenRoyalty(tokenId, receiver, feeNumerator);
    }

    /// @dev Allows a moderator to mint a single item
    function moderatorMint(
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) external onlyRole(MODERATOR_ROLE) {
        _mint(to, id, amount, data);
    }

    /// @dev Allows a moderator to batch mint items
    function moderatorMintBatch(
        address to,
        uint256[] calldata ids,
        uint256[] calldata amounts,
        bytes memory data
    ) external onlyRole(MODERATOR_ROLE) {
        _mintBatch(to, ids, amounts, data);
    }

    /// @dev Allows a moderator to airdrop items to multiple addresses
    function moderatorAirdrop(
        address[] calldata tos,
        uint256[][] calldata ids,
        uint256[][] calldata amounts,
        bytes memory data
    ) external onlyRole(MODERATOR_ROLE) {
        require(
            tos.length == ids.length && ids.length == amounts.length,
            "!valid"
        );
        for (uint256 i; i < tos.length; ) {
            _mintBatch(tos[i], ids[i], amounts[i], data);
            unchecked {
                ++i;
            }
        }
