interface ICommissionsRouter {
    function receiveFees(uint256, bytes32) external;
}

contract LuckyCoin is Ownable, VRFConsumerBaseV2 {
    VRFCoordinatorV2Interface immutable COORDINATOR;

    // Your subscription ID.
    uint64 private s_subscriptionId;
    // The gas lane to use, which specifies the maximum gas price to bump to.
    bytes32 private s_keyHash;

    using Counters for Counters.Counter;
    using EnumerableMap for EnumerableMap.UintToUintMap;
    using EnumerableSet for EnumerableSet.UintSet;
    event CoinFlipPending(
        uint256 indexed requestId,
        uint256 gameId,
        address indexed player,
        address indexed creator,
        CoinFlipSelection playerChoice,
        CoinFlipSelection creatorChoice,
        uint256 betAmount,
        uint256 updatedAt
    );
    event CoinFlipResult(
        uint256 indexed requestId,
        uint256 gameId,
        address indexed player,
        address indexed creator,
        CoinFlipSelection playerChoice,
        CoinFlipSelection creatorChoice,
        CoinFlipSelection gameResult,
        uint256 betAmount,
        bool playerWon,
        bool creatorWon,
        uint256 updatedAt
    );

    event GameCreated(
        address indexed creator,
        uint256 indexed gameId,
        uint256 balance,
        uint256 timestamp
    );
    event GameRemoved(
        address indexed creator,
        uint256 indexed gameId,
        uint256 balance,
        uint256 timestamp
    );

    struct CoinFlipStatus {
        uint256 randomWord;
        uint256 amount;
        uint256 gameId;
        address player;
        CoinFlipSelection choice;
        bool hasWon;
        bool fulfilled;
        address creator;
        uint256 feePercentage;
    }
    struct Game {
        address creator;
        uint256 balance;
        uint256 id;
        uint256 requestId;
        bool inProgress;
        bool isActive;
    }
    enum CoinFlipSelection {
        HEADS,
        TAILS
    }
    IAddressRouter commissionsAddressRouter;
    mapping(uint256 => CoinFlipStatus) public statuses;

    IERC20 BRIN;
    IERC20 LINK;
    uint256 public BRINGOLD_FEE_PERCENTAGE = 10;
    uint128 public entryFees = 0.001 ether; //Calculate it based on Link fees
    uint32 private CALLBACK_GAS_LIMIT = 190_000;
    uint32 constant NUM_WORDS = 1;
    uint16 private REQUEST_CONFIRMATIONS = 10;

    Counters.Counter private gameID;
    mapping(uint256 => Game) gamesById;
    mapping(address => EnumerableMap.UintToUintMap) private playerGames;
    EnumerableSet.UintSet private activeGames;

    constructor(
        address _brin,
        address _link,
        address _commissionsAddressRouter,
        uint64 subscriptionId,
        address vrfCoordinator,
        bytes32 keyHash
    ) payable VRFConsumerBaseV2(vrfCoordinator) {
        BRIN = IERC20(_brin);
        LINK = IERC20(_link);
        commissionsAddressRouter = IAddressRouter(_commissionsAddressRouter);
        COORDINATOR = VRFCoordinatorV2Interface(vrfCoordinator);
        s_keyHash = keyHash;
        s_subscriptionId = subscriptionId;
    }

    /// @notice Creates a game
    /// @dev Creates a game with given bet amount, give approval to this contract from BRIN contract first
    /// @param _bet Bet amount of game(wei)
    function createGame(uint256 _bet) external {
        require(_bet >= 1 ether, "Bet amount must be greater than 1 ether");
        Game memory game = Game({
            creator: msg.sender,
            balance: _bet,
            id: gameID.current(),
            isActive: true,
            inProgress: false,
            requestId: 0
        });
        gamesById[gameID.current()] = game;
        playerGames[msg.sender].set(gameID.current(), gameID.current());
        activeGames.add(gameID.current());
        BRIN.transferFrom(msg.sender, address(this), _bet);
        emit GameCreated(msg.sender, gameID.current(), _bet, block.timestamp);
        gameID.increment();
    }

    /// @notice Deletes a game
    /// @dev Deletes the game from user's game list with given GameID
    /// @param _gameId Id of game
    function removeGame(uint256 _gameId) external {
        Game storage game = gamesById[_gameId];
        require(game.creator == msg.sender, "Unauthorized");
        require(game.isActive, "Game is already removed");
        require(!game.inProgress, "flip in progress");

        game.isActive = false;
        playerGames[msg.sender].remove(_gameId);
        activeGames.remove(_gameId);
        emit GameRemoved(msg.sender, _gameId, game.balance, block.timestamp);
        BRIN.transfer(msg.sender, game.balance);
    }

    /// @notice Returns an active game
    /// @dev Returns a Game object with given id
    /// @param id Id of game
    /// @return Game object
    function getActiveGameById(uint256 id) external view returns (Game memory) {
        require(id < activeGames.length(), "Id out of bounds");
        return gamesById[id];
    }

    /// @notice Returns all active games
    /// @dev Returns all active games as List of Game objects
    /// @return List of Game objects
    function getGames() external view returns (Game[] memory) {
        uint gamesLength = activeGames.length();
        Game[] memory games = new Game[](gamesLength);
        for (uint i = 0; i < gamesLength; i++) {
            games[i] = gamesById[activeGames.at(i)];
        }
        return games;
    }

    /// @notice Returns all active games of account
    /// @dev Returns all active games of given account(wallet address of game creator)
    /// @return List of Game objects
    function getPlayerGames(
        address account
    ) external view returns (Game[] memory) {
        uint gamesLength = playerGames[account].length();
        Game[] memory games = new Game[](gamesLength);
        for (uint i = 0; i < gamesLength; i++) {
            (uint256 currentId, ) = playerGames[account].at(i);
            games[i] = gamesById[currentId];
        }
        return games;
    }

    /// @notice Returns a game by given id
    /// @dev Returns any game(active or in-active) by given id
    /// @param gameId ID of game
    /// @return Game Object
    function getGame(uint256 gameId) external view returns (Game memory) {
        return gamesById[gameId];
    }

    /// @notice Flips the coin for the game with given gameID
    /// @dev Call this when player flips coin for given game, approva BRIN first and entry fee matic "value" (0.001 ether)
    /// @param gameId ID of game
    /// @return Chainlink requestID
    function flip(uint256 gameId) external payable returns (uint256) {
        require(msg.value == entryFees, "Entry fees not sent");
        Game storage game = gamesById[gameId];
        require(game.isActive && game.balance > 0, "Game not found");
        require(!game.inProgress, "flip in progress");
        require(game.creator != msg.sender, "Cannot play own game");
        uint256 betAmount = game.balance;
        CoinFlipSelection choice = CoinFlipSelection(
            uint256(
                keccak256(
                    abi.encodePacked(
                        block.prevrandao,
                        block.timestamp,
                        msg.sender
                    )
                )
            ) % 2
        );
        uint256 requestId = COORDINATOR.requestRandomWords(
            s_keyHash,
            s_subscriptionId,
            REQUEST_CONFIRMATIONS,
            CALLBACK_GAS_LIMIT,
            NUM_WORDS
        );

        statuses[requestId] = CoinFlipStatus({
            randomWord: 0,
            player: msg.sender,
            creator: game.creator,
            hasWon: false,
            fulfilled: false,
            choice: choice,
            amount: betAmount,
            gameId: game.id,
            feePercentage: BRINGOLD_FEE_PERCENTAGE
        });
        game.inProgress = true;
        game.requestId = requestId;
        BRIN.transferFrom(msg.sender, address(this), betAmount);
        uint256 feeAmount = calculateFee(betAmount);
        address commissionsAddress = commissionsAddressRouter.getAddress();
        BRIN.approve(commissionsAddress, feeAmount);

        ICommissionsRouter(commissionsAddress).receiveFees(
            feeAmount,
            bytes32("TX")
        );
        emit CoinFlipPending(
            requestId,
            gameId,
            msg.sender,
            game.creator,
            choice,
            choice == CoinFlipSelection.HEADS
                ? CoinFlipSelection.TAILS
                : CoinFlipSelection.HEADS,
            betAmount,
            block.timestamp
        );

        return requestId;
    }

    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) internal override {
        require(statuses[requestId].player != address(0), "Request not found");

        statuses[requestId].fulfilled = true;
        statuses[requestId].randomWord = randomWords[0];
        CoinFlipSelection result = CoinFlipSelection.HEADS;
        if (randomWords[0] % 2 == 0) {
            result = CoinFlipSelection.TAILS;
        }
        CoinFlipStatus memory status = statuses[requestId];
        Game storage game = gamesById[status.gameId];
        uint256 feeAmount = calculateFeeWithPercentage(
            status.amount,
            status.feePercentage
        );
        require(game.inProgress, "game is not in progress");
        if (status.choice == result) {
            status.hasWon = true;
            BRIN.transfer(status.player, (status.amount * 2 - feeAmount));
        } else {
            BRIN.transfer(status.creator, (status.amount * 2 - feeAmount));
        }
        game.balance = 0;
        game.isActive = false;
        activeGames.remove(game.id);
        playerGames[game.creator].remove(game.id);
        game.inProgress = false;

        emit CoinFlipResult(
            requestId,
            status.gameId,
            status.player,
            status.creator,
            status.choice,
            status.choice == CoinFlipSelection.HEADS
                ? CoinFlipSelection.TAILS
                : CoinFlipSelection.HEADS,
            result,
            status.amount,
            status.hasWon,
            !status.hasWon,
            block.timestamp
        );
    }

    /// @notice Sets the BRINGOLD_FEE percentage
    /// @dev Sets Bringold Fee percentage in range (1-99)
    /// @param fee fee percentage in range (1-99)
    function setFee(uint fee) external onlyOwner {
        require(fee > 0 && fee < 100, "zero");
        BRINGOLD_FEE_PERCENTAGE = fee;
    }

    /// @notice Sets base entry fee
    /// @dev Sets base entry fee in matic
    /// @param fee fee amount in wei
    function setEntryFee(uint128 fee) external onlyOwner {
        entryFees = fee;
    }

    /// @notice Returns Flip status
    /// @dev Returns Flip status with given VRF requestID
    /// @param requestId ChainlinkVRF requestId
    /// @return Flip status
    function getStatus(
        uint256 requestId
    ) public view returns (CoinFlipStatus memory) {
        return statuses[requestId];
    }

    function calculateFee(uint256 amount) internal view returns (uint256) {
        return (amount * 2 * BRINGOLD_FEE_PERCENTAGE) / 100;
    }

    function calculateFeeWithPercentage(
        uint256 amount,
        uint256 percentage
    ) internal pure returns (uint256) {
        return (amount * 2 * percentage) / 100;
    }

    function withdrawBRIN(address to, uint256 amount) external onlyOwner {
        require(amount <= BRIN.balanceOf(address(this)));
        BRIN.transfer(to, amount);
    }

    /// @notice Sets the VRF Gas Lane
    /// @dev Sets VRF gas lane with given keyHash of gas lane
    /// @param keyHash Key Hash of VRF gas lane
    function setCLGasLane(bytes32 keyHash) external onlyOwner {
        s_keyHash = keyHash;
    }

    /// @notice Sets the VRF Callback Gas Limit
    /// @dev Sets the VRF Callback Gas Limit for FullfillRandomWords function
    /// @param callbackGasLimit CallbackGaslimit to be set for VRF service
    function setCLCbGasLimit(uint32 callbackGasLimit) external onlyOwner {
        CALLBACK_GAS_LIMIT = callbackGasLimit;
    }

    /// @notice Sets Request Confirmations for VRF request
    /// @dev Sets cnfirmation number required for VRF request
    /// @param confirmationNum Confirmation number required for VRF call
    function setClConfirmationNumber(
        uint16 confirmationNum
    ) external onlyOwner {
        REQUEST_CONFIRMATIONS = confirmationNum;
    }

    /// @notice Sets VRF Subscription ID
    /// @dev Sets VRF Subscription ID
    /// @param subId VRF Subscription ID
    function setClSubId(uint64 subId) external onlyOwner {
        s_subscriptionId = subId;
    }

    function withdrawMATIC(address receiver) external onlyOwner {
        (bool sent, bytes memory data) = payable(receiver).call{
            value: address(this).balance
        }("");
        require(sent, "Failed to send Ether");
    }
}
"
    }
  }
}}
